css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://twitter.com"), url-prefix("http://twitter.com") {
  
  
  /* GLOBAL */

  .global-nav-inner, .js-global-new-tweet, .js-global-new-tweet:hover, .AdaptiveSearchTitle, #global-tweet-dialog-header {
    background-color: /*[[header-color]]*/; 
    transition: 0.2s linear;
    border: 0px !important;
    color: #fff;
  }
  
  #global-tweet-dialog-header,  #global-tweet-dialog-header button, #delete-tweet-dialog-header, .message-inside, .new-tweets-bar:hover, #keyboard-shortcut-dialog-header, .Icon--close::before, #global-actions > li:hover > a, #global-actions > li > a:focus, .nav.right-actions > li > a:hover, .nav.right-actions > li > button:hover, .nav.right-actions > li > a:focus, .nav.right-actions > li > button:focus, #trends_dialog-header, #retweet-tweet-dialog-header, .js-stream-item, #search-query, .search-input::placeholder, #global-nav-search, .active .text, .MomentGuideNavigation-item .is-active, .u-borderUserColor, .u-textUserColorHover:hover {
    color: #fff!important;
    transition: 0.2s linear;
  }
  
  .global-dm-nav.new.with-count .dm-new .count-inner,
  .global-nav .people .count .count-inner, .profile_image_upload_dialog-header, .SearchNavigation, .SearchNavigation-canopy {
    background: /*[[header-color]]*/ !important;
    color: #fff;
    opacity: 1;
  }
  
  .module, .tweet, .roaming-module, .content-main, .global-nav, .topbar, .WhoToFollow, .ProfileHeading-toggle, .message, .stream, #stream-items-id li, #stream-items-id div, #content-main-heading, .ProfileHeading , .ProfileHeading-content, .stream-footer, .stream-fail-container, .js-stream-whale-end, .stream-loading, .SidebarFilterModule, .AdaptiveRelatedSearches, .flex-module, .module, .component, .content-header, .flex-module, .ProfileCard, .MomentCapsuleSummary, .GridTimeline, .ridTimeline-items, .Grid, .Grid-cell, .ListCreationModule, .js-stream-item {
     border: none!important;
     border-left: none!important;
     border-right: none!important;
     border-top: none!important;
     border-bottom: none!important;
     border-color: transparent!important;
     box-shadow:none!important;
     outline: none!important;
  }
  
  .stream, #stream-items-id {
    border-top-right-radius: 50% !important;
    border-top-left-radius: 50% !important;
  }
  
  .ProfileCard {
    padding-bottom: 2vh;
  }
  
  .topbar, .global-nav, .global-nav-inner {
    box-shadow: 0px 0px 10px #555;
  }
  
  .AdaptiveFiltersBar-nav, .TwitterCard {
    background-color: #fff;
  }
  
  .SearchExtrasDropdown-toggle {
    margin-right: 5vw;
  }
  
  .new-tweets-bar:hover {
    color: #fff;
  }
  
  .new-tweets-bar, .new-tweets-bar-visible, .js-new-items-bar-container {
    border: 0px!important;
    border-radius: 0px;
    background-color: #f5f8fa;
    transition: 0.1s linear;
  }
  
  .new-tweets-bar {
        padding: 20px 0px;
  }
  .new-tweets-bar:hover {
    background-color: /*[[header-color]]*/;
  }
  
  
  /* BUTTON */
  .dropdown-menu {
     border: 1px solid /*[[header-color]]*/;
     border-radius: 0px;
  }
  
  #typeahead-dropdown-3, .modal-content, .primary-btn, .MomentFloatingMenu, .ListCreationModule, .AdaptiveMedia, .AdaptiveMedia-video, .AdaptiveMedia-quadPhoto, #playerContainer, .poster-image, #typeahead-dropdown-1, .user-actions-follow-button, .dropdown-menu, .small-follow-btn, .DirectMessage-message, .TwitterCard-container, .DMComposer-send, .ProfileNav-item, .ProfilCard, .ProfileCard-bg, .ProfileCard-content, .btn, .user-dropdown-toggle, .QuoteTweet, .new-tweets-bar, .stream-container, .content-main {
     border-radius: 5px;
  }
  
  #search-query {
    margin-right: 2vw;
  }
  
  #user-dropdown .btn {
    border-radius: 50%;
    outline: none;
    background-color: /*[[header-color]]*/;
  }
  
  .GridTimeline-footerIcon {
    background-color: transparent;
  }
  
  .count-inner {
    background-color: rgb(0, 0, 0);
  }
  
  /* HEADER */
  .modal-header  {
    background-color: /*[[header-color]]*/;
  }
  
  .RichEditor-scrollContainer, .DMInboxItem, .btn, #global-new-tweet-button, .stream-item, .stream-item {
    border: transparent;
    outline: none;
  }
  
  #global-actions li a, #global-actions li a:hover, #global-actions li a:focus, .u-borderUserColor, .MomentGuideNavigation-item:focus {
    border-bottom-color: #fff!important;
    color: #fff;
  }
  
  .count-inner {
    border-radius: 0px;
    border-color: /*[[header-color]]*/;
    background-color: /*[[header-color]]*/ !important;
  }
  
  .global-nav-inner .Icon::before, #global-actions .new:before {
    color: #fff !important;
  }
  
  .btn {
    border-color: transparent;
  }
  
  .photo-selector:hover .btn,
  .icon-btn:hover,
  .icon-btn:active,
  .icon-btn.active,
  .icon-btn.enabled {
    border: none;
  }
  
   .photo-selector:hover .btn,
  .icon-btn:hover {
    background-image: none;
    filter: none; /* IE8-9 */
  }
  
  .bird-topbar-etched {
    color: white;
  }
  
  #search-query {
    border-radius: 50px;
    border-color: /*[[header-color]]*/;
    background-color: /*[[header-color]]*/;
    color: #fff!important;;
  }
  
  placeholder {
    color: #fff!important;
  }
  
  .Icon--search, .pushstate-spinner, .global-actions li::before {
    color: #fff !important;
  }
  
  .global-nav-inner li, .global-nav-home::before {
    color: #fff;
  }
  
  .home-tweet-box, .DMActivity-header {
    border: none;
  }
  
  .DMInbox-toolbar, .DMActivity, .light-inline-actions {
    border-radius: 5px;
  }
  
  .btn:focus,
  .btn.focus,
  .Button:focus {
    box-shadow: none;
  }
  
  .card2, .js-macaw-cards-iframe-container {
    background-color: transparent;
  }
  
  /* ERROR */
  
  p, p a, .stream-item-activity-line, .ActivityItem-displayText {
    color: #000;
  }
  
  .message {
    background-color: #fff;
    width: 100%;
  }
  
  /* PROFIL */
  
  .ProfileCanopy-navBar {
    border-color: transparent;
    color: #fff;
    z-index: 1;
  }
  
  .ProfilePage .AppContent {
    background-color: #F5F8FA;
  }
  
/* RECHERCHES */
  
  .AdaptiveFiltersBar, .AdaptiveFiltersBar-container, .AdaptiveFiltersBar-nav, .MomentGuideNavigation {
    background: /*[[header-color]]*/ !important;
  }
  
  .AdaptiveFiltersBar-item a {
    color: #fff;
    transition: 0.2s linear;
  }
  
  .AdaptiveFiltersBar-item a:hover {
    opacity: 0.7;
  }

  .AdaptiveSearchPage .topbar, .AdaptiveSearchPage .global-nav, .AdaptiveSearchPage .global-nav-inner, .MomentsGuidePage .global-nav-inner {
    box-shadow: 0px 0px 0px transparent !important;
  }
  
  .AdaptiveSearchPage #page-container, .MomentsGuidePage #page-container {
    background-color: #F5F8FA !important;
  }

  .AdaptiveFiltersBar {
    box-shadow: 0 4px 2px -10px #555;
  }

  .SearchNavigation-canopy {
    padding-top: 2vh;
  }
  
  .AdaptiveSearchPage .is-selected, .MomentGuideNavigation-target .is-selected {
    border-color: #fff !important;
  }

  .AdaptiveSearchPage .is-selected a, .MomentGuideNavigation-categories .is-selected a {
    color: #fff !important;
  }
  
  .SearchExtrasDropdown-toggle, .MomentGuideNavigation-target {
    color: #fff !important;
  }
  
 }

/* AUTRE */

@-moz-document url("https://twitter.com/"), url("http://twitter.com"), url("https://twitter.com/i/notifications"), url("https://twitter.com/mentions"), url("https://twitter.com") {

  .timeline-tweet-box {
    display: none;
  }
  
  .pushstate-spinner {
    background: #fff;
  }
  
   /* MEDIA */
  
  .AdaptiveMedia-photoContainer {
    margin-right: 0px;
  }
  
  #page-container {
    margin-top: 3vh;
  }
  
  .pushstate-spinner {
    background-image: none!important;
  }
  
  .ActivityItem-headerText {
    color: #000000;
  }
}
