css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nikkeibp.co.jp") {

  #wrapper{
    min-width: 1000px;
    width: 100%;
    padding: 5px 0 0 0;
  }
  #gnav2 {
      min-width: 1000px;
      width: auto;
  }
  #lnavi {
      min-width: 1000px;
      width: auto;
      margin: 0 10px;
  }
  #lnavi>div {
      min-width: 1000px;
      width: calc(100% - 20px);
  }
  #maincontainer {
    min-width: 1000px;
    width: 100%;
    padding: 0 10px;
  }
  #maincontainer #contents {
    min-width: 670px;
    width: calc(100% - 330px);
  }
/*  #columnRanking > ol > li.upper {
    min-width: 670px;
    width: 100%;
}*/
  
  div.content{
    min-width: 1000px;
    width: 100%;
    padding: 0 10px;
  }

  .topic_headline {
    min-width: 685px;
    max-width: 800px;
    width: 100%;
  }
  .basic-body {
    min-width: 660px;
    width: 100%;
  }
  .basic-body .main-area {
    width: calc(100% - 160px);
  }
  .basic-body .main-area .main-content {
    min-width: 450px;
    width: calc(100% - 320px);
  }


  /* business*/
  #topContent .pickupPrimary .articleList {
    min-width: 560px;
    width: calc(100% - 450px);
  }
  #topContent .pickupPrimary .articleList li {
    min-width: 270px;
    width: calc(50% - 15px);
  }
  .twoLows .articleList li {
    min-width: 310px;
    width: calc(50% - 15px);
  }
  .threeLows .column {
    min-width: 200px;
    width: calc(33% - 20px);
  }
  #mainContent .pickupSecondary .articleBlock .articleList {
    min-width: 260px;
    width: calc(50% - 30px);
  }
  
  #articleBody {
   padding: 0 20px;
  }

  #content{
    min-width: 1000px;
    width: 100%;
  }
  #contents {
    width: 100%;
  }
  #articleBody {
    min-width: 670px;
    width: 100%;
    padding: 0 20px;
  }
  
  /* IT*/
  #mainContent{
    min-width: 640px;
    width: calc(100% - 320px);
  }
    .topList .list_B {
    min-width: 319px;
    width: calc(50% - 15px);
  }
  .list_B li {
    min-width: 305px;
    width: 100%;
  }
  .topList .list_C {
    min-width: 319px;
    width: calc(50% - 15px);
  }
  .list_C li {
    min-width: 305px;
    width: 100%;
  }
  
  /* Trendy */
  .main{
    min-width: 683px;
    /*max-width: 900px;*/
    width: calc(100% - 320px);
  } 
  
  .articlebody{
    min-width: 600px;
    width: calc(100% - 50px);
    padding: 5px;
  }
  
  .articlebody .bpimage_double {
    min-width: 500px;
    width: 100%;
  }
  .headline_01 li {
    min-width: 330px;
    width: calc(50% - 15px);
  }
  [id^="alltheme_list"] {
    min-width: 680px;
    width: 100%;
  }

  [id^="alltheme_list"] > * {
    min-width: 680px;
    width: auto;
  }

  [id^="alltheme_list"] > ul > li {
    min-width: 680px;
    width: 100%;
  }
  
  #alltheme_list ul.list03 li, #alltheme_list03 ul.list03 li {
    width: 100%;
  }
  #alltheme_list ul.list03 li div.rc_txt, #alltheme_list03 ul.list03 li div.rc_txt {
    min-width: 600px;
    width: calc(100% - 100px);
  }
  #alltheme_list ul.list04 li, #alltheme_list03 ul.list04 li {
    width: 100%;
  }
  #alltheme_list ul.list04 li div.rc_txt, #alltheme_list03 ul.list04 li div.rc_txt {
    min-width: 600px;
    width: calc(100% - 100px);
  }
  .latest_article_left {
    min-width: 320px;
    width: 50%;
  }
  .latest_article_right{
    min-width: 338px;
    width: calc(50% - 10px);
  }
   .latest_article_right dl {
    width: 100%;  
  }
  .latest_article_right dd {
    min-width: 166px;
    width: calc(100% - 175px);
  }
  .latest_article_panel_l ul {
    min-width: 216px;
    width: calc(33% - 10px);
  }
  .latest_article_panel_s ul {
    min-width: 158px;
    width: calc(25% - 15px);
  }
  .channel_wrapper {
    min-width: 680px;
    width: 100%;
  }
  .channel_wrapper div.channel_left, .channel_wrapper div.channel_right {
    min-width: 330px;
    width: calc(50% - 10px);
  }
  .channel_wrapper li.channel_article {
    min-width: 276px;
    width: calc(100% - 60px);
  }
  .top_books {
    min-width: 680px;
    width: 100%;
  }
  /*.bp_magazine_info {
    min-width: 680px;
    width: 100%;
  }*/
  .top_books li {
    min-width: 215px;
    width: calc(33% - 10px);
  }
  .top_books li div {
    min-width: 123px;
    width: calc(100% - 80px);
  }
  /* Architecture */
  #middleArea #westArea {
    min-width: 660px;
    width: calc(100% - 340px);
  }
  #middleArea #westArea #mainContent{
    min-width: 660px;
    width: 100%;
    background: white;
  }

}
