css:
@-moz-document domain("qiita.com") {
#article-body-wrapper .col-sm-9,
#article-body-wrapper .col-sm-3,
article .col-sm-9 {
	width: 100%;
}

.ArticleMainHeader {
	padding: 15px 0;
}
.ArticleMainHeader__title {
	font-size: 25px;
}
.ArticleAsideHeader {
	padding: 0;
	background-color: #fff;
}
.ArticleMainHeader .col-sm-9 {
	min-width: 100%;
}
.ArticleMainHeader .col-sm-3 {
	display: flex;
	align-items: baseline;
	width: 300px;
}
.ArticleMainHeade .js-likebutton {
	width: 50px;
}
.itemsShowHeaderStock_count {
	font-size: 18px;
}
.itemsShowHeaderStock_statusList {
	display: inherit;
}
.s-flex-align-center {
	flex-direction: column;
	align-items: flex-start;
}
.LikeButton>.p-button {
	height: auto;
	border: 1px solid #ccc;
	background-color: #79b74a;
	color: #ffffff;
}
.itemsShowHeaderTags .tagIcon_count {
	background-color: #fff;
}
.itemsShowHeaderTags .tagIcon_base {
	border: 1px solid #fff;
}
.itemsShowHeaderTags .tagIcon_count:before,
.itemsShowHeaderTags .tagIcon_count:after {
	border-right: none;
}
.itemsShowHeaderTags .tagIcon_base:before,
.itemsShowHeaderTags .tagIcon_base:after {
	border-right: 9px solid #fff;
}
.tocTree,
.headerNews,
.itemsShowBody_articleColumnFooter,
.socialButtons,
.ArticleAsideHeader__revisionCount,
.StockButton__label,
.ArticleAsideHeader .dropdown,
.ArticleMainHeader__users,
.itemsShowHeaderStock_countText {
	display: none !important;
}

}
