css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("akiba-souken.com") {
  #container{
    min-width: 990px;
    width: 100%;
  }
  .main{
    min-width: 680px;
    width: calc(100% - 310px);
  }
  .articleList .articleListFrame .listBox .rightText {
    min-width: 510px;
    width: calc(100% - 140px);
  }
  .categoryTitle {
      min-width: 678px;
      width: calc(100% - 310px);
  }
  div.table01 {
      min-width: 624px;
      width: auto;
  }
  .indexArticleNew .box ul {
      min-width: 520px;
      width: calc(100% - 150px);
  }
  .indexArticleNew .box li a {
    min-width: 420px;
    width: calc(100% - 100px);
  }
  .indexArticleHot .left, .indexArticleHot .right {
     min-width: 335px;
     width: calc(50% - 20px);
  }
  .indexArticleHot .hotNews .text {
    min-width: 220px;
    width: calc(100% - 70px);
  }
  .animeContents .official {
    min-width: 388px;
    width: calc(50% - 20px);
  }
}
