css:
@-moz-document domain("tabun.everypony.ru") {
#update {
    background: url("http://i.imgur.com/UWHNggt.png") no-repeat !important;
    background-size: 173px 159px !important;
    margin-top: 1px !important;
}

.new-comments {
    font-size: 13px !important;
    background: url("http://i.imgur.com/1QOUEzd.png") no-repeat !important;
    height: 24px !important;
    width: 43px !important;
    padding-top: 15px !important;
    margin-left: 116px !important;
}

.update-comments i {
    background: url("http://svgshare.com/i/QH.svg") !important;
    height: 25px !important;
    width: 25px !important;
    margin-left: 125px !important;
    margin-top: 75px !important;
    opacity: 100 !important;
}

.toolbar-update {
    border-style: none !important;
    height: 159px !important;
    margin-top: -74px !important;
    width: 157px !important;
}

.toolbar .toolbar-update .update-comments.active i {
-webkit-animation-name: rotation;
-webkit-animation-duration: 0.3s !important;
-webkit-animation-iteration-count: infinite;
-webkit-animation-timing-function: linear;
-webkit-animation-direction: reverse !important;
-moz-animation-name: rotation;
-moz-animation-duration: 0.3s !important;
-moz-animation-iteration-count: infinite;
-moz-animation-timing-function: linear;
-moz-animation-direction: reverse !important;
-o-animation-name: rotation;
-o-animation-duration: 0.3s !important;
-o-animation-iteration-count: infinite;
-o-animation-timing-function: linear;
-o-animation-direction: reverse !important;
animation-name: rotation;
animation-duration: 0.3s !important;
animation-iteration-count: infinite;
animation-timing-function: linear;
animation-direction: reverse !important;
}

@-webkit-keyframes rotation {
0% {-webkit-transform:rotate(0deg);
-moz-transform:rotate(0deg);
-o-transform:rotate(0deg);
transform:rotate(0deg);}
100% {-webkit-transform:rotate(360deg);
-moz-transform:rotate(360deg);
-o-transform:rotate(360deg);
transform:rotate(360deg);}
}
@-moz-keyframes rotation {
0% {-webkit-transform:rotate(0deg);
-moz-transform:rotate(0deg);
-o-transform:rotate(0deg);
transform:rotate(0deg);}
100% {-webkit-transform:rotate(360deg);
-moz-transform:rotate(360deg);
-o-transform:rotate(360deg);
transform:rotate(360deg);}
}
@-o-keyframes rotation {
0% {-webkit-transform:rotate(0deg);
-moz-transform:rotate(0deg);
-o-transform:rotate(0deg);
transform:rotate(0deg);}
100% {-webkit-transform:rotate(360deg);
-moz-transform:rotate(360deg);
-o-transform:rotate(360deg);
transform:rotate(360deg);}
}
@keyframes rotation {
0% {-webkit-transform:rotate(0deg);
-moz-transform:rotate(0deg);
-o-transform:rotate(0deg);
transform:rotate(0deg);}
100% {-webkit-transform:rotate(360deg);
-moz-transform:rotate(720deg);
-o-transform:rotate(360deg);
transform:rotate(360deg);}
}
}
