css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("volafile.org") {

/*main*/

.page_room {
  background-color : #090510;
  color : #eee8fd;
}

/*main*/
/*header*/

#header {
  background-color: #190830;
  border-style: solid;
  border-top:none; border-left:none; border-right:none;
  border-width: 4px;
  border-color: rgba(32, 0, 72, 0.75);
}

#room_name_container { 
  border-width: 3px;
  border-color: rgba(32, 0, 72, 0.75);
}

#header_row1 { 
  border-width: 3px;
  border-color: rgba(32, 0, 72, 0.75);
}

#header_row2 { 
  border-width: 3px;
  border-color: rgba(32, 0, 72, 0.75);
}

.dropdown_option_text { color: #CEBCF9;}
.icon-user { color: #CEBCF9;}
#user_menu:hover .dropdown_hover.dropdown_option>.icon-user { color: rgba(160, 48, 96, .9);}

.icon-home { color: #CEBCF9;}
#home_menu:hover .dropdown_hover.dropdown_option>.icon-home { color: rgba(160, 48, 96, .9);}

.header_row_element:hover .icon-report{ color: rgba(195, 48, 96, .95);}
.header_row_element { 
  border-width: 3px;
  border-color: rgba(32, 0, 72, 0.75);
  color: #CEBCF9;
}


.drpodown_option { background-color: #190830;}

.dropdown_item.dropdown_option:hover { 
  background-color: #170c46;
  outline-style: solid;
  outline-color: #221164;
  outline-width: 2px;
}

.dropdown_list { 
  background-color: #190830;
  border-width: 3px;
  border-color: rgba(32, 0, 72, 0.75);
}

.dropdown_list::after { border:none; }
#show_search_ui { background-color: #190830;}
#show_search_us:hover { background-color: #170c46}

#toggles > .toggle { border-color: #271084;}
#toggles > .toggle:active { border-color: #271084;}
#toggles > .toggle:hover { backgrond-color: red!important;}

#search_input { background-color: #190830;}

#uploadButton {
  background-color: rgba(64, 18, 64, .75);
  color: #CEBCF9;
}

#uploadButton:hover { background-color: rgba(96, 18, 64, .75);}

#home_menu { background-color: #190830;}
#home_menu:hover { background-color: #170c46;}

#user_menu { background-color: #190830;}
#user_menu:hover { background-color: #170c46;}

#report_button:hover { background-color: #170c46;}

.toggle.enabled>.toggle_icon.icon-music { color: rgba(128, 18, 64, 1);}
.toggle>.toggle_icon.icon-music { color: rgba(42, 18, 96, .75);}
  
.toggle.enabled>.toggle_icon.icon-image { color: rgba(128, 18, 64, 1);}
.toggle>.toggle_icon.icon-image { color: rgba(42, 18, 96, .75);}
  
.toggle.enabled>.toggle_icon.icon-film { color: rgba(128, 18, 64, 1);}
.toggle>.toggle_icon.icon-film { color: rgba(42, 18, 96, .75);}
  
.toggle.enabled>.toggle_icon.icon-archive { color: rgba(128, 18, 64, 1);}
.toggle>.toggle_icon.icon-archive { color: rgba(42, 18, 96, .75);}
  
.toggle.enabled>.toggle_icon.icon-book { color: rgba(128, 18, 64, 1);}
.toggle>.toggle_icon.icon-book { color: rgba(42, 18, 96, .75);}
  
.toggle.enabled>.toggle_icon.icon-file { color: rgba(128, 18, 64, 1);}
.toggle>.toggle_icon.icon-file { color: rgba(42, 18, 96, .75);}

/*header*/
/*filelist*/

#files_frame { 
  border-color: rgba(64, 0, 64 , 0.75);
  border-top: none;
}

.file_name { color: #eee8fd;}
.file_name:visited { color: #eee8fd;}

#file_list.uneven .filelist_file:nth-child(2n+1):hover { background-color:rgba(128, 0, 64 , 0.1);}
#file_list.even .filelist_file:nth-child(2n):hover     { background-color:rgba(128, 0, 64 , 0.1);}
#file_list.uneven .filelist_file:nth-child(2n+1) { background-color:#0c0415;} /*purple*/
#file_list.even .filelist_file:nth-child(2n)     { background-color:#0c0415;} /*purple*/

#file_list.uneven .filelist_file:nth-child(2n):hover { background-color:rgba(128, 0, 64 , 0.1);}
#file_list.even .filelist_file:nth-child(2n+1):hover { background-color:rgba(128, 0, 64 , 0.1);}
#file_list.uneven .filelist_file:nth-child(2n) { background-color:#090510;} /*blue*/
#file_list.even .filelist_file:nth-child(2n+1) { background-color:#090510;} /*blue*/

.file_uploading { background-color:rgba(64, 0, 48, .5)!important;}

.filelist_file {
  color: #CEBCF9;
  border-style: solid;
  border-color:rgba(0, 0, 0, 0);
}

.filelist_file:hover { border-color:rgba(128, 0, 64 , 0.5);
  border-width: 3px;
  border-radius: 10px;
  border-left-width: 6px;
  border-right-width: 6px;
}

.file_tag.tag_key_user { background-color: rgba(64, 18, 42, .75);}
.file_tag.tag_key_user:hover { background-color: rgba(96, 18, 42, .75);}
  
.file_tag.tag_key_ip { background-color: rgba(64, 18, 42, .75);} /*for mods*/
.file_tag.tag_key_ip:hover { background-color: rgba(96, 18, 42, .75);} /*for mods*/

.file_tag.tag_key_artist { background-color: rgba(64, 18, 64, .75);}
.file_tag.tag_key_artist:hover { background-color: rgba(96, 18, 42, .75);}

.file_tag.tag_key_album { background-color: rgba(42, 18, 64, .75);}
.file_tag.tag_key_album:hover { background-color: rgba(42, 18, 96, .75);}

.file_status::before { border-left-color: rgba(96, 18, 42, .75);}
  
#file_notifier { background-color: rgba(64, 0, 48, .9);}
#file_notifier:hover { background-color: rgba(96, 0, 64, .96)}

#filter_reminder { background-color: rgba(64, 0, 48, .9);}
#filter_reminder:hover { background-color: rgba(96, 0, 64, .96)}

.icon-music { color: firebrick;}
.icon-image { color: firebrick;}
.icon-film { color: firebrick;}
.icon-archive { color: firebrick;}
.icon-book { color: firebrick;}
.icon-file { color: firebrick;}

.icon-music.clickable.filetype_download.music_playing { color: blue;}
#i.file_icon.file_control_icon.filetype.file_icon_hupload.clickable.filetype_download { color: blue;}

/*filelist*/
/*chat*/

#chat_frame { 
  border-color: rgba(64, 0, 64 , 0.75);
  border-top: none;
}

.highlight { background-color:rgba(22, 6, 48, 0.5)!important;}

.chat_file {
  background-color:rgba(24, 0, 64, .3)!important;
  border-color:rgba(32, 0, 108 , 0.5)!important;
}

.chat_file:hover { 
  background-color:rgba(64, 0, 48, .3)!important;
  border-color:rgba(128, 0, 64 , 0.5)!important;
}

#chat_input { 
  background-color:#090510;
  border-color:#160628;
}

#chat_input_container { border-left-color: #120a1f;}

#chat_hbar { 
  background-color:#1a0620;
  border-color:rgba(64, 0, 64 , 0.5);
  border-left-color:#120a1f;
}

#chat_hbar_container {
	box-shadow: 0 -5px 10px 5px #090510;
}

#chat_name { 
  background-color:#200620;
  border-style:solid;
  border-width: 2px;
  border-color:rgba(128, 0, 64 , 0.5);
  border-right-color:rgba(0, 0, 0, 0);
  border-left-color:rgba(0, 0, 0, 0);
}

.nano>.pane>.slider { background-color:rgba(64, 0, 48 , 0.5);}

#chat_messages_frame::before { border-left: .4em solid #120a1f;}

.chat_message.highlight { border-left-color: rgba(64, 0, 64 , 0.35);}

.chat_message .username { text-shadow: 0px 0px 5px rgba(241, 237, 237, 0.35);}
.chat_message.user .username { text-shadow: 0px 0px 5px rgba(19, 193, 95, 0.35);}
.chat_message.donator .username { text-shadow: 0px 0px 5px rgba(50, 244, 134, 0.35);}
.chat_message.staff .username { 
  text-shadow: 0px 0px 5px rgba(234, 74, 62, .35);
  color: #ea4d3e;
}

.chat_message.staff.admin .username {
  text-shadow: 0px 0px 5px rgba(211, 129, 248, .35);
  color: #d381f8;
}

a { color: #8eaCf9;}
a:visited { color: #CEBCF9;}
a:hover { color: #fE90e9;}

.chat_message i { 
  color: rgba(108, 18, 42, .75);
  font-weight: 900;
} 

/*chat*/
/*radio*/

#radio_container { background-color: #190830;}

#radio_progress { background-color: #190830;}

#radio_load_progress { background-color: #200c46;}

#radio_play_progress { 
  background-color: #170c46;
  border-right: solid; color:rgba(96, 18, 42, .75); 
}

#radio_current { 
  background-color: #090510;
  border-color: #221164;
}

#radio_current_time { border-color: #221164}

#radio_volume_container:hover .icon-volume-high.clickable { color: red;}
#radio_volume_bar { background-color: rgba(64, 18, 64, .75);}
#radio_volume_slider { background-color: rgba(96, 18, 64, .75);}
.clickable.icon-volume-mute { color: rgba(42, 18, 96, .75);}

.icon-next.clickable.radio_icon { color: rgba(128, 18, 64, .9);}
.icon-next.clickable.radio_icon:active { color: rgba(200, 18, 48, .9);}

.icon-previous.clickable.radio_icon { color: rgba(128, 18, 64, .9);}
.icon-previous.clickable.radio_icon:active { color: rgba(200, 18, 48, .9);}

.icon-stop.clickable.radio_icon { color: rgba(128, 18, 64, .9);}
.icon-stop.clickable.radio_icon:active { color: rgba(200, 18, 48, .9);}

.clickable.radio_icon.icon-pause { color: rgba(128, 18, 64, .9);}
.clickable.radio_icon.icon-pause:active { color: rgba(200, 18, 48, .9);}

.clickable.radio_icon.icon-play { color: rgba(42, 18, 96, .9);}
.clickable.radio_icon.icon-play:active { color: rgba(128, 18, 64, .9);}

.icon-reverse.radio_icon.toggle_icon { color: rgba(42, 18, 96, .75);}
.toggle.radio_toggle.enabled>.icon-reverse.radio_icon.toggle_icon { color: rgba(128, 18, 64, .9);}
  
.icon-random.radio_icon.toggle_icon { color: rgba(42, 18, 96, .75);}
.toggle.radio_toggle.enabled>.icon-random.radio_icon.toggle_icon { color: rgba(128, 18, 64, .9);}



/*radio*/
/*misc.*/

.ui_frame { 
  background-color: rgba(25, 8, 64, 0.95)!important;
  border-color: rgba(32, 0, 96, 0.9);
  color: #CEBCF9;
}

.button {
  background-color: rgba(32, 0, 96, 0.9);
  color : #eee8fd
}
.button:hover {
  background-color: rgba(48, 0, 144, 0.9);
  color : #eee8fd;}

td input { background-color: rgba(9, 5, 16, .3)!important; border-color: red;}
td textarea { background-color: rgba(9, 5, 16, .3)!important; border-color: red;}

.ui_frame_body.ui_frame_body_bar { border-color: rgba(32, 0, 96, 0.9);}

.ui_frame_tooltip.has_thumb .ui_frame_content { 
  border-color: rgba(96, 18, 42, .75);
  border-width: 2px;
}

#overlay { background-color: rgba(18, 32, 32, .9);}

.icon-cross.clickable { color: #CEBCF9;}

.chat_room_url { 
  outline-style: solid;
  outline-color: rgba(42, 18, 96, .75);
  background-color: rgba(42, 20, 64, .75);
  color: rgba(128, 18, 64, .75);}

.icon-arrow-down.clickable { color: #CEBCF9;}

.icon-play { color: rgba(42, 18, 96, .9)!important;}
.icon-play:hover { color: rgba(42, 18, 96, .9);}
.icon-play:active { color: rgba(128, 18, 64, .9)!important;}

.icon-pause { color: rgba(128, 18, 64, .9);}
.icon-pause:hover { color: rgba(128, 18, 64, .9)}
.icon-pause:active { color: rgba(200, 18, 48, .9);}

.icon-stop { color: rgba(128, 18, 64, .9);}
.icon-stop:hover { color: rgba(128, 18, 64, .9);}
.icon-stop:active { color: rgba(200, 18, 48, .9);}

}
