@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.tigsource.com") {
  #page_bg {
    background: url(data:image/png;base64,);
  }
  
/* ========================================================= FOOTER ========================================================= */
  
  #footer-l {
    background:   url('data:image/png;base64,');
  }
  #footer-r {
    background:  url('data:image/png;base64,') 100% 0 no-repeat;
  }
  #footer {
    background:  url('data:image/png;base64,') repeat-x;
  }
  
/* ========================================================= HEADER ========================================================= */
  
  #header-l {
    background:  url('data:image/png;base64,') 0 0 no-repeat;
  }
  #header-r {
    background:  url('data:image/png;base64,') 100% 0 no-repeat;
  }
  #header {
    background:   url('data:image/png;base64,') repeat-x;
  }
  #logo-bg {
    background:  url('data:image/png;base64,') no-repeat;
  }
  #logo-bg > a:nth-child(1) {
    background: none;
  }
  
/* ========================================================= OTHER STUFF ========================================================= */
  
  body {
    color: rgb(221,221,221);
  }
  #mainbody {
    background: rgb(35,36,37);
  }
  .windowbg, .windowbg3 {
    background: rgba(31,32,33,.5);
    border-bottom: 1px solid rgba(40,42,46,1);
  }
  .windowbg2 {
    background: rgba(31,32,33,1);
    border-bottom: 1px solid rgba(40,42,46,1);
  }
  .searchfield {
    background: rgb(35,36,37);
  }
  .userinfo {
    background: rgb(35,36,37) url(data:image/png;base64,) no-repeat scroll 100% 0px;
  }
  a {
    color: #6F99B4;
  }
  a:hover{
    color: rgb(129, 162, 190);
  }
  .bordercolor, .tborder {
    border:  1px solid rgba(40,42,46,1);
  }
  .catbg, .catbg2, .catbg3 {
    background: #EFEFEF url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAACBCAMAAAA2eyBjAAAAZlBMVEUuLi4uLi8vLi8vLy8wMDAxMTEyMjIzMzIzMzMzNDQ0NDQ1NTQ1NTU1NTY2NjY3Njc3Nzc3ODc4ODg4OTk5Ojk6Ojo6Ozo7Ozs8PDw8PTw9PT09PT4+Pj4/Pj4/Pz8/QEBAQEBBQEFTPGhPAAAAJElEQVQY02NQZPrP9J/pPxMDEwOUBeOj0rggAxxDWcwMQwsAALQkH1YLch4zAAAAAElFTkSuQmCC) repeat-x scroll 0px -10px;
    border-bottom: 2px solid rgba(40,42,46,1);
  }
  .quote {
    background-color: rgba(41,42,43,1);
    color: rgb(221,221,221);
    border:  1px solid rgb(172,155,176)
  }
  #showcase {
    color: rgb(200,200,200);
  }
  .titlebg, .titlebg2 {
    background: transparent;
  }
  .windowbg2, tr.windowbg2 td {
    background: none;
  }
  tr.windowbg td {
    background: none;
    border-bottom: none;
  }
  input {
    background: #CCCCCC;
  }
  
  #footer2:after {
  content: " \a TIGSource Dark by mediochrea  ";
  white-space: pre;
  }
}
