css:
@-moz-document domain("github.com"), domain("raw.githubusercontent.com") {
    
/* background */  
body, .main-content, .menu-item:hover, .usage-bar, .discussion-timeline::before, .discussion-timeline-actions, table.files tr.navigation-focus td, tr.js-navigation-item, .markdown-body .highlight pre, .markdown-body pre, .social-count, .ace_marker-layer .ace_active-line {
  background-color: #1d1e2a !important;  
}
  
/* content */
.mini-repo-list-item, #contributions-calendar, .thread-subscription-status, .conversation-list-heading .inner, .btn, .select-menu-item.navigation-focus, .select-menu-item.navigation-focus.selected, .select-menu-item.navigation-focus.select-menu-action, .select-menu-item.navigation-focus .description-inline, .tabnav-tabs, .filter-bar, .boxed-group .counter, .dropdown-menu, .comment, .dropdown-divider, .blankslate, .boxed-group-inner, .menu, .div.progress, li.session-device.session-current, .dashboard-notice, .table-list-header, .repohead.experiment-repo-nav, .branch-infobar, table.files td, .Box-body-row, .readme .markdown-body, .readme .plain, .counter, .comment-form-head, .previewable-comment-form, .drag-and-drop, .simple-stacked-bar, table.capped-list tr:nth-child(2n), .commit-tease-contributors-color {
  background-color: #1a1b26 !important;  
}  
  
/* headers */  
.header, .boxed-group > h3, hr, .boxed-group .heading, .table-list-header, .subnav, .select-menu-header, .select-menu-item, .tabnav-tab.selected, .form-control, .form-select, .select-menu-no-results, .filter-item.selected, .filter-item:hover, .menu-item.selected, .menu-item.selected::before, .menu-heading, .subnav-item.selected, .subnav-item.selected:hover, .subnav-item.selected:focus, .file-header, .ace_gutter, .timeline-comment-header, .ace_gutter-cell, .ace_content, .dropdown-item:hover, .dropdown-item.zeroclipboard-is-hover, .reponav-item.selected, .commit-tease, .summary-stats li a:hover, .capped-card-content, .tint-box, .subnav-item:hover, .subnav-item:focus, .form-actions, table.capped-list th, .branch-group-heading, .header-search-scope, .flash {
  background-color: #14151f !important;  
}  
  
button, .btn {
background: #14151f !important;      
} 

/*  Remove ugly borders  */
  .timeline-comment-wrapper, .drag-and-drop, .border-right, .bottom-border, .discussion-timeline-actions, .timeline-comment-header, .comment, .timeline-comment, .comment-form-head, .timeline-comment-wrapper  {
    border-top-width:0px;
    border-bottom-width:0px;
    border-image-width: 0 0 0 0;
    border-width:0px !important;;
  }
  
  .navigation-focus {
    background-color: #2C2E40 !important;
  }
  
  .discussion-sidebar-item, .Box-body-row {
    border-top-color: #14151f !important;
  }
  
.timeline-comment::before {
    border-right-color: #14151f !important;
}
  
  .reason {
    padding: 4px !important;
  }

.timeline-comment::after {
    border-right-color: #14151f !important;
}
  
.octicon {
  fill: #8a7b70 !important;  
  }  
  
  .axis line {
    stroke: #14151f !important;  
  }  
  
.pjax-loader-bar .progress {
  background: #74857c !important;  
  height: 4px !important;
  box-shadow: none !important;
  }  
  
.file .image, .file-info-divider, .markdown-body img {
    background: transparent !important;  
  }  
  
.usage-bar .progress.no-highlight {
  background: #74857c !important;  
  }  
  
/* main text */
body, .text-muted, .select-menu-header .select-menu-title, button.dropdown-item, .subscriptions-content .sorted-by, .form-select, .form-group .form-control, h3, .billing-section .section-content, .pl-s .pl-s1, .pl-smi, .form-control, .form-select, td, .final-path, .ace_scroller, .dropdown-header, .repository-lang-stats ol.repository-lang-stats-numbers li .lang, button.btn-link, .commit-tease, textarea, .ace_editor, .ace_content {
  color: #8a7b70 !important;
}
  
/* links */
a, .vcard-detail .octicon, .contrib-number, .site-footer, label, .reponav-item .counter, .summary-stats li, .repository-lang-stats ol.repository-lang-stats-numbers li > a, .repository-lang-stats ol.repository-lang-stats-numbers li > span, .flash {
  color: #556b82 !important;
}  
  
/* accent color */  
.vcard-username, .mini-repo-list-item .repo-description, .news .alert .time, .news .alert .simple .title, .btn, .news .alert .title .subtle, .boxed-group .counter, .octofication .message p, .menu-heading, p, h4, table.files td.age, .header-search-scope, .protip-callout, th, .summary-stats li .num, .commit-tease .muted-link, .text-emphasized, .branch-meta, .no-results-message, .repository-meta, .header-search-input {
    color: #74857c !important;
  }  
  
/* remove unwanted borders */  
.vcard-details, .vcard-stats, .boxed-group > h3, .boxed-group .heading, .mini-repo-list-item, .boxed-group-inner, .contrib-column-first, .contrib-column, .site-footer, .conversation-list-heading, .btn, .select-menu-item, .header, .tabnav-tab.selected, .news .alert, .filter-bar, .form-control, .form-select, .blankslate, hr, .rule, .menu, .menu-item.selected, .menu-item, .menu-heading, .boxed-group-inner .help, .billing-section, .boxed-group-list > li:first-child, .dashboard-notice, .table-list-header, .table-list-bordered .table-list-cell, .table-list, dl.new-email-form, .subnav-item.selected, .subnav-item.selected:hover, .subnav-item.selected:focus, a.js-selected-navigation-item, .repohead.experiment-repo-nav, .file, .overall-summary, .repository-lang-stats-graph, .branch-infobar, .commit-tease, tr.js-navigation-item, .file-wrap, tr.js-navigation-item td, .readme .markdown-body, .readme .plain, .markdown-body blockquote, h2, h1, .subhead, .pulse-graph:first-child, .capped-card, .social-count, .header-search-scope, .protip-callout, .repo-list-item, .summary-stats li, .pulse-graph, .capped-card h3, .traffic-graph-stats, table.capped-list th, table.capped-list td, .commit-tease-contributors, .blob-num, .file-header, .file .image img, .file .image canvas, .file .image .border-wrap, .subnav-item, .branch-group-heading, .branch-summary:last-child, .no-results-message, .gh-header-meta, .wiki-wrapper .wiki-custom-sidebar, .flash {
  border: none !important;
  border-radius: 0px !important;
}  
  
label.form-control {
  box-shadow: none !important;    
  }  
  
  .file .image .border-wrap {
    box-shadow: 2px 2px 3px 3px rgba(0,0,0,0.2) !important;
  }  
  
/* a, body, td, th, p, .file-info, .commit-tease .muted-link, .dropdown-item, pre, .header-search-scope, .header-search-input, .file-navigation-option .input-group .form-control, .input-monospace {
    font-family: Consolas !important;
    font-weight: 600;
  }   */
  
.select-menu-header .select-menu-title, .menu, .menu-item.selected, .menu-item, h3, .branch-group-heading {
  text-shadow: none !important;
}
  
.repo-filterer .repo-filter.filter-selected, .octofication .message {
    border-color: #556b82;
  }
  
.dropdown-menu::after {
    border-color: transparent;
  }  
  
.avatar {
    -webkit-transition: -webkit-transform .8s ease-in-out !important;
    transition: transform .9s ease-in-out !important;    
  }  
  
.avatar:hover {
    transform:rotate(360deg) !important;
    -webkit-transform:rotate(360deg) !important;
    opacity: .4 !important;
  }    
}
