css:
@-moz-document url-prefix("https://slushpool.com/dashboard/") {
.contentWrapper {
    background-image: url("https://www.whitebeardscottage.com/shared_images/holiday_bg.png");
    background-position: bottom;
}
.bg-blue {
    background-color: blue;
}
.blue {
    color: blue;
}
.bg-red {
    background-color: red;
}
.progress-line-component > div {
    background-color: red;
}
.panelWrapper {
    opacity: 0.9;
    filter: alpha(opacity=90);
    /* For IE8 and earlier */
}
.panel-addon-component:before {
	box-shadow:0 0 15px gold;
}
}
