@-moz-document url-prefix("https://bs.to/"), url-prefix("http://bs.to/") {
/*************************/
/*Allgemeine Anpassungen*/
/***********************/
::-moz-selection { background: #5d92bf; color: white; }
::selection { background: #5d92bf; color: white; }
html {
background: none;
}

body {
    background-image: url("https://s13.postimg.org/gef9ssl13/blackorchid.png");
    background-repeat: repeat;   
}

body > #root {
 	margin: 0px auto 0px auto;
    min-width: 100%;
    border-radius: 0px;
    width: 100%;
	background-image: none;
    background-color: rgba(255,255,255,0);
    box-shadow: none;
    z-index: 1;
    font-size: 0.85em;
    color:white;
}

#root > section {
    margin: auto;
    padding-top: 100px;
    min-width: 60%;
	width: 80%;
    font-size: /*[[font_size]]*/;
    /*background-color: rgba(33, 33, 33, 0.5);
	-webkit-box-shadow: 0px 0px 6px 1px rgba(0,0,0,0.75);
    -moz-box-shadow: 0px 0px 6px 1px rgba(0,0,0,0.75);
	box-shadow: 0px 0px 6px 1px rgba(0,0,0,0.75);*/
}

a {
	text-decoration: none; 
    color: #365a79;
}
  
/*MessageBox (Neue Nachricht etc.)*/
.messageBox {
	color: white;
    -webkit-box-shadow: 0px 0px 6px 1px rgba(0,0,0,0.75);
    -moz-box-shadow: 0px 0px 6px 1px rgba(0,0,0,0.75);
	box-shadow: 0px 0px 6px 1px rgba(0,0,0,0.75);
}

.messageBox a {
	color: white;
}

.messageBox.info {
    background-color: #232323;
    border-color: #484848;
}

.messageBox.newMessages {
    background-color: #365a79;
    border-color: #52708a;
}
/*Scrollbar*/

html::-webkit-scrollbar, .home #shoutbox dl::-webkit-scrollbar, #sbUser::-webkit-scrollbar, #news > div::-webkit-scrollbar {
    width: 6px;
    background-color: rgba(33, 33, 33, 1);
    border-radius: 10px;
}

html::-webkit-scrollbar-thumb, #sbUser::-webkit-scrollbar-thumb, .home #shoutbox dl::-webkit-scrollbar-thumb, #news > div::-webkit-scrollbar-thumb {
    background-color: rgba(66, 66, 66, 1);
    width: 2px;
    border-radius: 10px;
}

footer {
	position: absolute;
    width: 100%;
    bottom: 0px;
    display: none;
}
}

@-moz-document url-prefix("https://bs.to/") {
/*************/
/*Navigation*/
/***********/

nav {
    position: absolute;
    z-index: 100;
    width:100%;
    top:0px;
    font-family: 'Droid Sans', 'Helvetica', 'Arial', sans-	serif;
	-webkit-box-shadow: 0px 2px 5px 2px rgba(0,0,0,0.75)!important;
	-moz-box-shadow: 0px 2px 5px 2px rgba(0,0,0,0.75)!important;
	box-shadow: 0px 2px 5px 2px rgba(0,0,0,0.75)!important;   
}

nav > ul {
 	border-width: 0px!important;
    background: none!important;
    background-color: rgb(35, 35, 35)!important;
    height: 4em;
    box-shadow: none!important;
}

nav > ul > li {
	line-height: 4em!important;
    border-width: 1px;
    border-color: black;
    transition: all ease 0.3s;
}

nav > ul > li:hover {
    background: none!important;
	background-color: rgba(48, 48, 48, 1)!important;
}

nav > ul > li > a, nav > ul > li > ul > li > a {
	text-decoration: none;
    color: #ffffff;
    font-size: 0.8em!important;
}

nav > ul > li > ul {
    left: 0px;
    border-width: 0px!important;   
    border-radius: 0px!important;
	line-height: 2.5em!important;
    background: none!important;
    background-color: rgba(48, 48, 48, 1)!important;
    transition: all ease 5s;
}

nav > ul > li > ul > li {
    border: none!important;
    border-width: 0px;   
    border-radius: 0px;
    border-bottom: 1px;
    background: none!important;
    background-color: rgba(39, 43, 53, 0);
    transition: all ease 0.3s;
}

nav > ul > li > ul > li:hover {
    background: none!important;
	background-color: rgba(56, 56, 56, 1)!important;
    border-width: 0px;
    border-radius: 0px;
}

nav > ul > li > ul > li > a {
	text-decoration:none; 
    color: white;
    font-size: 0.85em;
}

nav> ul > li > ul > li > a:hover {
	text-decoration:none;   
}
}

@-moz-document url-prefix("https://bs.to/") {
/*******************************************/
/****************Home Seite****************/
/*Credits für teile der Shoutbox Karzerus*/
/****************************************/
.home > h2:nth-child(1) {display:none;} 

.home > h2:nth-child(2) {display:none;} 

#sbposts.element.style {
 overflow: hidden;   
}

.home > .column {
    width: /*[[modules]]*/;
}

.home > .column > section > div, .home > #teaser > div {
 	background-color: transparent;   
}

#teaser {
    margin-top:-20px !important;
}

.home > .column > section, .home > #teaser {
    border: 0px solid black;
    margin-top: 15px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    -webkit-border-top-left-radius: 0px;
    -webkit-border-top-right-radius: 0px;
    background-image: none;
    background:#232323;
	-webkit-box-shadow: 0px 0px 6px 1px rgba(0,0,0,0.75);
    -moz-box-shadow: 0px 0px 6px 1px rgba(0,0,0,0.75);
	box-shadow: 0px 0px 6px 1px rgba(0,0,0,0.75);
}

.home > .column > section > header, .home > #teaser > header {
    display: block;
    color: #303F9F;
    padding: 10px 10px;
    box-shadow:none; 
    border-bottom:1px solid #E6E6E6;
}

.home > .column > section > header > h3, .home > #teaser > header > h3 {
    float: left;
    font-size: 1.4em;
    font-weight:300;
    letter-spacing: 0.03em;
    color: #365a79;
}

.home li:nth-child(even) {
    background-color: #1b1b1b;
}

.home #shoutbox dl {
    max-height: 400px;
    overflow-y: auto;

}
     
.home #shoutbox #sbOutput .error {
    font-weight: bold;
    color: #ff0000;
}

.home #shoutbox dl {
    margin:5px;
    
    
}
.home #sbPosts > dt:first-child {
    border-top:0px;}
    
.home #shoutbox dl > dt {
    background:#3c3c3c;
    padding-top:2px;
    border-top: 10px solid #313131;
    color: white;
}
    
.home #shoutbox dl > dd {
    margin-left:15px;
    padding:5px;
    min-height:20px;
    color: white;
}


.home #sbUser {
    font-size:0.8em;
    max-height:150px;
    overflow: auto;
}

.home #sbUser li {
    float: left;
    background: transparent;
    margin: 1px;
}

.home #user {
    width: 100%;
}

.home #sbMsg {
    background:#D3D3D3;
    font-family:Arial;
    height: 36px;
    width: 500px;
    padding:10px 10px;
    padding-bottom:5px;
    border-radius:2px;
    float: left;
    font-size: 0.8em;
    margin:0;
    color:#666;
    border: none;
}
.home #sbMsg:focus {
        color:#000; }
    
    #shoutbox > div:nth-child(2) > form:nth-child(3) > fieldset:nth-child(1) > label:nth-child(1) > legend:nth-child(1) {display:none;}
    #shoutbox > div:nth-child(2) {background:#313131;} /*Shoutbox Background */
    #sbPosts { background:#3c3c3c;
    border-bottom:0px solid #ccc;} /* Shoutbox Text Hintergrund */

.home #sbSubmit {
    margin-top:2px;
    padding:0px 0px;
    background:#365a79;
    border-radius:2px;
    float:right;
    border:none;
    color:#fff;
    height:30px;
    margin-left:4px;
    content: "Senden";
    transition: all ease 0.6s;
}
    
.home #sbSubmit:hover {
    background:#48779e;
}
.home #shoutbox time, .home #news time {
    font-size:0.65em;
    float:right;
}

.home #news article {
    display: block;
    padding-bottom:10px;
    margin-bottom: 10px;
    border-bottom:1px solid #ccc;
}

.home #news h4 {
    display: inline;
    font-size: 1em;
}

.home {
	font-size: 0.9em;   
}

.home #feedback h4 {
    margin-top: 10px;
}

.home #feedback h4:first-of-type {
    margin-top: 0;
}

.home table {
    width: 100%
}


/*Anpassungen für Ranks*/

.home #shoutbox a.admin, .home #shoutbox a.mod, .home #shoutbox a.smod, .home #shoutbox a.user, .home #shoutbox a.mobile, .home #shoutbox a.bullet_pink {
    background-repeat: no-repeat;
    background-position:left center;
    background-size:10px 10px;
    padding:0 0 0 15px;
    margin-left:5px;
}

.home #shoutbox a.mod, .rank.mod {
    color: #447eaf;
}

.home #shoutbox a.smod, .rank.smod {
    color: #9bd099;
}

.home #shoutbox a.cmod, .rank.cmod {
    color: #a261d4;
}


.home #shoutbox a.user {
	text-decoration: none;
    color: #ff6a00;
}
section#news > div > article > header {
 background: none!important;   
}
.rank.admin {

}
}

@-moz-document url-prefix("https://bs.to/") {
/***************/
/*Loginbereich*/
/*************/
#user {
	background: none;
    border: none;
    border-radius: 0px;

    height: 4em;
    margin: 0;
    padding: 0px;
    font-size: 16px;
    line-height: 4em;
    text-align: center;
}

li.user {
    background: #22394c!important;
    border-left: 1px solid black!important;
}


li.user:hover {
	background-color: #365a79!important;   
}

#user > ul {
	background-color: #365a79;   
}

#user > ul > li:hover {
	background-color: #477AA6;   
}

#user > br {
    display: none;
}

#user > fieldset{
	display: none;
}

#user > strong {
    	margin-bottom: 0px!important;
}

#userPanel {
	text-align: center;   
    height: auto;
    width: 100%;
    background-color: #313131;
}

#userPanel > fieldset {
 	margin: 0;
    padding:10px 0px;
}

#userPanel > fieldset > label {
	display: inline;
}

#userPanel > fieldset > label > input {
	float: none;   
}
}

@-moz-document url-prefix("https://bs.to/") {
/*******************/
/***Serienbereich**/
/*****************/

table {
	-webkit-box-shadow: 0px 0px 15px 1px rgba(0,0,0,1);
	-moz-box-shadow: 0px 0px 15px 1px rgba(0,0,0,1);
	box-shadow: 0px 0px 15px 1px rgba(0,0,0,1);
    margin-left: 10px;
}

tr:hover {
	background-color: #101010;  
}

tr {
	background-color: #212121;
}

#sp_left > table > tbody > tr:first-child {
	background-color: #131313;
   
}

#sp_left > h2, #sp_left > div, #sp_left > strong {
	padding-left: 10px;   
}

tr:nth-child(even) {
	background-color: #1b1b1b;
}

tr:nth-child(even):hover {
	background-color: #101010;
}

tr.watched:nth-child(even) {
	background-color: #2f4e69!important;   
}

td > a {
	color: #fff;
}

#social-nav, #description,#sp_left > div> a,#sp_right > small, .slink {
	display: none!important;
}

ul.pages {
	padding: 10px
}

.serie .epiInfo {
	background: #232323;   
}

/*Linker Bereich*/
.pages > li {
	width: 30px;
	line-height: 30px;
    border-radius: 0px;
    -webkit-box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.75);
	-moz-box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.75);
	box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.75);
}

.pages > li > a {
	color: white;
    transition: all ease 0.8s;
}

tr {transition: all ease 0.4s;}
.serie .watched {
	background-color: #365a79!important;   
}

.serie .watched:hover, .pages > li.watched > a:hover {
 	background-color: rgba(40, 65, 88, 1)!important;   
}

.serie .current, .pages .current {
	background-color: rgba(71, 71, 71, 1)!important;   
}

.pages > li {
    transition: all ease 0.6s;
	background-color: rgba(33, 33, 33, 1);   
}

.pages > li > a:hover {
	background-color: rgba(20, 20, 20, 1)!important;   
}

#seriesdata td ul li:not(.noentry):hover {
    background-color: rgba(0,0,0,0);
}

#sp_left > a {
	padding-left: 10px;   
}
}

