css:
@-moz-document domain(forum.mistrzowie.org) {
    /* your code goes here */

body { 
margin: 0px;
background-color: black;
background-image: url(http://i.imgur.com/4t7Z901.gif);
}
	.bodyline > table:nth-child(1) > tbody > tr > td{
	    background: url(http://i.imgur.com/TUW2paT.png) !important;
}

.bodyline > table:nth-child(1) > tbody > tr > td img{
	opacity: 0.0;
}

/* General font families for common tags */
font,th,td,p {  font-family: Verdana, Arial, Helvetica, sans-serif;  }
a:link,a:active,a:visited { text-decoration: none; color : #d7d9d9; }
a:hover { color : #efefef; }
p, td { font-size : 11px; color : #d7d9d9; }
hr	{ height: 0px; border: solid #b7b7b7 0px; border-top-width: 1px;}

/* This is the border line & background colour round the entire page */
.bodyline { background-color: #242424; border: 1px #6b6b6b solid;  }

/* This is the outline round the main forum tables */
.forumline { background-color: black; }

/* Main table cell colours and backgrounds */
td.row1	{ background-color: #383838 !important; }
td.row2	{ background-color: #2c2c2c !important; }
td.row3	{ background-color: #161616 !important; }
td.row_helped { background-color: #F2F0E6; }

/*
	This is for the table cell above the Topics, Post & Last posts on the index.php page
	By default this is the fading out gradiated silver background.
	However, you could replace this with a bitmap specific for each forum
*/
td.rowpic {
background-color: #FFFFFF;
background-image: url(http://i.imgur.com/2JkJMPe.gif);
background-repeat: repeat-y; height: 22px;
}

/* Header cells - the blue and silver gradient backgrounds */
th	{ color: #d2d4d5; font-size: 10px; font-weight: bold; background-image: url(http://i.imgur.com/2JkJMPe.gif); height: 28px; }

td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom {
background-image: url(http://i.imgur.com/TUgDIhm.gif);
background-color:#D1D7DC; border: black; border-style: solid; height: 28px;
}

/*
	Setting additional nice inner borders for the main table cells.
	The names indicate which sides the border will be on.
	Don't worry if you don't understand this, just ignore it :-)
*/
td.cat,td.catHead,td.catBottom { background-color: #C7D0D7; border-width: 0px; background-image: url(http://i.imgur.com/4t7Z901.gif); height: 28px; }
th.thHead,th.thSides,th.thLeft,th.thRight,th.thBottom {
font-weight: bold; border: #FFFFFF; height: 28px;
}

th.thCornerL,th.thTop,th.thCornerR {color: #FFFFFF; font-size: 10px; font-weight: bold; background-color: #000000; background-image: url(http://i.imgur.com/2JkJMPe.gif); height: 28px; }

td.row3Right,td.spaceRow { background-color: #474747; border: #474747; border-style: solid; }

th.thHead,td.catHead { font-size: 12px; border-width: 1px 1px 0px 1px; }
th.thSides,td.catSides,td.spaceRow	 { border-width: 0px 1px 0px 1px; }
th.thRight,td.catRight,td.row3Right	 { border-width: 0px 1px 0px 0px; }
th.thLeft,td.catLeft	  { border-width: 0px 0px 0px 1px; }
th.thBottom,td.catBottom  { border-width: 0px 1px 1px 1px; }
th.thTop	 { border-width: 1px 0px 0px 0px; }
th.thCornerL { border-width: 1px 0px 0px 1px; }
th.thCornerR { border-width: 1px 1px 0px 0px; }

/* The largest text used in the index page title and toptic title etc. */
.maintitle,h1,h2 { font-weight: bold; font-size: 22px; font-family: "Trebuchet MS",Verdana, Arial, Helvetica, sans-serif; text-decoration: none; line-height : 120%; color : #000000; }

/* General text */
.gen { font-size : 11px; }
.genmed { font-size : 10px; }
.gensmall { font-size : 10px; }
.gen,.genmed,.gensmall { color : #b1b2b2; }
a.gen,a.genmed,a.gensmall { color: #cacecf; text-decoration: none; }
a.gen:hover,a.genmed:hover,a.gensmall:hover	{ color: #ffffff; }
	
a:hover, a:hover *, a:visited:hover, a:visited:hover *, span[onclick]:hover, div[onclick]:hover, [role="link"]:hover, [role="link"]:hover *, [role="button"]:hover *, [role="menuitem"]:hover, [role="menuitem"]:hover *, .link:hover, .link:hover * {
    color: #F0F0F0 !important;
    text-shadow: 0 0 5px rgba(255,255,200,0.9) !important}


.nav1 { height: 23px; vertical-align:center; background-image: url(http://i.imgur.com/2JkJMPe.gif); padding: 0; margin: 0; }
.nav_top { color: #d7d9d9; padding: 0; margin: 0; }
.nav_top li {  float: center; display: inline;  }	
.nav_top a:link, .nav_top a:visited { float: center; font-size: 12px;  font-weight: bold;	padding: 4px 8px 6px 8px; text-decoration: none; color: #cacecf !important; }
.nav_top a:link.active, .nav_top a:visited.active, .nav_top a:hover { color: #e1e1e1 !important;background: none;}

/* The register, login, search etc links at the top of the page */
.mainmenu		{ font-size : 11px; color : #000000 }
a.mainmenu		{ text-decoration: none; color : #d7d9d9; }
a.mainmenu:hover	{ text-decoration: underline; }

/* Forum category titles */
.cattitle		{ font-weight: bold; font-size: 10px ; letter-spacing: 1px; color : #d7d9d9}
a.cattitle		{ text-decoration: none; color : #cacecf; }
a.cattitle:hover	{ text-decoration: underline; }

/* Forum title: Text and link to the forums used in: index.php */
.forumlink		{ font-weight: bold; font-size: 11px; color : #d7d9d9 !important;; }
a.forumlink 	{ text-decoration: none; color : #d7d9d9 !important; }
a.forumlink:hover{ text-decoration: none; color : #e5e5e5 !important; }

/* Used for the navigation text, (Page 1,2,3 etc) and the navigation bar when in a forum */
.nav			{ font-weight: bold; font-size: 11px; color : #000000;}
a.nav			{ text-decoration: none; color : #cacecf; }
a.nav:hover		{ text-decoration: none; }

/* titles for the topics: could specify viewed link colour too */
.topictitle,h1,h2	{ font-weight: bold; font-size: 11px; color : #000000; }
a.topictitle:link   { text-decoration: none; color : #c5c5c5; }
a.topictitle:visited	{ text-decoration: none; color : #d7d9d9; }
a.topictitle:hover	{ text-decoration: none; color : #e8e8e8; }

/* Name of poster in viewmsg.php and viewtopic.php and other places */
.name			{ font-size : 11px; color : red;}

/* Location, number of posts, post date etc */
.postdetails		{ font-size : 10px; color : #cacecf; }

/* The content of the posts (body of text) */
.postbody { font-size : 12px;}
a.postlink:link	{ text-decoration: none; color : #4356f9 }
a.postlink:visited { text-decoration: none; color : #6868ff; }
a.postlink:hover { color : #1a91ff;}

/* Quote & Code blocks */
.code {
	font-family: Courier, 'Courier New', sans-serif; font-size: 11px; color: #006600;
	background-color: #FAFAFA; border: #D1D7DC; border-style: solid;
	border-left-width: 1px; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px
}

.quote {
	font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 11px; color: #444444; line-height: 125%;
	background-color: #FAFAFA; border: #474747; border-style: solid;
	border-left-width: 5px; border-top-width: 5px; border-right-width: 2px; border-bottom-width: 2px
}

/* Copyright and bottom info */
.copyright		{ font-size: 10px; font-family: Verdana, Arial, Helvetica, sans-serif; color: #FFFFFF; letter-spacing: -1px; background: url(http://i.imgur.com/2JkJMPe.gif);}
a.copyright		{ color: red; text-decoration: none;}
a.copyright:hover	{ text-decoration: underline; color : #DD6900; }

/* Form elements */

form { margin: 0; }
form { display: inline; }

input,textarea, select { color : black; font: normal 11px Verdana, Arial, Helvetica, sans-serif; border-color : #000000; }

/* The text input fields background colour */
input.post, textarea.post, select { background-color : #c1c1c1 !important; }
input.post2, textarea.post2, select	{ background-color : #c1c1c1; }
input { text-indent : 2px; }

/* The buttons used for bbCode styling in message post */
input.button { background-color : #c1c1c1; color : #000000; font-size: 11px; font-family: Verdana, Arial, Helvetica, sans-serif; }

/* The main submit button option */
input.mainoption { background-color : #c1c1c1; font-weight : bold; }

/* None-bold submit button */
input.liteoption { background-color : #c1c1c1; font-weight : normal; }

/* This is the line in the posting page which shows the rollover
	help line. This is actually a text box, but if set to be the same
	colour as the background no one will know ;)
*/
.helpline { background-color: #DEE3E7; border-style: none; }

/* Highlight bad words for moderators instead of censoring them */
span.badwordhighlight { background-color: #FFFF00; }

.topbkg { background: #dbe3ee url(http://i.imgur.com/LhaIQKy.jpg) repeat-x }
.topnav { font-size:10px;background: #e5ebf3 url(http://i.imgur.com/EtklV1c.gif) repeat-x;color:#dd6900; height: 21px; white-space: nowrap; text-align: center; border: 0px solid #91a0ae; border-width: 1px 0 1px 0 }

.pm { font-size: 11px; text-decoration: none; color: re } 
.table0
{
	padding: 4px;
	border-bottom: 1px #dfdfdf;
	border-left: 1px solid #dfdfdf;
	color : #000000;
	font-size : 10px;
}

.sb1 { background-color: #868686; }
.sb2 { background-color: #b2b2b2; }

.pagination {
	color: white;
	font-size: 10px;
	font-weight: bold;
	padding: 3px;
   padding-bottom: 4px;
	cursor: pointer;
	text-decoration: none;
}
.pagination a, .pagination a:visited, .pagination a:active {
	background-color:  #c1c1c1;
	color: #000;
	border-color: #000;
	border: #000 1px solid;
	padding: 1px;
	text-decoration: none;
}
.pagination a:hover {
	background-color: #f0f0f0;
	color: #666;
	border-color: black;
	border: black 1px solid;
	text-decoration: none;
	padding: 1px;
}
  }
