css:
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain("drive2.ru"){

 
.c-car-info 
{
    background: #333333;
    
}


html {
    height: 100%;
    background: #003333;
    color: #999999;
    font-size: 16px;
    line-height: 1.4;
}
.c-block 
{
    clear: both;
    position: relative;
    margin-bottom: 20px;
    padding: 20px 20px 0;
    background-color: #333333;
    border-radius: 12px;
    border-bottom: 1px solid #999900;
}

.c-button 
{
    border-radius: 4px;
}

.c-pc-card 
{
    border-bottom: 0 solid #dcdcdc;
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    background: #333333;
    
}

.c-block__more {
    width: 100%;
    border: none;
    outline: 0;
    box-sizing: content-box;
    clear: both;
    display: block;
    margin: 20px -20px -20px;
    padding: 12px 20px 11px;
    border-top: 1px solid #999900;
    font-size: 16px;
    line-height: 1.25;
    background: #333333;
    color: #205090;
    text-align: center;
    cursor: pointer;
    border-bottom-left-radius: 12px;
    border-bottom-right-radius: 12px;
}

.c-car-desc.is-shaded:before 
{
    background: linear-gradient(to bottom,rgba(255,255,255,0),#333333);
}

.c-dv-sns 
{
    display: none;
}

.c-dv-label 
{
    display: none;
}

.totop 
{
    position: fixed;
    z-index: 2;
    width: 176px;
    bottom: 40px;
    font-size: 16px;
    line-height: 3;
    color: #717171;
    fill: #982525;
    text-align: center;
    cursor: pointer;
    border-radius: 9px;
    padding: 20px;
    box-sizing: border-box;
}

.totop:hover 
{
    color: #585858;
    fill: #860b0b;
    background: #ffffff;
}
.c-nav-block__link {
    color: #999900;
    cursor: pointer;
    display: block;
    margin-right: 5px;
    padding: 4px 0;
}

.c-block:not(.c-block--base):after, .c-block__footer:after 
{
    margin-top: 25px;
}

.c-lb-card 
{
    border-bottom: 1px solid #f1f1f1;
    background: #f1f1f1;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
}

.c-block__footer 
{ 
    margin-bottom: 10px;
    border-radius: 15px; 
}
.xKMZEpnoLxw {
    position: relative;
    margin-bottom: 20px;
    padding: 20px 20px 0;
    background-color: rgba(0, 0, 0, 0.62);
   }
a.c-link.c-link--text {
    color: #999966;
} 
}
