css:
@-moz-document domain("theartistunion.com") {
.navbar-default {
    background-color: rgb(0, 0, 0);
    border-top: 1px solid #111;
}

body {
    background: url(http://i.imgur.com/rE8NUqa.png);
}

.welcome-card {
    background-color: hsl(0, 0%, 0%);
}

.side-banner {
    background-color: #000000;
}

.content-page .item-list {
    background-color: #000000;
}

.content-page .item-list .track-list-item:hover {
    background: #080808;
}

.dropdown-menu {
    background-color: rgba(0,0,0,7);
    border: 1px solid #111;
}

#search #hits-container {
    background-color: #000000;
}

#search #hits-container .track-list-item:hover {
    background-color: #080808;
}

.dashboard .dashboard--nav .dashboard--nav--title {
    border-bottom: 1px solid hsl(0,0%,6%);
    background: #000;
}

.dashboard .dashboard--content {
    border-left: 1px solid hsl(0,0%,6%);
    background: #000;
}

.dashboard .dashboard--nav .tabs {
    background: black;
}

.dashboard .dashboard--nav {
    background: #000;
}

tr {
    border: 1px solid #111;
}

.dashboard-table table tbody tr td {
    border-top-color: hsl(0,0%,6%);
}

.dashboard {
    background-color: hsl(0, 0%, 0%);
}

.dashboard .dashboard--content .dashboard--main .referral-code code {
    background-color: rgb(4, 4, 4);
}

.profile-card {
    background-color: #000000;
}

.profile-description {
    background-color: #000000;
}

.public-page .track-lists {
    background-color: #000000;
}

.public-page .track-list .track-list-item:hover, .public-page .item-list .track-list-item:hover {
    background-color: #080808;
}

.modal--container {
    background-color: #000;
    border: 1px solid hsl(0,0%,6%);;
    color: #29272c;
}

.ReactModal__Overlay--after-open {
    background: rgba(17, 17, 17, 0.77);
}

.static .static--content {
    background-color: #000000;
}

.form-card {
    background-color: hsl(0, 0%, 4%);
}

.form-control {
    color: #555555;
    background-color: hsl(0, 0%, 3%);
    border: 1px solid #333;
}

.track-card {
    background-color: #000000;
}

.public-page .track-fans, .public-page .profile-donors {
    background-color: #000000;
}

.alert .alert--message {
    background: #000;
    border-bottom: 2px solid #000;
}

.artists-page {
    background-color: #000000;
}

.artists-page .artists-page--hero {
    border-bottom: 2px solid hsl(0,0%,6%);
    border-top: 2px solid hsl(0,0%,6%);
}

.static #team .card .card-body {
    background-color: hsl(0,0%,6%);
    border: 2px solid hsl(0,0%,6%);
}

.navbar-default .navbar-nav>li>.dropdown-menu>li.divider {
    background-color: #202020;
}

.modal--overlay.modal--overlay--mask {
    background-color: rgba(0, 0, 0, 0.57);
}

#player {
    box-shadow: 0 0 4px 0 rgb(16, 16, 16);
    background: #101010;
    border-bottom: 1px solid rgb(16, 16, 16);
    border-radius: 0;
}

#player .player--progress-bar .progress-bar--background {
    background-color: #202020;
    border-radius: 0;
}

#player .player--progress-bar .progress-bar--foreground {
    border-radius: 0;
}
    
.progress {
    background-color: #0c0c0c;
}
}
