css:
@-moz-document domain("www.corazondemelon.es") {

#container {
    background: url(http://img00.deviantart.net/f414/i/2016/324/8/9/nathfondo_by_chuscaro-dap2zn1.png) no-repeat fixed center top !important;
    background-size: cover!important;
}
#container.connected #header {
    background-image: url(http://orig10.deviantart.net/f5ff/f/2016/326/d/e/nathbarra_by_chuscaro-dapary6.png) !important;
}
.idcard-member {
    background: url(http://orig03.deviantart.net/da14/f/2016/326/a/f/nathid_by_chuscaro-dapb51u.png) no-repeat !important;
}
#avatarGirl {
    padding-left: 40px!important;
}
#logged-deco a {
    background-image: url(http://orig06.deviantart.net/74a2/f/2016/326/5/9/nathlogout_by_chuscaro-dapayft.png)!important;
}
#logged-pseudo a {
   color: darkslateblue;
}
#logged-episode {
   padding:2px 4px;
   color:darkslategray;
   text-shadow:none;
   text-transform:uppercase;
   font-family: "century gothic", sans-serif;
}
#logged-pseudo span {
   float:left;
   color:darkslategray;
   text-shadow:none;
   font-family: "century gothic", sans-serif;
}
#astrological-sign a {
   text-decoration:none;
   font:oblique 11px calibri;
   color:white!important;
   text-transform:uppercase;
}
}
