Updates:

2018.01.03 - dark mode for entire site;
2016.11.30 - styles for reply form and markdown help;

css:
@-moz-document domain("designernews.co") {
:root {
    --color_bg1: #15161a;
    --color_bg2: #1f2226;
    --color_bg3: #2a2e33;
    --color_bg3_95p: rgba(42, 46, 51, 0.95);
    --color_bg4: #343a40;
    --color_bg5: #3f474d;
    --color_bg6: #495359;
    --color_bg7: #546066;
    --color_bg8: #5e6e73;
    
    --color_accent0: #1b4380;
    --color_accent1: #0c64e8;
    --color_accent2: #3686ff;
    --color_accent3: #4d94ff;
    
    --color_fg5: #e9f2f2;
    --color_fg4: #dce5e5;
    --color_fg3: #d0d7d9;
    --color_fg2: #c4cacc;
    --color_fg1: #b8bdbf;
    
    /* V: 55-70 */
    --color_interFg1: #87898c;
    --color_interFg2: #939699;
	--color_interFg3: #9fa3a6;
	--color_interFg4: #abb0b3;
    
    --color_border1: rgba(255, 255, 255, 0.02);
    --color_border2: rgba(255, 255, 255, 0.04);
    --color_border3: rgba(255, 255, 255, 0.06);
    --color_border4: rgba(255, 255, 255, 0.08);
    --color_border5: rgba(255, 255, 255, 0.1);
}

/* BACKGROUNDS */

.sidebar-accolades {
	background-color: transparent;
}

body,
#main-footer, body.podcast-index, body.podcast-episode {
	background-color: var(--color_bg1);
}

#page-main-content-inner,
#page-sidebar-inner,
#header-sub-navigation-container,
.sub-navigation-hover-dropdown,
.sidebar-rte {
	background-color: var(--color_bg2);
    box-shadow: 0 0 6px rgba(0, 0, 0, 0.15);
    border: 1px solid var(--color_border1);
}

#header-additional-navigation {
	box-shadow: 0 0 6px rgba(0, 0, 0, 0.35);
    border: 1px solid var(--color_border2);
}

#header-additional-navigation:after {
    display: none;
}

.standard-button.outline:focus, .podcast-episode-list-item:hover, #podcast-about-writeup, #player-scrubber-bar {
	background-color: var(--color_bg4);
}

.sidebar-ad-block,
#category-list-header, #badges-header-title, #listings-deals-container {
    background: none;
	background-color: var(--color_bg2);
}

#header-bar, #header-additional-navigation {
	background-color: var(--color_accent0);
}

.story-upvote-button,
.standard-text-field, .standard-text-area,
.standard-button.outline,
.story-upvote-button, .standard-text-field, .standard-text-area, .standard-button.outline {
	background-color: var(--color_bg3);
    fill: #4a92ff;
    color: #4a92ff;
    border-radius: 4px;
}

.story-comment-inner:hover .story-comment-header:hover,
.story-comment-inner:hover .story-comment-header,
#sub-navigation-badges-dropdown a, .rte blockquote, .markdown-preview-content blockquote, #_syndicate_html tr:nth-child(odd), .sidebar-rte ul, .sidebar-rte ol, #header-account-navigation, .checkbox-wrapper, .deal-list-item-cta-group code, .radio-wrapper .radio-check, #user-deals-sidebar-container p, #user-karma-label, #user-id-label, #user-twitter-link, #site-podcast-player, .comment-login-block:first-child, .resizing-text-area-container.active {
    background-color: var(--color_bg3);
}

#sub-navigation-badges-dropdown a:hover, #job-board-list .montana-list-item.featured, .job-filters-section-container a:not(.clear-location-filter-button), .badge-link-item a, .password-field-visibility-toggle, #header-search-container, input, #story-header-more-navigation, .select-label {
	background-color: var(--color_bg3);
}

.job-filters-section-container a:not(.clear-location-filter-button):hover, .badge-link-item a:hover, .badge-link-count, .password-field-visibility-toggle:hover, .checkbox-wrapper input:focus~.checkbox-check, .radio-wrapper input:focus~.radio-check {
	background-color: var(--color_bg4);
}

.list-story-star-grouper {
    background-color: var(--color_bg2);
    background: -webkit-linear-gradient(right,var(--color_bg2) 60%,rgba(255,255,255,0));
    background: linear-gradient(to left,var(--color_bg2) 60%,rgba(255,255,255,0));
}

/* BORDERS */

.stories-show .sidebar-ad-block,
#story-main-container,
.montana-list-item {
	border-bottom: 1px solid var(--color_border3);
}

.standard-text-field, .standard-text-area {
	border-color: var(--color_border1);
}

.standard-text-field:focus, .standard-text-area:focus, .rte blockquote, .markdown-preview-content blockquote, .resizing-text-area-container.active {
	border-color: var(--color_bg7);
}

#footer-bottom-container,
.nevada-list-item,
.montana-list-item:first-child {
	border-top: 1px solid var(--color_border3) !important;
}

.markdown-syntax-grouper,
#comment-login-required,
.standard-button.outline {
	border: 1px solid var(--color_border1) !important;
}


ul.story-comment-thread-nested {
	border-left: 3px solid var(--color_border1);
}

.comment-login-block:first-child,
.resizing-text-area-container.active {
	border-right: 1px solid var(--color_border4);
}

.markdown-controls-container {
	border-top: 1px dashed var(--color_border3);
}

.sub-navigation-hover-dropdown:after {
	border-bottom-color: var(--color_border1);
}

.sidebar-job-listing-link:hover {
    background-color: var(--color_border1);
}

.standard-button.green {
    background-color: #13753c;
}

.standard-button.yellow {
    background-color: #b78100;
}

.rte hr, .markdown-preview-content hr {
	background-color: var(--color_border4);
}

#feed-motd-container, body.podcast-index #main-footer, body.podcast-episode #main-footer, #podcast-episodes-list, .page-section, .about-badge-list-item, #new_user_session, #badges-header-title, #header-account-navigation .notification, .users-edit .tabbed-content-tabs, .form-table-row, #edit-account-footer, .checkbox-wrapper, #no-notifications-label, .deals-badge-list-item, .resizing-text-area-container, .radio-wrapper .radio-check, .radio-wrapper input:focus~.radio-check, #user-deals-sidebar-container p, #user-karma-label, #user-id-label, #user-twitter-link, #user-numbers-container, .select-label, .things-badge-list-item {
	border-color: var(--color_border4);
}

#podcast-home-header, #header-account-navigation:after, #story-header-more-navigation:after {
	border-bottom-color: var(--color_bg3);
}

#header-additional-navigation:after {
	border-bottom-color: var(--color_accent0);
}

.select-label:after {
	border-top-color: var(--color_interFg1);
}

/* FOREGROUNDS */

.montana-hero-info p, .job-listing-month-name, .episode-item-number, #no-notifications-label, .markdown-controls-note, .montana-item-meta-bullet, .nevada-item-meta-bullet {
	color: var(--color_interFg1);
}

.markdown-controls-note:hover, .search-show .powered-by {
	color: var(--color_interFg4);
}

body, #story-header-meta, #story-header-meta span, 
.nevada-item-header-main,
p, ul,
.comment-login-block p,
.story-comment-points,
.sidebar-job-listing-superline,
.sidebar-ad-block,
#story-comments-sort-list a.active,
#sub-navigation-list-items>#sub-navigation-visible-items>li>a,
.montana-item-meta,
.list-story-domain,
#sidebar-latest-podcast-titles-episode-title,
#sidebar-latest-podcast-titles-episode-date,
#story-header-domain,
.story-upvote-button, .standard-text-field, .standard-text-area,
.markdown-syntax, .job-filters-section-container a:not(.clear-location-filter-button), 
.episode-item-title, .rte, .markdown-preview-content, .badge-link-item a, #badges-header-title, #story-header-more-navigation a, #feed-motd-message, #feed-motd-content, #user-karma-label, #user-id-label, #user-twitter-link, #user-earned-colors-container h3, #listings-deals-right, #player-titles-episode-title, #player-close-button {
	color: var(--color_fg1);
}
.comment-login-block h3,
#story-comments-header h2,
#story-header-title a,
#story-body p a,
.nevada-item-header-sub a,
.nevada-item-body p a,
#main-footer a,
.sidebar-job-listings h3,
.sidebar-job-listing-title,
.sidebar-job-listings-more,
#story-comments-sort-list a,
#sub-navigation-list-items>#sub-navigation-visible-items>li>a.active,
.montana-hero-info h2,
.montana-list-pagination a,
.montana-item-title,
.montana-item-meta a,
.sub-navigation-hover-dropdown a:not(.ignore-standard),
.sidebar-podcast-episode h3,
#category-list-title,
#story-header-meta a,
.sidebar-rte h3,
.toggle-markdown-preview, .rte h1, .markdown-preview-content h1, .rte h2, .markdown-preview-content h2, .rte h3, .markdown-preview-content h3, .rte h4, .markdown-preview-content h4, .rte h5, .markdown-preview-content h5, .rte h6, .markdown-preview-content h6, #header-account-navigation a:not(.ignore-standard), li[data-tab], .form-table-title, #profile-basic-info h1, #user-twitter-link svg {
	color: var(--color_fg1);
    fill: var(--color_fg1);
}

.story-comment-vote-controls {
	fill: var(--color_interFg1);
}

.rte a, .markdown-preview-content a, .podcast-home-header-navigation a, .podcast-home-header-navigation li, #login-intro-title, .badge-link-count, .sidebar-rte a {
	color: var(--color_fg2);
    fill: var(--color_fg2);
}

#header-additional-navigation a:not(.ignore-standard), .job-filters-section-container a:not(.clear-location-filter-button):hover, input, #player-titles-episode-playing, .select-label {
	color: var(--color_fg3);
}

#carbonads a, li[data-tab].active, .nevada-item-meta a {
	color: var(--color_accent2);
}

.montana-list-pagination a:hover, #carbonads a:hover
.sub-navigation-hover-dropdown a:not(.ignore-standard):hover,
.toggle-markdown-preview:hover, .podcast-home-header-navigation a:hover, .sidebar-rte a:hover, #story-header-more-navigation a:hover {
	color: var(--color_accent3);
    fill: var(--color_accent3);
}

#header-message-container p, #header-account-navigation a:not(.ignore-standard):hover, li[data-tab]:hover, #sub-navigation-badges-dropdown a:hover, #user-id-label:hover, #user-twitter-link:hover, #user-twitter-link:hover svg, .sub-navigation-hover-dropdown a:not(.ignore-standard):hover {
	color: var(--color_fg5);
    fill: var(--color_fg5);
}

.standard-button.yellow, .standard-button.green, #header-additional-navigation a:not(.ignore-standard):hover, .montana-item-meta a:hover {
	color: var(--color_fg5);
    fill: var(--color_fg5);
}

.standard-button.yellow:hover, .standard-button.green:hover {
    color: #fff;
    fill: #fff;
}

#sub-navigation-list-items>#sub-navigation-visible-items>li>a:hover,
#sub-navigation-list-items>#sub-navigation-visible-items>li>a.active:hover {
    color: var(--color_accent3);
    background-color: rgba(45,114,217,.15);
}

/* LAYOUT */

.sidebar-ad-block {
    margin-bottom: 16px;
}

#page-sidebar-inner {
	border-radius: 4px;
}

* {
	text-shadow: 0.7px 0.7px 0 rgba(0, 0, 0, 0.2);
}

a.swiftype-logo {
    filter: invert(1);
}
}
