css:
@-moz-document domain(tproger.ru) {
.navbar-inverse .nav li.dropdown.open > .dropdown-toggle, .navbar-inverse .nav li.dropdown.active > .dropdown-toggle, .navbar-inverse .nav li.dropdown.open.active > .dropdown-toggle, div.navbar-inverse .nav-collapse .nav > li > a, .navbar-inverse .nav-collapse .dropdown-menu a, #masthead .bluth-navigation.shrunk .mini-logo h1, #masthead .bluth-navigation.shrunk .mini-logo img, #masthead .searchform input, .widget-head, .widget-head a, .navbar .brand, #masthead .searchform a,
.box,
p, .entry-content ul li, .entry-content p, .entry-content ol li,
.site-footer #footer-body .bl_tabs ul li .tab_text h5 a, .bl_tabs ul li .tab_text h5 a,
.descr,
.first, .second
{
	color: rgb(136, 136, 136);
}

#feed_mode_select
{
	color: rgb(136, 136, 136) !important;
}

.main-page article:not([id])
{
	display: none;
}

blockquote
{
  border-left: 5px solid #181818;;
}

.post-title h1,
body,
.widget-area div.widget_search input[type="text"], #footer-body div.widget_search input[type="text"],
.entry-title a,
.main-page article:hover .entry-title-description,
.header_searchbox_input
{
  color: white !important;
}

footer.entry-meta ul a,
.pagination>a:hover
{
	color: #04a49c;
}

.pagination>a,
.tumbler:after
{
	background: rgb(68, 68, 68);
}

header#masthead, .navbar-inverse .navbar-inner, .dropdown-menu, .widget-head,
.box,
.entry-content,
.page #content article.type-page,
.widget-area div.widget_search input[type="text"], #footer-body div.widget_search input[type="text"],
.bl_tabs .tab-content,
.header_searchbox
{
	background: rgb(34, 34, 34);
}

body,
.tumbler:before
{
	background-color: #141414;
}

code,
.widget_tag_cloud .tagcloud a,
#bl_side_posts>ul li:hover, #bl_side_comments>ul li:hover,
.tquiz_progress
{
	color: #43b298;
  background-color: #191919;
}

.feed_switcher_wrapper
{
	border-left-color: rgb(68,68,68); 
}

.quote_refresh_block > img
{
  box-sizing: border-box;
  padding-left: 100%;
  background: url(http://s8.hostingkartinok.com/uploads/images/2016/08/2d25b3dc1fd9eba20cfde9ca0971ac71.png);
  height: 60px;
}

.btn.bluth,
.bluth.green,
.tx_rating_plus, .tx_rating_minus
{
  border-color: #04a49c;
  color: #fff;
  background: #04a49c;
}

.btn.bluth:hover
{
  background-color: #02aea6;
  outline: 0;
  border-color: #02aea6;
}

.entry-title-description:after
{
  background: linear-gradient(to bottom,transparent,hsla(0,0%,7%,1));
}


.main-page article .entry-image
{
  border-bottom: 1px solid #121212;
}

.entry-title-description
{
	color: rgba(255, 255, 255, 0.45) !important;
}

hr, .answer
{
	border-color: #191919;
}


code,
.widget_tag_cloud .tagcloud a
{
  border: 1px solid #171717;
}

.crayon-theme-classic .crayon-striped-line,
.crayon-syntax div
{
	background: #23252 !important;
}
}
