css:
@-moz-document domain("shikimori.one") {
::-webkit-scrollbar {
    width: 10px;
}
::-webkit-scrollbar-thumb {
    background: rgb(27, 27, 27) !important;
}
::-webkit-scrollbar-track {
    background: rgb(13, 13, 13) !important;
}
::-webkit-scrollbar-track-piece {
    background: rgb(13, 13, 13)!important;
}
::-webkit-scrollbar-corner {
    background: rgb(13, 13, 13)!important;
}
* {
    color: #C7D4D4 !important;
    border-color: #444444 !important;
    -webkit-transition: width 0.2s ease-in-out;
}
img:not(.mfp-img) {
    opacity: 0.7;
}
img:hover {
    opacity: 1;
}
.image-decor::after {

    background: inherit !important;

}
body,
.b-to-top .slide,
select {
    background: rgb(10, 10, 10) !important;
}
body>*,
.marker,
.date {
    background-color: rgb(10, 10, 10) !important;
}
.to-top-fix,
.l-top_menu,
.l-top_menu .submenu>.entry,.l-top_menu-v2 .menu-dropdown>button {
    background: rgba(17, 17, 17, 0.3) !important;
}
.l-top_menu .submenu,
ul.popup,.l-top_menu-v2 .submenu {
    background: rgba(17, 17, 17, 0.9) !important;
}
.l-menu {
    background: rgba(68, 68, 68, 0.1)!important;
}
.c-image,
.c-about,
.block,
.b-breadcrumbs,
.c-my_list,
.b-link_button,
.cc-player_controls,
.cc,
.collapsed,
.posters,
.b-comments .comments-loader, .b-comments .comments-hider, .b-comments .comments-expander,.p-user_rates-index .list-lines tr.edit-form form,.b-hot_topics-v2,.l-top_menu-v2 .global-search .search-results,.b-anime_status_tag.episode, .b-anime_status_tag.studio{
    background-color: rgba(17, 17, 17, 0.3) !important;
}
.tooltip-inner,
.inner {
    background-color: rgb(17, 17, 17) !important;
}
.subheadline,
.comments-loader,
input,
.input,
textarea {
    background-color: rgb(13, 13, 13) !important;
}
.trigger,
.click-loader,
.option,
.shade,
.b-quote,
.midheadline,
.linkheadline,
.headline,
.close,
.marker-text,
.b-feedback>.hover-activator,
.toggler,
.ac_odd,
.b-hot_topics,
.locale-ru .b-offtopic_marker:before,
.locale-ru .b-new_marker:before,
.locale-ru .b-summary_marker:before,
.ac_over,
.name,
.b-anime_status_tag.other,
.b-close{
    background: inherit !important;
}
li a:hover,
.selected,
.match-link:hover,
.selectable:hover,
.b-link_button:hover,
.menu-items>.entry>a:hover,
.submenu>a:hover,
.l-top_menu .submenu>.entry:hover,
.block-list li:hover,
.b-block_list li:hover,
.l-top_menu-v2 .menu-icon:hover, 
.l-top_menu-v2 .menu-dropdown>button:hover,.l-top_menu-v2 .global-search .search-results .nothing_found.active:before, .l-top_menu-v2 .global-search .search-results .nothing_found:focus:before, .l-top_menu-v2 .global-search .search-results .b-db_entry-variant-list_item.active:before, .l-top_menu-v2 .global-search .search-results .b-db_entry-variant-list_item:focus:before, .l-top_menu-v2 .global-search .search-results .search-mode.active:before, .l-top_menu-v2 .global-search .search-results .search-mode:focus:before{
    background-color: #282828 !important;
       
}
.c-image:hover,
.c-about:hover,
.block:hover,
.b-breadcrumbs:hover,
.c-my_list:hover,
.c-control:hover,
.video-variant:hover {
    background-color: rgba(17, 17, 17, 0.4) !important;
}
a {
    text-decoration: none!important;
}
.b-list li {
    list-style-image: none !important;
}
.tooltip-arrow,
.arrow-container,
.menu .contest {
    display: none !important;
}
.b-topic,
.line,
.b-hot_topics,.b-hot_topics-v2 {
    border: none!important;
}
a.back:before,
.toggler:after {
    color: #C7D4D4 !important;
}
.s0,
.s1,
.s2,
.s3,
.s4,
.s5,
.first {
    background: #1B1B1B !important;
}
.second {
    background: #333 !important;
}
.third {
    background: #444 !important;
}

.l-top_menu,
.to-top-fix,
.submenu>.entry,
.b-main_search ul.popup li,
.popup,
.submenu {
    border: none !important;
}
.block-list li,
.block-list-alt li {
    padding: 0 !important;
}
.image-cutter,
.c-poster img {
    border: 1px solid #333333 !important;
}
.watch-online {
    border: 1px solid #E7E7E7;
}
.p-animes-show .studio-logo{
 background:  #C7D4D4  ;
 border: 1px solid #444 !important;
 padding:1px;
}


.l-top_menu-v2 .global-search .search-results .nothing_found.active, .l-top_menu-v2 .global-search .search-results .nothing_found:focus, .l-top_menu-v2 .global-search .search-results .b-db_entry-variant-list_item.active, .l-top_menu-v2 .global-search .search-results .b-db_entry-variant-list_item:focus, .l-top_menu-v2 .global-search .search-results .search-mode.active, .l-top_menu-v2 .global-search .search-results .search-mode:focus{  
     background: -webkit-gradient(linear, right top, left top, from(rgba(40,40,40,0.2)), to(rgba(40,40,40,0))),rgba(40,40,40,0.01); 
     background: -webkit-linear-gradient(right, rgba(40,40,40,0.2) 0%, rgba(40,40,40,0) 100%),rgba(40,40,40,0.01); 
     background: linear-gradient(270deg, rgba(40,40,40,0.2) 0%, rgba(40,40,40,0) 100%),rgba(40,40,40,0.01); 
}

}
