css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("phileweb.com") {
body.home div#main {
    min-width: 960px;
    width: 95%;
}

body.shop div#wrapper div#main div#contentWrap div#content {
    min-width: 430px;
    width: auto;
}

body.home div#contentWrap,
body.interview.havesp div#wrapper div#main div#contentWrap div#content {
    min-width: 660px;
    width: calc(100% - 320px);
    margin-right: 0;
}
/*html body.topics div#wrapper div#main div#contentWrap div#content*/
div#contentWrap div#content {
    min-width: 640px;
    width: 100%;
    /*margin-right: 0px;*/
}

div#pickupBox,
div#bodyBlock {
    min-width: 640px;
    width: calc(100% - 300px);
}

div#headLine {
    min-width: 430px;
    width: calc(100% - 320px);
}

div#headLineTop {
    min-width: 430px;
    width: calc(100% - 160px);
}

#closeupBox ul > li .closeUpText {
    min-width: 125px;
    width: calc(100% - 90px);
}
#hometheater_ch ul > li .closeUpText {
    min-width: 125px;
    width: calc(100% - 90px);
}

div#mainBlock,
div#linksBlock,
h3.relayHead,
div#relayBlock,
div#reviewBacknumber {
    width: calc(100% - 320px);
    min-width: 640px;
    margin: 0 10px 0 10px;
}

div#mainBlock div.newsbody {
    margin: 15px 20px 40px;
}

div#relayBlock > li .relaytext {
    width: calc(100% - 20px);
}

li.relaytext {
    width: calc(100% - 20px);
}

div#main {
    min-width: 960px;
    width: 100%;
}
div.section {
    min-width: 640px;
    width: 100%;
}
ul#topicNewsList {
    width: calc(100% - 300px);
    min-width: 350px;
}
div#communityList {
    margin: 0 320px 0 0;
}
ul#special {
    width: calc(100% - 320px);
}
table#contents {
    width: calc(100% - 320px);
}

table#contents tbody tr td.w620.mt20 {
    width: auto;
}
div#newsBacknumber {
    width: calc(100% - 320px);
    min-width: 430px;
}
body.interview.havesp div#wrapper div#main div#contentWrap div#content div#bodyBlock {
    min-width: 660px;
    width: 100%;
}

body div#wrapper div#main div#contentWrap div#content div.section div.contents-toggle-area div.audio-carousel{
    width: calc(100% - 320px);
}
}
