css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("radiko.jp") {
@media (max-width: 999px){
    body {min-width: 0;}
    .header__logo,.header__banner,.header__menu .item--login,.header__menu .item--premium,
    #header-nav .header__nav li:nth-child(n+4),
    .top-main-slider,
    .header--not-login .header__station-list .btn,
    #topic-list,
    .timeshift-detail__sub, .live-detail__sub,
    #others-program,
    .program-table__hour,
    .program-table__pager,
    #footer{display: none;}
    
    .header__inner,
    .header--not-login .header__station-list ul,
    .heading-area__inner,
    .content .content__inner,
    .sub-menu__inner,
    .channel__list,
    .news__main,
    .timeshift-detail__content,
    .live-detail__content,
    .timeshift-detail__main,
    .timeshift-detail__ad .img,
    .live-detail__ad .img,
    .timeshift-detail__ad .img img,
    .live-detail__ad .img img,
    .top-main-slider__nav .inner,
    .top-slider__inner,
    .tutorial-app .img,
    .tools-tab__nav ul,
    .about-intro__col.col-02,
    .areafree-hero__thum img,
    .heading-area__inner,
    .program-table__outer,
    .top-info__col,
    .timeshift-detail__timeline .timeline-col,
    .live-detail__timeline .timeline-col,
    .premium-area__text{width:auto;}
    
    .live-detail__main,
    .player-area__inner,
    .top-sns__col{width: 100% !important;}
    
    .top-main-slider{min-width: 0;}
    
    .header__nav-container,
    .program-table__body,
    .program-table__items .item .contents{height: auto;}
    .program-table__outer,
    .program-table__items,
    .program-table__items .item,
    .tune-list .tune-list__item{height: auto !important;min-height: 0 !important;}
    
    .container{overflow: hidden;}
    .container *,
    #colorbox *{font-size: 11px !important;}
    .header__nav .item__link,
    .page-timeshift .header__nav .item__link{background: none;}
    .header--not-login .header__nav .item__live,
    .header--not-login .header__nav .item__timeshift,
    .header--not-login .header__nav .item__areafree,
    .page-timeshift .header--not-login .header__nav .item__live,
    .page-timeshift .header--not-login .header__nav .item__timeshift,
    .page-timeshift .header--not-login .header__nav .item__areafree{width: 40px;}
    .header__nav .item__live span,
    .page-timeshift .header__nav .item__live span,
    .header__nav .item__timeshift span,
    .page-timeshift .header__nav .item__timeshift span,
    .header__nav .item__areafree span,
    .page-timeshift .header__nav .item__areafree span{width: 26px;}
    
    .header__menu .item--search input{background-color: #fff;width:160px;}
    .header__utility .header__inner{height: 0;}
    .header__station-list li a{font-size: 9px !important;}
    #station-list{padding: 0 5px;}
    .header__utility{height: 0;padding: 0;}
    .header__utility .item--search{position: absolute;z-index: 1;top:10px;right:5px;}
    .header__nav-outer.fixed{position:relative;}
    .heading-area__inner{padding: 0;}
    .timeshift-detail, .live-detail{padding-top:5px;}
    .timeshift-detail__img, .live-detail__img{margin-right: 5px;}
    .content{box-shadow:none;padding-left:10px;padding-right: 10px;padding-top:20px;}
    .top-search__form .item{margin-bottom: 10px;}
    .top-slider {padding: 10px 0;}
    .img-list__item{width: 110px;}
    .img-list__img{width: 150px;height: 94px;line-height: 94px;}
    .top-slider__list .item img,
    .img-list__item img{max-width: 100%;}
    .top-sns__col,
    .top-info__col{margin-bottom: 1em;}
    .top-sns__body{padding:10px;}
    .fb_iframe_widget span{width: 290px !important;overflow:scroll;}
    
    .timeshift-detail__body, .live-detail__body{margin-top: 10px;}
    .live-detail__img img{width: 100px;}
    .timeshift-detail__text, .live-detail__text{width: 200px;}
    .live-detail__cast{height: auto !important;overflow: visible !important;}
    .live-detail__cast div{display:inline;float:none !important;padding:0 !important;max-width: 200px;white-space: normal !important;}
    .live-detail__cast .live-detail__cast-title{display: none !important;}
    .timeshift-detail__share,
    .live-detail__share,
    .timeshift-detail__noti,
    .live-detail__noti,
    .btn--share,
    .btn--tooltip{width: 95px;height: 30px;box-sizing: border-box;}
    .btn--default,
    .btn--x-large{padding: 5px 0;}
    .timeshift-detail__text .btn--default i,
    .timeshift-detail__text .btn--x-large i,
    .live-detail__text .btn--default i,
    .live-detail__text .btn--x-large i{display: none;}
    .timeout{height: 40px;background-size:100% auto;}
    .timeshift-detail__share, .live-detail__share,
    .timeshift-detail__noti, .live-detail__noti{margin-top: 5px;}
    .timeshift-detail__main, .live-detail__main{border-right:none;padding-right: 0;min-height: 0;}
    .live-detail__failed{padding: 5px 0;}
    .timeshift-detail__description,
    .live-detail__description{padding-top: 10px;}
    .timeshift-detail__sites,
    .live-detail__sites{padding-top: 10px;margin-top: 10px;}
    .tune-list {margin-right: 0;margin-top: 0;}
    .tune-list .tune-list__item{box-shadow:none;width:auto;float:none;margin-right:10px;margin-top:10px;padding:10px;}
    .tune-list .tune-list__img {top:10px;left:10px;}
    .tune-list .tune-list__img img{width: 60px;height: 60px;}
    .tune-list .tune-list__text {min-height: 55px;padding-left: 80px;margin-bottom: 5px;}
    .tune-list .tune-list__link{margin-top: 0;padding-left: 80px;}
    .tune-list .tune-list__link .item{height: 0;overflow: hidden;transition-duration:.5s;}
    .tune-list .tune-list__item:hover .tune-list__link{height: auto;}
    .tune-list .tune-list__item:hover .tune-list__link .item{height: 15px;}
    .timeshift-detail__timeline .stream-item-wrapper .time,
    .live-detail__timeline .stream-item-wrapper .time{position:static;}
    .btn--recochoku,.btn--itunes,.btn--amazon{height: 15px;background-size: 247.5px 239px;}
    .btn--recochoku{width: 46px;background-position: -70px -88px;}
    .btn--itunes{width: 40.5px;background-position: -52px -162.5px;;}
    .btn--amazon{width: 43px;background-position: -70px -103px;}
    .timeshift-page-nav{margin: 0 0 1em;}
    .timeshift-page-nav__btn,
    .timeshift-page-nav__list{float: none;margin:0 0 1em;}
    .timetable-weekly__onair .img{width:50%;}
    .timetable-weekly__onair .img img{max-width: 100%;}
    #program-table *{font-size: 9px !important;}
    .program-table__body--col7 .program-table__items .item-outer {width: 86px;}
    .program-table__body--col7 .program-table__date {width: 78px;}
    .program-table__body--col8 .program-table__items .item-outer {width: 86px;}
    .program-table__body--col8 .program-table__channel,
    .program-table__body--col8 .program-table__channel .name,
    .program-table__body--col8 .program-table__date{width: 78px;}
    .program-table__date-outer{margin-left: 0;}
    .program-table__date-wrapper{border: none;}
    .program-table-header__list-outer{overflow: scroll;}
    .program-table-header__list{display: table;width:auto;height: auto;}
    .program-table-header__list .item{float:none;display: table-cell;padding-right: 10px;}
    .program-table-header--col8 .program-table-header__list .item__link{width: auto;padding: 5px;height: 22px;
  line-height: 22px;box-shadow:none;}
    .program-table-header--col7 .program-table-header__list .item__link,
    .program-table-header--logo .program-table-header__list .item__selected{width: auto;padding: 5px;height: 40px;
  line-height: 40px;box-shadow:none;}
    /*.program-table__header .program-table__pager{display:block;}*/
    .program-table__pager .item--next{right: 0px;}
    .program-table__pager .item--prev{left: 0px;}
    .program-table__items,
    .program-table__channel-wrapper,
    .program-table__date-wrapper{margin-left: 0;}
    .program-table__date-outer{padding-left: 0;}
    .program-table__body{overflow-x: scroll;box-shadow:none;margin-top:10px;}
    .program-table__date-wrapper,
    .program-table__channel-wrapper,
    .program-table__outer{overflow: visible;}
    .program-table .js-scroll{left:0 !important;display: inline-flex;width: auto;}
    .program-table__items .item a,
    .program-table__body--col8 .program-table__items .item a{padding: 8px;}
    .program-table__items .item .scrollContents .cast{line-height: 0px; color:transparent; transition-duration: .5s;}
    .program-table__items .item .scrollContents:hover .cast{line-height: 12px; color:#000;}
    .timelimit{position: absolute;top:0;right:0;}
    
    .player-area__inner{padding: 3px 0;height: auto;}
    .player-area__btn{margin-right: 10px;}
    .player__play{position: absolute;top: 3px;left: 4px;}
    #player-area.page-timeshift .player__play{left: 5px;}
    .player__play a{width: auto;}
    .player__play .on{display: none;}
    .player__stop{margin-right: 7px;margin-left: 5px;}
    .player-area__volume .icon--volume{margin-right: 5px;}
    #player-area.page-timeshift .icon--volume{width: 10.5px;
height: 10px;background-size: 253.5px 248.5px;background-position: -142px -142px;}
    #player-area.page-timeshift .player-area__volume{padding-top: 0px;margin:0 0 0px;}
    .player-area__seek{float:left;padding-top:1px;margin-bottom: -2px;line-height: 15px;position: relative;}
    .player-area__seek .start{font-size: 8px !important;position: absolute;top: 9px;}
    .player-area__seek .seek-bar{margin-right: -40px;}
    .player-area__seek .end{font-size: 8px !important;position: relative;top: 7px;}
    #player-area.page-timeshift .icon--seek-btn{height:8px;}
    #player-area.page-timeshift .balloon{top:-27px;padding: 1px 11px;width: auto;left: 72%;}
    #player-area.page-timeshift .balloon span{font-size: 8px !important;}
    .player-area__detail{width: 73px;top: 3px;}
    .player-area__detail .img{position: relative;}
    .program-ticker .marquee,
    .program-ticker .name{display: none;}
    
    .premium-area__btn{width: auto;margin-top: 10px;float: left;}
    .premium-area__btn a{padding: 10px 5px;}
    .content__areafree-top .tab-list .item{margin: 0 0 10px 12px;}
    
    #colorbox{width: 100% !important;}
    #cboxWrapper,
    #cboxContent,
    #cboxLoadedContent,
    #cboxTopCenter{
      width:auto !important;
      height:auto !important;
    }
    #cboxBottomCenter{width: 300px !important;}
    #cboxWrapper{position: static;margin:0 10px;}
    #cboxLoadedContent {
      padding: 20px 15px;
    }
    .colorbox__text,
    .colorbox__link,
    .colorbox__btn{margin-top: 5px;}
    .page-timeshift #cboxClose{
      width: 26px;
      height: 26px;
      right: -5px;
      top: -5px;
      background-size: 26px 26px;
    }
  }
}
