css:
@-moz-document domain("hyunsdojo.com") {
* {
	box-shadow: none !important;
	text-shadow: none !important;
	border-radius: 0px !important;
	color: inherit !important;
	border-color: /*[[text]]*/ !important;
}
*:not(#user_background):not(#container):not(#header) {
	background: none !important;
}
#profile_top_footer,
button,
a.button {
	background-color: /*[[bg]]*/ !important;
}

#footer {
	display: none;
}
#container { background: /*[[bg]]*/ !important; }
body,
.view_forum_pag,
.right_cat h2,
.forum_cat h2 {
	background-color: /*[[bg]]*/ !important;
	color: /*[[text]]*/ !important;
	font-family: sans-serif !important;
}

a,
input[type=submit],
button,
.u_com_blue,
.clickable:hover,
a.topictitle,
.forum_cat a {
	color:/*[[highlight]]*/ !important;
}

#header {
	border-bottom: 1px solid !important;
	background: /*[[bg]]*/ !important;
	color: /*[[text]]*/ !important;
	height: 32px;
}

@media (max-width: 960px) {
	.m-nav {
		display: inline-block;
		position: relative;
		top: 0;
		left: 0px;
		width: auto;
	}
	.m-nav a {
		float: left;
		width: auto;
		background: none;
		padding: 0px 7px;
		margin-top: -2px;
		border-bottom: none;
	}
	.m-open {
		display: none;
	}
}

.menu_btn,
.clickable {
	height: 32px !important;
}

.text,
.clickable {
	margin-top: 0px;
	line-height: 32px;
	vertical-align: middle;
	color: /*[[text]]*/ !important;
}

.clickable div {
	padding: 0 !important;
}

#user_box div {
	margin-top: 0px;
}

#user_box .user_icon {
	margin-top: 2.5px !important;
}

#notifications_box {
	border: 1px solid black;
}

#lights,
.logo,
.search-wrap,
.forgot_pass_btn {
	display: none;
}

.status_update:after,
.status_update:before,
.activity_right:after,
.activity_right:before,
.u_com_btn_sel:after,
.u_com_btn_sel:before,
.post_right:before,
.post_right:after {
	visibility: hidden;
}

#content {
	padding: 0 10px;
}

.user_content_avatar {
	margin-top: 2px;
}

#main_activity {
	margin-left: 0px !important;
}

.user_content_box {
	margin: 5px 0px;
}

.status_update,
.activity_right {
	float: left;
	border: 1px solid black;
}

#front_menu {
	border: 1px solid black;
	padding: 0px;
	margin-right: 9px;
}

.front_menu_a:not(.front_submit),
#ptl_menu a:not(.ptl_submit) {
	border: none;
}

.front_menu_a.label,
.ptl_submit,
.front_submit {
	border: 1px solid !important;
	border-width: 1px 0px !important;
}

.u_comments_box {
	padding: 0px;
	margin: 0 10px !important;
	padding-top: 10px;
}

.u_comments_top {
	border: 1px solid ;
	border-width: 1px 0px;
}

.comment_bod+div {
	height: 5px !important;
	border-top: 1px dotted !important;
}

#profile_top {
	width: 100%;
	border: 1px solid ;
}

#profile_top_footer {
	border-top: 1px dashed ;
	width: 100%;
}

.status_update,
.activity_right,
#profile_top,
.forum_wrap,
.right_wrap,
#front_menu,
#footer,
.tablebg,
#ptl_menu,
.post_right,
a.notify_box,
#notifications_box,
#messages_drop,
.m_msg,
#user_box_drop,
#ptl_main,
#ptl_menu a:not(.ptl_submit),
.list_msg_inner,
.new_post_wrap,
#swf_desc_box,
.more_info,
.user_content_avatar,
.post_avatar,
.avatar {
	background-color: /*[[bg]]*/ !important;
}

.right_cat, .forum_cat { border-bottom: 1px solid; }
  
/*[[borders]]*/

/*[[shadows]]*/
}
