@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.leo.org/)
{
	/** Werbung entfernen **/
	td[width="15%"],
	table.maxwidth td[width="55%"]
	{
		display: none !important;
	}
}
@-moz-document domain("leo.org")
{
	body
	{
		font-family: Calibri, Helvetica, Arial, sans-serif !important;
	}
	
	/** Suchergebnisse anpassen **/
	#results, #results tr, #results td, #results th
	{
		border: 0px !important;
		font-family: "Lucida Sans Unicode","Lucida Grande", "Calibri", Sans-Serif !important;
		font-size: 12px !important;
		vertical-align: middle !important;
	}
	
	#results th
	{
		color: #003399 !important;
		font-size: 14px !important;
		font-weight: bold !important;
	}
	
	#results td[colspan="5"]
	{
		color: #003399 !important;
		font-size: 14px !important;
		line-height: 35px !important;
	}
	
	#results td i
	{
		color: green !important;
	}
	#results td a
	{
		color: #666699 !important;
	}
	
	#results tr
	{
		line-height: 25px !important;
	}
	
	/** Such-Formular aufrÃ¤umen **/
	#dict table table tr.language td[colspan="2"],
	#dict table table tr.language td[colspan="5"],
	#dict table table tr.language td[colspan="3"],
	#dict table table tr:not(.language) td:not([valign="middle"])
	{
		display: none !important;
	}
	
	#dict table table tr.language
	{
		border: 0px !important;
	}
	
	#dict table table tr.language td input[type="reset"]
	{
		left: 230px !important;
		position: relative !important;
		top: -32px !important;
	}
	
	#dict table table tr.language td
	{
		height: 1px !important;
	}
	
	.searchform,#searchrow,#dict
	{
		height: 40px !important;
		overflow: hidden;
	}
	
	/** Werbung entfernen **/
	#main .sidebar,
	.standard_banner
	{
		display: none !important;
	}
}
