css:
@-moz-document domain("www.kiees.com") {
#header #member,#menu-item-27,#sidebar,#homelists .sectitle,#afterstory,#con_tit #pinglun,#footer{display:none !important;}
#header{width:1238px;padding: 0;}
#header-container{padding:0 0 0 10px;}
#mainnav{width:1238px;}
#shopbox{float:right;margin:0 10px 0 0;}
#wrapper{width:1218px;padding: 0 10px 10px 10px;border: 1px solid #ddd;}
#content,#home-left{width:1218px;float:none;}
#con_box{float:none;}
.postbox{width:50%;float:left;box-sizing: border-box;}
.postbox:nth-child(odd){padding: 10px 0 10px 10px;}
.postbox:nth-child(even){padding: 10px 0;border-right: 1px dashed #ddd;}
.postmetadata{left:182px;bottom:12px;background:#fff;}
.postbox:nth-child(even) .postmetadata{left:172px;}
.postbox .a_img{width:160px;height:160px;}
.postbox .a_img img{width:160px;height:160px;}
.postbox .entry h2{float: left;width: calc(100% - 172px);}
.postbox .entry .detail{float: left;width: calc(100% - 172px);height:auto;}
.pinglun{width:428px;}
.pinglun_left{width:auto;}
#content-single{float:none;border:none;width: 100%;padding: 0;}
#con_tit #shop_url{width:100px;float:left;margin:0 0 10px 0;}
.entry-content{width:100%;}
.single_gobuy{margin:0 0 10px 0;padding: 0 0 0 0;text-align:left;}
#wrapper>div:last-child{display:none;}
}
