css:
@-moz-document domain("numerama.com") {
body,
section.universe-preview footer.tag-universe,
#universe .tag-universe,
#site-footer,
#ctBoc .wrap,
#tag .small-feature,
#tag .small-feature .article-title,
.category .wrap h1,
section.universe-preview,
#home-universe-guide-dachat {
    background-color: #444;
    color: #eee;
}

#page-content,
.top-bloc .container_native_banner .title,
.featured-news-home,
section.universe-preview article{
    background-color: #252525!important;
}

.featured-news-home .featured-news_feat1 h4,
.featured-news-home .featured-news_feat2 h4,
.newsletter_form .input-filed {
    background-color: #252525!important;
}

.featured-news-home .featured-news_feat2 h4 {
    color: #eee;
}

.related-tags .tag,
section.universe-preview footer.tag-universe a,
#universe .tag-universe a,
#search article.post-grid .tags-list a,
section.recent-posts .post-grid .tags-list a,
section.universe-preview article .tags-list a,
.post .container_native_sidebar article .tags-list a,
.best-post-container .container_native_sidebar article .tags-list a,
#simple-page-contact .container_native_sidebar article .tags-list a,
#simple-page .container_native_sidebar article .tags-list a,
.report-footer .container_native_sidebar article .tags-list a,
.post .related-article article .tags-list a,
.best-post-container .related-article article .tags-list a,
#simple-page-contact .related-article article .tags-list a,
#simple-page .related-article article .tags-list a,
.report-footer .related-article article .tags-list a,
.community-featured article .tags-list a,
section.recent-posts .post-list footer .tags-list a,
.startup-article-container .startup-top-area .startup-title .content-container .tags-list a,
#tag .small-feature .tags-list a,
.talk .content .enter-talk,
.post footer .tags-list a,
.best-post-container footer .tags-list a,
#simple-page-contact footer .tags-list a,
#simple-page footer .tags-list a,
.report-footer footer .tags-list a,
.home-video__video,
.home-video__title{
    background-color: #444;
    color: #eee;
}

.brief-list li a,
.archives-select li a {
    color: #aaa;
}

.brief-list a:hover,
.archives-select a:hover,
.home .brief-list a:hover, 
.archive .brief-list a:hover, 
.single-startup .brief-list a:hover, 
.archives-select a:hover {
    background-color: #000;
}

.post,
.best-post-container,
#simple-page-contact,
#simple-page,
.progress-bar,
.bloc-comments_content {
    background-color: #252525!important;
}

.top-bloc,
.comments-bloc,
.content-side-bloc {
    display: none;
}

.post .post-content {
    width: 100%;
    border-right: none;
}

.post .embed {
    height: 0;
    overflow: hidden;
    position: relative;
    padding-bottom: 56.25%;
}

.post .embed iframe {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}
}
