css:
@-moz-document domain("tabun.everypony.ru") {
a {
    transition: color .2s;
}

#c-header {
    background: #1d2a4f;
}

#header * {
    box-sizing: border-box;
    font-family: Roboto, sans-serif;
    font-weight: 400;
}

#header {
    background: #1d2a4f;
    padding: 0;
    display: flex;
    align-items: center;
    min-height: 40px;
}

#nav {
    min-height: 0px;
    padding: 0;
    height: 40px;
}

.nav.nav-menu {
    height: 40px;
    border-radius: 0px;
}

.nav.nav-menu li {
    padding: 0;
}

.nav.nav-menu li a {
    display: inline-flex;
    align-items: center;
    color: white;
    font-size: 17px;
    height: 40px;
    font-weight: 300 !important;
    transition: background .2s;
    padding: 10px;
}

.nav.nav-menu li a:hover {
    background-color: #374572;
}

.nav.nav-menu li.active a {
    color: white;
    background-color: #374572;
}

#header a {
    font-weight: 300;
    text-decoration: none;
}

#container {
    padding-top: 64px;
}

#nav-main {
    box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
}

#dropdown-user {
    display: flex;
    position: fixed;
    right: 0;
    top: 0;
    margin-top: 0 !important;
}

#dropdown-user a {
    padding: 0 10px;
}

#dropdown-user a {
    font-size: 20px;
}

#header .dropdown-user {
    padding-right: 15px;
}

#dropdown-user a:first-child {
    align-items: center;
    display: inline-flex;
    order: 2;
}

#dropdown-user a:first-child .avatar {
    background-color: white;
    width: 50px;
    height: 50px;
}

#dropdown-user .username {
    order: 1;
}

#header .dropdown-user .username {
    color: white;
    display: inline-flex;
    align-items: center;
    font-size: 20px;
    text-decoration: none;
    font-weight: 300;
    transition: background-color .2s;
}

#dropdown-user .username:hover {
    color: white !important;
    background-color: #374572;
}

#header .dropdown-user .avatar {
    margin: 0;
    float: none;
}

#nav-main, #dropdown-user {
    padding: 0 15px;
    background-color: #1e274e;
    height: 64px;
    z-index: 1;
}

#sidebar {
    margin-right: 1.5%;
    z-index: 0 !important;
}

.nav.nav-main li {
    height: 100%;
    margin-right: 0;
}

.nav.nav-main li a {
    font-family: Roboto,sans-serif;
    display: inline-flex;
    align-items: center;
    height: 100%;
    padding: 0 10px;
    color: white;
    text-decoration: none;
	transition: background-color .2s;
    font-weight: 300;
}

.nav.nav-main li a:hover, .nav.nav-main li.active a {
    color: white;
    background-color: #374572;
}

#dropdown-user-menu li{
    height: 100%;
}

#dropdown-user-menu li a {
    font-weight: 300;
    color: white;
    display: inline-flex;
    align-items: center;
    height: 64px !important;
    transition: background-color .2s;
}

#dropdown-user-menu li a:hover {
    background-color: #374572;
}

.dropdown-user-menu {
    padding: 0 10px;
    display: flex;
    flex-direction: column;
}

.dropdown-user-menu li {
    display: flex;
    align-items: flex-start;
    justify-content: center;
    height: 50%;
    color: #27ae60;
}

.dropdown-user-menu li:first-child {
    align-items: flex-end;
}

#dropdown-user-menu {
    padding: 0;
    flex-direction: row;
}

.dropdown-user-menu li {
    margin-left: 0px;
}

.dropdown-user-menu li.item-stat .strength {
     margin-right: 0px;
    color: #27ae60;
}

.item-messages {
	flex-flow: row-reverse;
}

#header .new-messages {
    position: relative;
    color: #e74c3c;
    padding: 5px;
    border-radius: 0;
    background-color: inherit;
    font-weight: 500;
    font-size: 20px;
    text-decoration: none;
}





body {
    font-size: 10pt;
    background: #eef;
    font-family: Roboto, sans-serif;
}

h1, h2, h3, h4, h5, h6 {
    font-size: 13px;
    line-height: 1.6em;
    font-weight: 400;
    margin: 0;
    font-family: Roboto, sans-serif;
}

#container {
    background: none;
}

.nav.nav-pills {
    padding: 10px;
    background-color: white;
    border-radius: 3px;
    border: 1px solid rgba(192,202,213,0.55);
}

.topic {
    margin-bottom: 30px;
    background-color: white;
    padding: 10px;
    border-radius: 3px;
    border: 1px solid rgba(192,202,213,0.55);
}

.topic-header .topic-title {
    margin-bottom: 5px;
}

#content-wrapper {
    margin-left: 1.5%;
}

.running-ponies {
    display: none;
}

#sidebar .block {
    background: white;
    border-radius: 3px;
    border: 1px solid rgba(192,202,213,0.55);
    margin-bottom: 15px !important;
}

.block .block-header {
    padding-bottom: 0;
}

.block .nav.nav-pills {
    margin-bottom: 0;
    padding: 0;
    border: 0;
    padding-top: 15px;
}

.block-type-pseudomenu ul {
    display: flex;
    justify-content: center;
    width: 100%;
}

.block-type-pseudomenu ul li {
    padding: 0 10px;
}

.text {
    font-size: 14px;
}

.comment {
    border: 1px solid rgba(192,202,213,0.55);
    margin-bottom: 10px;
}

.comment-content {
    background-color: white;
    margin-bottom: 5px;
    border: none;
    border-radius: 0;
}

.comment-info {
    padding: 0 70px 0px 0;
}

.talk-search, .table {
    background: white;
    border: 1px solid rgba(192,202,213,0.55);
}

#form_talks_list {
    background:white;
    border: 1px solid rgba(192,202,213,0.55);
}


.topic-footer .topic-info-author .avatar {
    position: absolute;
    margin: -4px 0 0;
    left: 10px;
}

.stream-list li.stream-item {
    background:white;
    border: 1px solid rgba(192,202,213,0.55);
    padding: 10px 10px 10px 50px;
    margin-bottom: 10px;
}

.stream-list li.stream-item .avatar {
    top: 5px;
    left: 5px;
}

#content[itemtype="http://data-vocabulary.org/Person"] {
    background:white;
    border: 1px solid rgba(192,202,213,0.55);
    padding: 10px 10px 10px 50px;
}

.table.table-users .cell-name img {
    margin-left: 5px;
}

.button {
    display: inline-block;
    padding: 5px 10px;
    margin: 0;
    text-align: center;
    vertical-align: middle;
    text-decoration: none;
    line-height: 20px;
    text-shadow: none;
    border: 1px solid #ddd;
    border-radius: 0px;
    color: #333;
    background: #fafafa;
    box-shadow: none;
    cursor: pointer;
}

