@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forecast.weather.gov") {

body {
    background-color: #222 ;
       color: #999; 
  }
  
.body {
    background-color: #222 ;
       color: #999; 
  }
  
body.nonav670 {
    background: none ;
    background-color: #222 ;
    color: #999;
  }
  
/* Image Mask-Unmask */
body img,a[href] img, a[href] button, input[type="image"],*[onclick]:empty, body a:empty{opacity:.5!important}
body img:hover,a[href]:hover img, a[href]:hover button, *[onclick]:empty:hover, body a:empty:hover{opacity:1!important}
body .header670,a[href] .header670, a[href] button, input[type="image"],*[onclick]:empty, body .header670:empty{opacity:.5!important}
body .header670:hover,a[href]:hover .header670, a[href]:hover button, *[onclick]:empty:hover, body .header670:empty:hover{opacity:1!important}
  
/* Forecast Area Map Mask-Unmask */
body canvas,a[href] canvas, a[href] button, input[type="image"],*[onclick]:empty, body canvas:empty:hover{opacity:.5!important}
body canvas:hover,a[href]:hover canvas, a[href]:hover button, *[onclick]:empty:hover, body canvas:empty:hover{opacity:1!important}
  
container {
    background: #222;
   }

.applicationnotificationContainerBanner {
    background: #333 ;
    color: #999 ;
    } 

/* Search Box */
.form-control {
    background-color: #354046 ;
  }

.panel {
    background-color: #222 ;
    color: #999 ;
    border-color: #444;

  }

.panel-title {
    color: #15a3e0 ;        
    }

.panel-heading {
    background: #222 !important;
    color: #999 !important;
  }

/* 20161203 */    
.panel-heading h3 {
    color: #dd3636 !important;
    }

/* 20161203 */    
div.panel-body ul li a.anchor-hazards {
    color: #ff5858 !important;
    }
    
.contentArea {
    background-color: #222 ;
    color: #222 ;
  }

/* 20161203 */
#seven-day-forecast .forecast-tombstone .temp-high {
    color: #dd3636
    }
    
#topnews.important .body {
    color: #999 !important ; 
  }

.panel-body {
    background: #222 !important;
    color: #999 ;
  }

.period-name {
    color: #666 !important ;
  }

.row {
    background: #222 !important;
  }

.row-even {
    background: #333 !important;
  }

floatingDivs {
    background: #222 ;
  }

footer {
    background: #222 !important;
  }

#footer670 a {
    color: #115E90 !important;
  }

/* 20200615 --amj */
#point-forecast-map-label {
  /* color: black; */
  color: #999;
}

.contentArea {
  /* color: #222; */
  color: #999;
}
button,
input,
optgroup,
select,
textarea {
 margin:0;
 font:inherit;
 color: black;
}
/* 20200615 */
}
