css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
/*********************************************************
 * Global
 *********************************************************/

.mainmenu .menugroup:nth-child(1) .button {
	width: 230px;
	padding: 6px 7px 6px 7px;
	font-size: 14pt;
	color: #CAFFFF;
	border: solid 2px #4FD7D9;
	background: #054D4E;
	box-shadow:none;
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: 0 -1px 0 hsl(210,40%,10%);
	border-radius: 5px;
}
.menugroup .button.disabled,
.menugroup .button.disabled:hover,
.menugroup .button.disabled:active {
	cursor: default;
	background: #054D4E;
	border: 1px solid #4FD7D9;
	color: #CAFFFF;
	text-shadow: none;
	box-shadow: 0 1px 2px rgba(0,0,0,.1);
}
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button, body .button.mainmenu4, body .button.mainmenu5 {
	box-sizing: border-box;
	font-size: 12pt;
	font-family: Verdana, Helvetica, Arial, sans-serif;

	display: inline-block;
	padding: 5px 7px 3px 7px;

	size: 200px;
	box-shadow:none;
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: 0 -1px 0 hsl(210,40%,10%);
	border-radius: 5px;

	/* default colors */
color: #CAFFFF;
	border: solid 2px #4FD7D9;
	background: #054D4E;
	box-shadow:none;
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: 0 -1px 0 hsl(210,40%,10%);
	border-radius: 5px;
}

  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover, body .button.mainmenu4:hover, body .button.mainmenu5:hover {
	background: #087274 ;
	border-color: #2CF1F5;
}
 .mainmenu .menugroup:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:active,.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:active, body .button.mainmenu4:active, body .button.mainmenu5:active {
	box-shadow: none;
	background: #067779 ;
	border-color: #2CF1F5;
}
body .button.mainmenu1{
    background: #067779;
    	border-color: #2CF1F5;
}
body .button.mainmenu1:hover{
	background: #087274 ;
	border-color: #2CF1F5;
}
html, body {
	margin: 0;
	padding: 0;
}
body {
	color: white;
	font-size: 16px;
	font-family: Helvetica, Arial, sans-serif;

	/* license: CC-BY source: http://commons.wikimedia.org/wiki/File:%22La_piedra%22_(2809466426).jpg */
	background: #344b6c url(http://i.imgur.com/Q9tqlsL.jpg) /*no-repeat left center fixed*/ !important;
  -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-size: cover;
}

.pad {
	margin: 0;
	padding: 5px 15px;
}
.label {
	font-size: 12px;
	font-weight: normal;
	display: block;
}
.optlabel {
	font-size: 12px;
	display: block;
}
.label strong {
	font-size: 12px;
	display: block;
}
.label .textbox {
	display: block;
}
.textbox {
	border: 1px solid lightgrey;
	border-radius: 3px;
	padding: 2px 3px;
	font-family: Helvetica, Arial, sans-serif;
	font-size: 12px;

	box-shadow: none;
	background: white;
	color: black;
}
.textbox:hover {
	border-color: lightgrey;
	box-shadow: none;
	background: white;
}
.textbox:focus {
	outline: 0 none;
	border: 1px solid lightgrey;
	box-shadow: none;
	background: white;
}
a.subtle {
	text-decoration: none;
	color: white;
}
a.subtle:hover {
	text-decoration: underline;
	color: #6688CC;
}
.buttonbar {
	margin-top: 1em;
	text-align: center;
}

/*********************************************************
 * Header
 *********************************************************/

.header {
	position: relative;
	height: 50px;
	background: /* url(http://i.imgur.com/W5irw2t.png) repeat-x left top scroll */ rgba(0, 0, 0, 0.5);
}
.header h1 {
	margin: 0;
	padding: 0;
}
.header .logo {
    display: none;
	float: left;
	margin: 2px 0 0 2px;
}

.userbar {
	position: absolute;
	top: 14px;
	right: 14px;
	font-weight: bold;
}
.userbar .username {
	color: white;
  	font-size: 20px;
	text-shadow: .5px .5px .5px black;
}
.username {
	cursor: pointer;
}
.userbar button {
	cursor: pointer;
}
.userbar button.icon {
    border-radius: 1px;
	height: 21px;
	width: 23px;
	font-size: 14px;
	text-align: center;
	padding: 0;
}
.userbar button.icon i {
	margin: 0 -5px;
}

.tabbar {
	position: absolute;
	bottom: 1px;
	left: 100px;
	right: 0;

	display: block;
	list-style: none;
	margin: 0;
	height: 37px;
	text-align: left;

	color: #CAFFFF;
	text-shadow: none;
	font-size: 16px;
}
.maintabbarbottom {
	content: "";
	display: block;
	position: absolute;
	left: 0;
	right: 0;
	bottom: -6px;
	height: 6px;
	background: #002728;
	border: solid #013436 1px;
	border-left: 0;
  	border-right: 0;
	margin: -1px 0 0 0;
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}
.tabbar.maintabbar {
	margin-left: 50px;
	margin-right: 185px;
	overflow: hidden;
}
.maintabbar .inner {
	width: 400%;
}
.maintabbar .overflow {
	position: absolute;
	bottom: 0;
	right: 0;
	z-index: 12;
}
.maintabbar .overflow button {
	height: 37px;
	margin: 0;
}

.tabbar li,
.tabbar ul {
	display: block;
	float: left;

	list-style: none;
	margin: 0;
	padding: 0;
}
.tabbar a.button {
	outline: none;
	display: inline-block;
	height: 40px;
	min-width: 60px;
	cursor: pointer;
	text-shadow: 0 1px 0 rgba(255,255,255,.4);
	border-radius: 0px;
  	transform:skewX(-30deg);
	/* default colors */
	color: #CAFFFF;
	text-shadow: 0 0px 0 #707070;
	border: solid 2px #CAFFFF;
  border-left-width: 2px;
  border-right-width: 2px;
	background: #054D4E;

	position: relative;
	padding: 3px 2px 3px 2px;
	margin: 0 -1px 0 0;
	top: 1px;
	border-radius: 0;
	box-shadow: none;
	font-size: 11px;
}
.tabbar a.button i {
	display: block;
	text-align: center;
	margin: 1px auto 0 auto;
	font-style: normal;
	font-size: 14px;
	height: 14px;
  	transform:skewX(30deg);
}
.tabbar a.button span {
	display: block;
	overflow: hidden;
  	transform:skewX(30deg);
}
.tabbar a.button i.text {
	font-size: 11px;
	margin-right: 8px;
	padding-left: 0;
	overflow: hidden;
	font-weight: bold;
  	transform:skewX(30deg);
}
.tabbar a.button i.text + span {
	margin-right: -6px;
}
.tabbar a.button:hover {
	background: #087274;
	border-color: #CAFFFF;

	z-index: 10;
}
.tabbar a.button:active {
	background: #014041;
	border-color: #CAFFFF;
  
}
.tabbar a.button.notifying {
	background: #1BB1B3;
	border-color: #CAFFFF;
}
.tabbar a.button.notifying:hover {
	background: #0B8C8E;
	border-color: white;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover {
	color: #CAFFFF;
	background: #014041;
	box-shadow: none;
	border-color: #CAFFFF;
	border-bottom: 0;
	cursor: default;
	padding-top: 3px;
	padding-bottom: 5px;
	top: 0px;
}

.tabbar li:first-child a.button {
	border-top-left-radius: 5px;
	margin-left: 6px;
}
.tabbar li:last-child a.button {
	border-top-right-radius: 5px;
}
.tabbar a.closable {
	padding-right: 8px;
}
.tabbar .closebutton {
  	color:#707070;
}
.tabbar a.cur + .closebutton {
}
.closebutton,
.minimizebutton {
	text-decoration: none;
	font-size: 14px;
	color: #999999;
	text-shadow: none;
	border: 0;
	padding: 0;
	margin: 0;
	background: transparent;
	cursor: pointer;
  	left:4px;
}
.closebutton:hover,
.minimizebutton:hover {
	color: #BB2222;
    border:none !important;
    background:none !important;
}
.closebutton:active,
.minimizebutton:active {
	color: #661111;
    border:none !important;
    background:none !important;
}

/*********************************************************
 * Rooms
 *********************************************************/

.ps-room {
	position: absolute;
	display: block;
	top: 56px;
	left: 0;
	right: 0;
	bottom: 0;
    border-right: #98E2E2;
	margin-left: 0px;
}
.scrollable {
	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
}
.ps-room.ps-room-light {
	background: #000B0B;
    color: #CAFFFF;
    border-color: #013436;
    opacity: 0.9;
}
.ps-room.ps-room-opaque {
	background: #013536;
	color: #CAFFFF;
}

.ps-overlay {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background: rgba(0, 0, 0, 0);
	background: rgba(0, 0, 0, 0.5);

	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
	z-index: 20;
}

.ps-popup {
	position: absolute;
	top: auto; right: auto; left: auto; bottom: auto;
	text-align: left;
	background: #054D4E;
	color: #CAFFFF;
	border: 2px solid #4FD7D9;
	padding: 2px 10px;

	border-radius: 5px;
	box-shadow: none;
	z-index: 20;
}
.ps-overlay .ps-popup {
	position: static;
	margin: 80px auto 20px auto;
	max-width: 320px;
}
    
.ps-popup p,
.ps-popup h3 {
	margin: 7px 0;
}
.ps-popup h3 {
	font-size: 12px;
}
p.or {
	font-size: 12pt;
	font-style: italic;
	font-weight: bold;
	color: grey;
	margin: 20px 0;
	text-align: center;
}
p.or:before,
p.or:after {
	content: "";
	font-size: 10px;
	padding: 0 0 0 85px;
	margin: 0 9px 0 0;
	border-bottom: 1px solid lightgrey;
	position: relative;
	top: -7px;
}
p.or:after {
	margin: 0 0 0 9px;
}
.ps-popup p.error {
	font-weight: bold;
	color: #C12942;
}

.popupmenu, popupmenu li {
	margin: -2px 0 -1px 0;
	padding: 0;
	list-style: none;
	font-size: 10px;
}
.popupmenu h3 {
	margin: 5px 0 0 -2px;
	padding: 1px 0;
	font-size: 10px;
    background: #023B3C;
    border: 1px solid #4FD7D9;
    border-radius: 2px;
	color: #CAFFFF;
}
.popupmenu li:first-child h3 {
	margin-top: 0;
}
.popupmenu button {
	display: block;
	cursor: pointer;
	font-size: 8pt;
	font-family: Helvetica, Arial, sans-serif;
	margin: 0 0 0 6px;
	padding: 0 4px;
	border: 1px;
	border-radius: 2px;
	background: transparent;
	color: #CAFFFF;
	width: 204px !important;
	height: 19px;
	text-align: left;

	box-sizing: border-box;
}
@media (max-height:590px) {
	.popupmenu button {
		height: 17px;
	}
	.popupmenu h3 {
		margin-top: 2px;
	}
	.popupmenu, popupmenu li {
		margin: -3px 0 -2px 0;
	}
}
@media (max-height:518px) {
	.popupmenu button {
		height: 16px;
	}
	.popupmenu h3 {
		margin-top: 1px;
	}
}

.popupmenu button.sel {
	padding: 0 3px;
	border: 1px solid lightgrey;
}
.popupmenu button:hover,
.popupmenu button.sel:hover {
	padding: 0 3px;
    color: #CAFFFF;
	border: 1px solid #4FD7D9;
	background: #066466;
}
.select {
	cursor: pointer;
	box-sizing: border-box;
	width: 230px;
	height: 32px;
	padding: 0 5px;
	font-size: 9pt;
	text-align: left;
	font-family: Helvetica, Arial, sans-serif;

	border-radius: 4px;
	border: 2px solid #4FD7D9;
	background: #054D4E;
	box-shadow: none;
	color: #CAFFFF;
}

.select.teamselect {
	height: 42px;
    color: #CAFFFF;
  	border-color: #4FD7D9 !important;
  	background: #054D4E !important;
  
}
.select:hover {
	background: #06686A;
    border: #4FD7D9 solid 2px;
}
.select:active {
	border-color: lightgrey;
	background: #06686A;
	box-shadow: none;
}
.select.formatselect:disabled {
	border-color: lightgrey;
    color: #CAFFFF;
	background: #054D4E;
	box-shadow: none;
}
.select:after {
	float: right;
	margin-top: 1px;
	font-size: 14px;
	content: "\f0d7";
  font-family: FontAwesome;
  -webkit-font-smoothing: antialiased;
  color: #CAFFFF;
}

.select:disabled {
	border-color: lightgrey;
	background: #054D4E;
	color: #CAFFFF;
	box-shadow: none;
}
.select:disabled:after {
	color: #CAFFFF;
}
.select.preselected,
.select:disabled.preselected {
	color: #CAFFFF;
}

/*********************************************************
 * Main menu
 *********************************************************/

.leftmenu {
	padding: 40px 0 0 0;
	width: 324px;
}
.rightmenu {
	position: absolute;
	top: 40px;
	right: 25px;
}
.activitymenu {
	position: absolute;
	left: 325px;
	top: 40px;
}
.mainmenu {
	padding: 0 0 30px 0;
}

.battleform {
	width: 230px;
	text-align: left;
	margin: 0 auto;
}
.battleform .buttonbar {
	text-align: center;
}
.button.disabled, .button.disabled:hover, .button.disabled:active, .mainmenuwrapper .menugroup .button.disabled, .mainmenuwrapper .menugroup .button.disabled:hover, .mainmenuwrapper .menugroup .button.disabled:active {
    cursor: default;
    background: #002324;
    border-color: #CAFFFF;
    border-style: solid;
    color: #999999;
    text-shadow: none;
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
}
.mainmenuwrapper {
	position: relative;
	min-height: 100%;
}
.mainmenufooter {
  opacity: 0.0;
  }
.mainmenufooter blockquote {
	margin: 0 0 1em 0;
	padding: 0;
}
.mainmenufooter small {
	font-size: 9pt;
}
.mainmenufooter a {
	text-decoration: underline;
	color: #C12942;
}
.mainmenufooter a:hover {
	text-decoration: underline;
	color: #C12942;
}

.pmbox {
	width: 270px;
	margin: 0 auto;
	min-height: 2px;
}
.pm-window {
	margin-bottom: 12px;
}
.pm-window h3 {
	background: #06686A;
    opacity: 0.9;
	color: white;
	margin: 0;
	padding: 2px 5px;
	font-size: 11pt;
	border: 2px solid #CAFFFF;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
	cursor: pointer;
}
.pm-window h3:hover {
	color: white;
}
.pm-window h3.pm-minimized {
	border-radius: 5px;
}
.pm-window h3.pm-notifying {
	border-color: #CAFFFF;
	background: #0A8688;
}
.pm-window h3.pm-notifying:hover {
	border-color: #CAFFFF;
	background: #0A8688;
}
.pm-window h3 .closebutton,
.pm-window h3 .minimizebutton {
	float: right;
	padding-right: 1px;
}
.pm-window h3 small {
	color: white;
	font-weight: normal;
	vertical-align: 1px;
}
.pm-log {
	min-height: 100px;
	max-height: 300px;
	background: #054D4E;
	color: lightgrey;
        opacity: 0.9;
	word-wrap: break-word;
	border: 1px solid #CAFFFF;
	border-bottom: 0;

	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
}
.pm-log-add {
	border: 1px solid #CAFFFF;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
	background: #054D4E;
}
.pm-window.collapsed .pm-log,
.pm-window.collapsed .pm-log-add {
	display: none;
}
.pm-window.focused h3,
.pm-window.focused .pm-log,
.pm-window.focused .pm-log-add {
	border-color: #CAFFFF;
}
.pm-window.focused h3,
.pm-window.focused h3:hover {
	background: #0A8688;
	color: #CAFFFF;
  	border-color: #CAFFFF;
	cursor: default;
}

.challenge {
	margin-top: 0px;
	background: #003B3C;
	border: 2px solid white;
  	border-top: 0;
	color: #CAFFFF;
	padding: 1px 5px;
}
.challenge p {
	margin: 4px 0;
}

.tiny-layout .leftmenu,
.tiny-layout .rightmenu,
.tiny-layout .activitymenu {
	position: static;
	top: 0;
	right: 0;
	left: 0;
	width: auto;
	margin: 0;
	padding: 0;
}
.tiny-layout .leftmenu {
	padding-top: 30px;
}
.tiny-layout .activitymenu {
	padding-bottom: 8px;
}

.mainmessage {
	display: block;
	padding: 10px 15px;
	margin: 30px 40px;
	max-width: 400px;
	border-radius: 20px;

	background: rgba(0, 0, 0, 0);
	color: black;
}

.menugroup {
	display: block;
	padding: 10px 0;
	margin: 0 27px 20px 27px;
	max-width: 400px !important;
	border-radius: 10px;
  
	background: rgba(0, 0, 0, .5);
	color: white;
	text-shadow:none;
}
.menugroup {
	background: rgba(0, 0, 0, .5);
	text-align: center;
}

.mainmessage p,
.menugroup p {
	margin: 10px 0;
}
.menugroup .button {
	box-sizing: border-box;
	font-size: 14px;
	font-family: Helvetica, Arial, sans-serif;

	display: inline-block;
	padding: 5px 7px 3px 7px;

	width: 230px !important;
  	height: 30px !important;

  	font-size: 18px;
  
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: none;
	border-radius: 5px;

	/* default colors */
	color: white;
	border: solid 2px;
	background: #C12942;
	border-color: white;
  	box-shadow: none;
}

.menugroup button.big {
	width: 230px;
  	height: 32px;
	padding: 6px 7px 6px 7px;
	font-size: 14pt;
  
}
.folderpane{
    width: 0px;
    height: 0px;
}
.teampane{
    left: 50px;
}
.menugroup .button:hover {
	color: white;
	border: solid 2px;
	background: #054D4E;
  	background-size: 100%;
	border-color: black;
  	box-shadow: none;
}
.menugroup .button:active {
	color: white;
	border: solid 2px;
	background: url(http://i.imgur.com/MhIr3Z9.png);
  	background-size: 100%;
	border-color: black;
  	box-shadow: none;
}
.menugroup .button.disabled,
.menugroup .button.disabled:hover,
.menugroup .button.disabled:active {
	cursor: default;
	color: white;
	border: solid 2px;
	background: #054D4E;
  background-size: 100%;
	border-color: black;
  	box-shadow: none;
}

.rightmenu {
    width: 330px !important;
  }

@media (max-width:895px) {
	.mainmenu {
		padding-bottom: 1px;
	}
	.rightmenu {
		position: static;
		top: 0;
		right: 0;
		padding-bottom: 30px;
	}
	.rightmenu {
		width: 324px;
	}
	.rightmenu .menugroup .button {
		width: 200px;
	}
}

/*********************************************************
 * Ladder
 *********************************************************/
.button, .popupmenu button.button {
    color:#CAFFFF;
    border-radius: 5px;
    border: solid 2px #4FD7D9;
    background: #054D4E;
    font-size:12px;
    height:inherit;
    box-shadow:none;
    text-shadow:none;
    float:none !important;
    padding:0px;
}
.button:HOVER, .popupmenu button.button:hover {
    background:#0C7F81;
    border: solid 2px #4FD7D9;
  }
.ladder table, .ladder td, .ladder th {
	border-collapse: collapse;
	border: 1px solid lightgrey;
}
.ladder td, .ladder th {
	padding: 3px 5px;
}
.ladder th {
	text-align: left;
    color: #98E2E2;
	font-size: 9pt;
	background: rgba(0, 0, 0, 0);
}
.ladder span {
	color: lightgrey;
}
.roomcounters .button {
