.teampane .button.big, .teamwrapper .button.big {
    color:#CAFFFF;
    border-radius: 5px;
    border: solid 2px #CAFFFF;
    background:#054D4E;
    font-size:12px;
    box-shadow:none;
    text-shadow:none;
    padding:1px 6px !important;
    float:none !important;
    margin-right:5px;
}
.teampane .button.big:hover, .teamwrapper .button.big:hover {
	border-radius: 5px;
    color:#caffff;
	border: solid 2px #CAFFFF;
	background:#067779;
}

.teampane .button.big:active, .teamwrapper .button.big:active {
	border-radius: 5px;
    color:#CAFFFF;
	border: solid 2px #CAFFFF;
	background:#078D8F;
}
