.team { 
	
	box-sizing: border-box;
	display: inline-block;
	padding: 5px 7px 3px 7px;
	box-shadow:none;
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	border-radius: 5px;
	color: #CAFFFF;
	border: solid 2px #98E2E2;
	background: #054D4E;
	box-shadow:none;
	outline: none;
	cursor: pointer;
	text-align: center;
}
.team:hover {
 background: #067779;
	border-color: #98E2E2;   
}

.team:active {
   	box-shadow: none;
	background: #078D8F !important;
	border-color: #98E2E2; 
}

