css:
@-moz-document domain("minerva.ugent.be") {
body {
    background: rgb(52, 49, 52) !important;
    color: #444 !important;
    font-size: 100% !important;
    overflow-x: hidden;
}
html, body {margin:0;padding:0;height:100%;}

.ui-widget, body {
    font-family: "Century Gothic", CenturyGothic, AppleGothic, sans-serif !important;
	font-variant: normal;
	font-weight: 100;
}

.col-xs-1, .col-sm-1, .col-md-1, .col-lg-1, .col-xs-2, .col-sm-2, .col-md-2, .col-lg-2, .col-xs-3, .col-sm-3, .col-md-3, .col-lg-3, .col-xs-4, .col-sm-4, .col-md-4, .col-lg-4, .col-xs-5, .col-sm-5, .col-md-5, .col-lg-5, .col-xs-6, .col-sm-6, .col-md-6, .col-lg-6, .col-xs-7, .col-sm-7, .col-md-7, .col-lg-7, .col-xs-8, .col-sm-8, .col-md-8, .col-lg-8, .col-xs-9, .col-sm-9, .col-md-9, .col-lg-9, .col-xs-10, .col-sm-10, .col-md-10, .col-lg-10, .col-xs-11, .col-sm-11, .col-md-11, .col-lg-11, .col-xs-12, .col-sm-12, .col-md-12, .col-lg-12 {
    position: initial;
}

* a, * a:link, * a:visited, #content a, #content a:link, #content a:visited, .box a:link, .box a:visited, .facet li a:link, .facet li a:visited, .box li a:hover, .facet li a:hover {
    color: #2f9377;
}
a, a:link {
    color: #2f9377;
    text-decoration: none;
}
a:hover {
    border-bottom: 1px solid transparent;
    text-decoration: underline;
}

a.active, .box li a:active, .facet li a:active {
    color: #7bcfff;
}
h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5, h6, .h6 {
	font-family: "Century Gothic", CenturyGothic, AppleGothic, sans-serif;
    font-weight: normal;
}
h1 {
    color: #4b4b5d;
}
h2 {
    color: #58587b;
}
fieldset {
    border: 0 !important;
}

select, input[type=text], input[type=password], input[type=email], input[type=number], textarea {
    min-width: 250px;
    width: 250px !important;
    padding: 5px 10px;
    border: 1px solid #aaa !important;
    height: 38px !important;
    background: #ffffff;
    color: #777 !important;
    box-sizing: border-box;
    font-weight: normal;
}
select:hover, input[type=text]:hover, input[type=password]:hover, input[type=email]:hover, input[type=number]:hover, textarea:hover {
    border-color: #ababab;
}
input[type=submit], button, input[type=button], input[type=submit]:hover, input[type=button]:hover, button:hover, .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    padding: 10px 40px;
    border: 0 !important;
    font-weight: normal;
    padding: 11px 15px !important;
}
input[type=submit], button, input[type=button], input[type=submit]:hover, input[type=button]:hover, button:hover {
    background-color: #d1bd10 !important;
    border: 0 !important;
    color: #222 !important;
}
input[type=submit]:active, button:active, input[type=button]:active {
    background-color: #222 !important;
    border: 0 !important;
    color: #d1bd10 !important;
}
input[type=submit], input[type=button] {
    background-color: #d1bd10 !important;
    color: #222;
}
input[type=checkbox] + label:before {
    background: #444;
    content: " ";
    display: inline-block;
    width: 15px;
    height: 15px;
    color: #CCC;
    line-height: 15px;
    vertical-align: middle;
    text-align: center;
    border-radius: 2px;
    margin-right: 10px;
}
input[type=checkbox] + label:hover:before {
	/* todo */
}
input[type=checkbox] {
    position: absolute;
	visibility: hidden;
    width: 0px;
}
input[type=checkbox]:checked + label:before {
	content: "\2713";
}

.ui-button.ugent-default, .ui-button.ugent-default.ui-state-hover, .ui-button.ui-state-active, .ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active, .ui-datepicker-buttonpane .ui-priority-primary, .ui-dialog .ui-state-focus, .ui-widget-header {
    color: #EEE;
    background: #3e4c56;
    font-weight: normal;
}
.ui-widget-content {
    color: inherit;
    border: 0;
    background: #FFF;
}
.ui-widget-content .subscription {
    position: relative;
    left: -8px;
    padding: 10px 8px;
    width: 100%;
}

/* GENENERAL STUFF */
#container, body, html {
    overflow: hidden;
}
#container {
	position: relative;
    width: 100%;
    height: 100% !important;
    margin: 0 auto;
    display: block;
    box-sizing: border-box;
	padding: 0 0 50px 0 !important;
	min-height: 0px;
    max-width: none !important;
}
#container > * {
	display: block;
    box-sizing: border-box;
    height: 100%;
}

#topbar img {
    display: none !important;
}
#user li.member {
    background: none !important;
}

#header {
    position: relative;
    width: 20%;
    max-width: 350px;
    min-width: 200px;
    float: left;
    background: #35465f;
    transition: all .4s;
    -webkit-transition: all .4s;
    overflow: hidden;
}
#main {
    overflow: hidden;
    background: #EEE;
    padding: 50px 0 0 0;
}
#breadcrumb {
    position: absolute;
    right: 10px;
    top: 10px;
    text-align: right;
    height: auto;
}
#container > .right, #topbar, #container > .clear, .hidden {
    display: none !important;
}

#headerleft, #headerright {
    display: none;
}

#subbar {
    margin: 0 !important;
    padding: 0 !important;
    background: none !important;
    height: auto !important;
}

#subbar div {
    background: none !important;
}

#subbarul, #subbar ul {
    width: 100% !important;
    height: 100% !important;
    margin: 0 !important;
    padding: 0 !important;
}

#subbar li {
    display: block;
    padding: 0 !important;
    margin: 0 !important;
    height: auto;
    float: none !important;
}

#subbar a {
    display: block;
    color: #FFF;
    padding: 20px 20px !important;
    margin-bottom: 20px;
}
#subbar a:hover {
	background-color: rgba(255, 255, 255, .1);
}

#subbar ul li.selected a:link, #subbar ul li.selected a:visited {
    background: #d1dbe2;
    font-weight: normal !important;
    color: #000 !important;
}

#topbar, #breadcrumb, #main {
    margin: 0;
    box-sizing: border-box;
}
#container {
    background: rgb(50, 50, 50) !important;
}
#breadcrumb {
    margin-top: 5px !important;
    padding: 0 !important;
}
#breadcrumb a:hover {
    text-decoration: none;
}

#content {
    padding: 0 0 0 20px;
    overflow-y: auto;
	max-height: 100%;
}

.short_rest {
    display: inline-block !important;
}
.short_more {
    display: none !important;
}
div.system_announcement {
	color: inherit;	
    margin-right: 0;
}
.system_announcement_content {
    width: 100%;
}
img.hide_system_announcement {
    width: 24px;
    height: 24px;
    box-sizing: border-box;
    /* ugly, i know i know, but its the only way to get rid of those icons. Remember: after/before pseudo selectors don't work for imgs/inline elements
    What it does: creates an svg background, with only one text element containing an ascii char
	*/
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' version='1.1' height='24px' width='24px'><text x='-2' y='20' fill='#DDD' font-size='22'>&#128065;</text></svg>");
    background-position: 3px 0px;
    padding-left: 24px;
    
}


.actions {
    display: block;
    margin: -5px 0 10px 0;
}

#content > span, #content > a, #content > strong {
    background: none !important;
}

/* throw away those ugly images */
.box a img, .facet img {
    display: none;
}
.box ul li, .facet ul li {
    background: none;
}
/* dat border?? */
.box, .facet, .box-img, .announcement {
    border: 0;
}
.announcement label.multiple_actions_checkbox::before {
    display: none;
}

/* clearfix dat shit */
.actions:after {
     visibility: hidden;
     display: block;
     font-size: 0;
     content: " ";
     clear: both;
     height: 0;
}
.actions a, #tabs li, button {
    background: #EEE !important;
    padding: 5px 20px !important;
    color: #444 !important;
}
#tabs li {
    margin: 0 0 0 20px !important;
    float: left;
}
#tabs li.ui-state-active {
    background: #4c4c4c !important;
}
#tabs li.ui-state-active a {
    color: #dcdcdc !important;
}
.actions li {
    margin: 0 0 0 20px !important;
    float: right;
}
.actions a img {
    display: none;
}
.error-message, .normal-message {
    font-weight: normal !important;
    width: 100%;
    left: -40px;
    margin: 0;
    padding: 10px 40px;
}
.normal-message:before, .bodyform .normal-message:before {
	display: inline-block;
	content:'\2709';
    font-size: 150%;
    margin: 0 15px 5px 0;
}
.normal-message > img:first-child, .bodyform .normal-message > img:first-child {
	display: none;
}

/*   HOMEPAGE COURSE LIST   */
div.courselist div.course, #subbar a {
    transition: background .2s;
}
div.courselist div.course:hover {
    background: #f7f7f7;
}
div.course a {
    color: #6d7396 !important;
}


.user_course_category_wrapper, .minerva-ui-accordion-content, .box dd, .facet dd, .announcement_content, div.system_announcement {
    background: #FFF;
    border: 0;
    color: inherit;
}
div.announcement_content {
    padding: 10px 20px 20px 20px;
}

.user_course_category_header:hover, .minerva-ui-accordion-header:hover, .box dt:hover, .facet dt:hover, .announcement_title:hover, .user_course_category_header, .minerva-ui-accordion-header, .box dt, .facet dt, .announcement_title, .ui-accordion .ui-accordion-header, .box .menusectioncaption, .system_announcement_title {
    overflow: hidden;
    background: #526675;
    color: #FFF;
    font-weight: normal;
    font-size: 90%;
    border: 0 !important;
    padding: 0 0 0 9px !important;
    height: 30px !important;
    line-height: 30px;
}
div.courselist div.course {
    color: #4E4E4E;
    padding: 10px 10px 10px 10px;
}

.user_course_category_header .ui-icon, .minerva-ui-accordion-header .ui-icon {
    position: relative;
}
.user_course_category_header .ui-icon:before, .minerva-ui-accordion-header .ui-icon:before {
    display: block;
    position: absolute;
	/*content: '\2228';*/
    content:'>';
    line-height: 14px;
    font-size: 14px;
    width: 14px;
    color: blue;
    height: 14px;
    left: 0;
    top: 0;
}
.ui-icon-closethick, .ui-icon-triangle-1-s, .ui-icon-triangle-1-n, .ui-icon-triangle-1-e {
    display: none;
}
.ui-accordion .ui-accordion-header {
    position: relative;
}
.ui-accordion .ui-accordion-header > * {
    display: inline-block;
    padding: 0 10px 0 30px;
}
.ui-accordion .ui-accordion-header > .ui-icon {
    position: absolute;
    padding: 0;
    left: 10px;
    top: 12px;
}
.ui-accordion-content {
    border: 0 !important;
    padding: 0;
}
/* magic random red text voor "infosites" */
.course > span {
    color: inherit !important;
    font-weight: normal !important;
}
.course .icon {
    display: none;
}
.course > a:hover {
    text-decoration: none !important;
}


/* COURSE PAGE */
#tools {
    background: #FFF;
}
.tool {
    transition: background .2s;
}
.tool:hover {
    background: rgba(0, 0, 0, 0.08);
}

/* course icons (files, agenda, links,...) */
.toolshortcut_agenda, .toolshortcut_link, .toolshortcut_document, img[title="Cursus-startpagina"], .notifications, .toolshortcut_wiki {
    -webkit-filter: grayscale(50%);
    filter: grayscale(50%);
}
.toolshortcut_agenda:hover, .toolshortcut_link:hover, .toolshortcut_document:hover, img[title="Cursus-startpagina"]:hover {
    -webkit-filter: grayscale(50%);
    filter: grayscale(50%);
}
.toolshortcut_announcement, .toolshortcut_forum, .toolshortcut_studiefiche {
    opacity: .8;
}
.toolshortcut_announcement:hover, .toolshortcut_forum:hover, .toolshortcut_studiefiche:hover {
    opacity: 1;
}
img[title="Cursus-startpagina"] {
    background-image:url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' version='1.1' height='24px' width='24px'><text x='-5' y='20' fill='#EEE' font-size='22'>&#127968;</text></svg>");
    background-position: 4px 0px;
    width: 24px;
    height: 24px;
    box-sizing: border-box;
    padding-left: 24px;
}


/* dropdown icon next to course group */
.ui-state-hover .ui-icon, .ui-state-focus .ui-icon, .ui-state-default .ui-icon {
    filter: grayscale(100%) brightness(200%);
}

/* BESTANDEN */

.dataTables_wrapper, div.dataTables_wrapper .ui-widget-header {
    background: initial !important;
}
.dataTables_wrapper, .ui-widget-header {
    border: 0;
}

table.display thead th {
    background: #EEE;
}
table.display tr.odd {
    background: #F7F7F7;
}
table.display tr.even {
    background: #F1F1F1;
}
tbody tr.even:hover, tbody tr.odd:hover, td.highlighted {
    background: #FCFCFC !important;
}
table.display tbody tr {
    color: inherit;
    border-top: 0;
}
/* ANNOUNCEMENTS*/
.announcement {
    border: 0;
}
div.announcement_title img {
    display: none;
}

div.announcement_title:before {
	display: inline-block;
	content:'\2709';
    margin-right: 10px;
}

/* MIJN PROFIEL */
.ui-tabs .ui-tabs-panel {
    border: 0;
}
#tabs {
    margin-top: 20px;
}
#tabs a {
    color: #444 !important;
}

/* LOGIN BUTTON */
#sidebar {
    position: static;
}
#frm_logincas input[type=submit] {
    position: absolute;
    left: 0;
    top: 90%;
    width: 100%;
    height: 10%;
    z-index: 9;
    background: #0766b1 !important;
    color: #FFF !important;
    transition: background 1s;
    box-shadow: 0 0 0 0 rgba(0, 0, 0, 0);
    
}
#frm_logincas input[type=submit]:hover {
    background: #097cd6 !important;
    transition: background .2s;
}

#frm_logincas dt {
    display: none;
}
#frm_logincas dd small {
    display: none;
}
#frm_logincas dd {
    border: 0;
    height: 0px;
    padding: 0;
}
#frm_logincas + dl dt {
    display: none;
}
#frm_logincas + dl, #loginform + dl dd {
    background: none;
}
#frm_logincas + dl dd {
    border: 0;
    padding: 0;
}
#frm_logincas + dl li {
    padding: 0;
}

/* CALENDER */
.fc-view-month .ui-widget-content.fc-today {
    background: rgba(0, 100, 255, .2);
}
.fc-event.platform, .fc-agenda .fc-event-time.platform, .fc-event.platform a {
    color: #e0e0e0;
    border: 1px solid #a21313;
    background-color: #7d2c2c;
    float: left;
}

/* CURRICULUM */
div.subscription {
    border: 0;
}
div.subscription.active {
    background-color: #CAF0C7;
    margin: 0;
}

/* LINKS PAGE */
#links_content_wrapper td > img {
    width: 24px;
    height: 24px;
    box-sizing: border-box;
    padding-left: 24px;
    background-image:url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' version='1.1' height='24px' width='24px'><text x='-3' y='20' fill='red' font-size='21'>&#127760;</text></svg>");
    background-position: 0px 0px;
    vertical-align: middle;
}


#footer-wrap {
    position: absolute;
    left: 0;
    bottom: 0;
    height: 50px;
    box-sizing: border-box;
    width: 100% !important;
}




/* FIX FOR MINERVA 2016 */


#subbarul ul li.selected a, #subbarul ul li a:hover {
	border-color: transparent;
}
#subbarul ul li a, #subbarul ul li a:link, #subbarul ul li a:visited {
    color: #FFF;
    font-family: "Century Gothic", CenturyGothic, AppleGothic, sans-serif;
    font-size: 14px;
}
.courselist {
    margin-right: 0;
}
.user_course_category_wrapper div {
    margin: 0;
}
.ui-accordion-header, .admin_section h4 {
    cursor: pointer;
    margin-bottom: 0;
}
.box .menusectioncontent ul li:before, .box ul li:before {
    content: '';
}
div#toolshortcuts img.icon, div.notifications img.icon, .icon24, .toolshortcut {
    border-radius: 3px;
}
/* toolshortcut colorize :D 
.toolshortcut_announcement, .tool_announcement {
    filter: grayscale(0%) hue-rotate(295deg) brightness(140%) !important;
    -webkit-filter: grayscale(0%) hue-rotate(295deg) brightness(140%) !important;
}
.toolshortcut_document, .tool_document {
    filter: grayscale(0%) hue-rotate(195deg) brightness(150%) !important;
    -webkit-filter: grayscale(0%) hue-rotate(195deg) brightness(150%) !important;
}
.toolshortcut_agenda, .tool_agenda {
    filter: grayscale(0%) hue-rotate(127deg) !important;
    -webkit-filter: grayscale(0%) hue-rotate(130deg) !important;
}
.tool_link, .tool_forum, .tool_studiefiche {
    filter: grayscale(0%) hue-rotate(-15deg) brightness(150%) !important;
    -webkit-filter: grayscale(0%) hue-rotate(-15deg) brightness(150%) !important;
}*/
.box {
    margin-bottom: 5px;
    padding: 20px 0 0 20px;
    background: none;
}
#footer-wrap {
    padding-top: 0;
}
#footer {
    padding-top: 0;
}
#footer, #footer-wrap {
    background-color: #404e64;
    color: #888;
}
#footer a, #footer-wrap input[type=button] {
    color: #88a08a !important;
    border-bottom: 1px solid rgba(0, 0, 0, 0) !important;
}
#footer a:hover, #footer-wrap input[type=button]:hover {
    color: #67a26c !important;
    border-bottom: 1px solid #67a26c !important;
}
#footer .plugins {
    position: absolute;
    right: 20px;
    bottom: 5px;
}
#footer-wrap input[type=button], #footer-wrap input[type=button]:hover, #footer-wrap input[type=button]:active {
    background: none !important;
    display: inline;
    padding: 0 !important;
    
}

#user_course_category_add input {
    vertical-align: top;
    margin-right: 5px;
}


/* login page */
#minerva-custom-login-page .bg-tertiary {
    background-color: initial;
}
#frm_login {
    display: block !important;
}

#frm_login input {
    width: 100% !important;
}
#frm_login input[type=submit] {
    background-color: #dcb604 !important;
}
#frm_login input[type=submit]:hover {
    background-color: #FFD200 !important;
}
#frm_login input[type=text], #frm_login input[type=password] {
    border: 1px solid #8e8e8e !important;
    background: #FAFAFA;
    padding: 20px 10px;
}
#frm_logincas {
    display: none;
}
#li_link_cas_login {
    display: block;
}
#li_link_alternative_login {
    display: none;
}
#homepage-title {
    display: none
}
#minerva-custom-login-page .content-header-home .linklist li a:hover, #minerva-custom-login-page .content-header-home .linklist li a:focus {
    border-color: transparent;
}
}
