css:
@-moz-document domain('habrahabr.ru'), domain('geektimes.ru'), domain('tmfeed.ru') {
  * {
    border-color: rgba(35,35,35,0.8) !important;
    border-width: 0 !important;
    box-shadow: none !important;
    color: #ccc !important;
    font-family: 'Ubuntu Mono' !important;
    font-size: 13px;
    font-weight: normal !important;
    text-shadow: none !important;
  }
  html,
  body,
  .layout,
  .App * {
    background: #232323 !important;
  }
  select,
  textarea,
  .search-field__input,
  select:hover,
  textarea:hover,
  .search-field__input:hover,
  select:focus,
  textarea:focus,
  .search-field__input:focus {
    background: #1a1a1a !important;
    color: #de5e1e !important;
    border: none !important;
  }
  hr {
    border-style: solid !important;
    border-color: #1a1a1a !important;
  }
  div.page-nav span,
  div.page-nav a {
    color: #de5e1e !important;
  }
  div.page-nav em {
    background: #de5e1e !important;
    color: #121212 !important;
  }
  ::-moz-selection {
    background: #de5e1e !important;
    color: #121212 !important;
  }
  ::selection {
    background: #de5e1e !important;
    color: #121212 !important;
  }
  .spoiler::before {
    height: 0 !important;
    width: 0 !important;
    margin: 0.45em 0 !important;
    background: none !important;
    border-style: dotted;
    border-color: transparent !important;
    border-width: 0.4em 0.6em !important;
    border-left-color: #de5e1e !important;
  }
  .spoiler_title {
    color: #de5e1e !important;
    border-bottom: 1px dotted;
    line-height: 1.5;
    display: block;
    width: 95%;
  }
  *.post__title,
  *.post__title * {
    color: #de5e1e !important;
  }
  *.post__title:hover,
  *.post__title *:hover,
  *.post__title:focus,
  *.post__title *:focus {
    color: #dc1e1e !important;
    text-decoration: none !important;
  }
  .hljs {
    display: block;
    overflow-x: auto;
    font-size: 13px;
    padding: 0.5em;
    background: #171717 !important;
    color: #999 !important;
  }
  .hljs-strong,
  .hljs-emphasis {
    color: #fff !important;
    font-weight: bold !important;
  }
  .hljs-bullet,
  .hljs-quote,
  .hljs-link,
  .hljs-number,
  .hljs-regexp,
  .hljs-literal {
    color: #de3e1e !important;
  }
  .hljs-code,
  .hljs-selector-class {
    color: #de1e1e !important;
  }
  .hljs-emphasis {
    font-style: italic;
  }
  .hljs-keyword,
  .hljs-selector-tag,
  .hljs-section,
  .hljs-attribute,
  .hljs-name,
  .hljs-variable {
    color: #de1e1e !important;
  }
  .hljs-params {
    color: #ccc !important;
  }
  .hljs-string {
    color: #d59575 !important;
  }
  .hljs-subst,
  .hljs-type,
  .hljs-built_in,
  .hljs-builtin-name,
  .hljs-symbol,
  .hljs-selector-id,
  .hljs-selector-attr,
  .hljs-selector-pseudo,
  .hljs-template-tag,
  .hljs-template-variable,
  .hljs-addition {
    color: #de1e1e !important;
  }
  .hljs-comment,
  .hljs-deletion,
  .hljs-meta {
    color: #804020 !important;
  }
  *.html_banner,
  *.firskill__label,
  *.sticky,
  *.xyz_wrapper,
  *.xyz_wrapper_inner,
  *.html_banner *,
  *.firskill__label *,
  *.sticky *,
  *.xyz_wrapper *,
  *.xyz_wrapper_inner * {
    transition: color 0.1s, background 0.1s;
    display: none !important;
    opacity: 0 !important;
    visibility: hidden !important;
    transform: translateX(200vw) !important;
  }
  *.promo-block,
  *.promo-block * {
    transition: color 0.1s, background 0.1s;
    display: none !important;
    opacity: 0 !important;
    visibility: hidden !important;
    transform: translateX(200vw) !important;
  }
  .facebook_like_box,
  .vkontakte_groups,
  .facebook_like_box *,
  .vkontakte_groups * {
    transition: color 0.1s, background 0.1s;
    display: none !important;
    opacity: 0 !important;
    visibility: hidden !important;
    transform: translateX(200vw) !important;
  }
  .for_users_only_msg,
  .for_users_only_msg * {
    transition: color 0.1s, background 0.1s;
    display: none !important;
    opacity: 0 !important;
    visibility: hidden !important;
    transform: translateX(200vw) !important;
  }
  a {
    color: #de5e1e !important;
  }
  a:hover,
  a:focus,
  a:active {
    color: #de1e1e !important;
  }
  svg path {
    fill: #de5e1e !important;
    transition: transform 0.1s;
  }
  svg:hover path,
  svg:focus path {
    transform: scale(0.99, 0.99);
    fill: #de1e1e !important;
  }
  img {
    background: #232323 !important;
    mix-blend-mode: exclusion;
    filter: invert(1);
  }
  img:hover {
    mix-blend-mode: none;
    filter: none;
  }
  .post__title-arrow,
  .comment-item__controls {
    background: transparent !important;
    color: rgba(222,94,30,0.33) !important;
    text-decoration: none !important;
  }
  a::before,
  button::before,
  span::before,
  .views-count_post::before,
  .icon_tag::before,
  .post .hubs::before,
  .inline-list__item::before,
  .checkbox__label::before,
  a::after,
  button::after,
  span::after,
  .views-count_post::after,
  .icon_tag::after,
  .post .hubs::after,
  .inline-list__item::after,
  .checkbox__label::after {
    background: transparent !important;
    color: rgba(222,94,30,0.33) !important;
    text-decoration: none !important;
  }
  .content-list__item_habrahabr::before,
  .content-list__item_geektimes::before {
    background-color: #de5e1e;
    background-blend-mode: multiply;
    border-radius: 50%;
  }
  .comment-item__avatar-img,
  .author-info__image-pic {
    border-radius: 50%;
  }
  *.flag,
  *.flag * {
    background: #de5e1e !important;
    color: #171717 !important;
  }
  *.flag::after {
    border-color: transparent !important;
    border-right-color: transparent !important;
  }
  *.flag:hover,
  *.flag:focus,
  *.flag:active {
    background: #de1e1e !important;
    color: #171717 !important;
  }
  #TMpanel {
    background: #171717 !important;
  }
  #TMpanel a {
    color: rgba(222,94,30,0.5) !important;
  }
  #TMpanel a:hover,
  #TMpanel a:focus {
    color: #de5e1e !important;
  }
  #TMpanel .bmenu_inner {
    opacity: 0 !important;
  }
  a sup {
    color: #de5e1e !important;
  }
  a.btn,
  a.button {
    background: #1a1a1a !important;
  }
  a.btn:hover svg path,
  a.button:hover svg path,
  a.btn:focus svg path,
  a.button:focus svg path {
    fill: #de1e1e !important;
  }
  .sort_menu {
    background: #232323 !important;
  }
  .sort_menu li.current a {
    background: #de5e1e !important;
    color: #171717 !important;
  }
  .companies_list ul li,
  .friends_list ul li,
  .hubs_list ul li,
  .companies_list ul li::before,
  .friends_list ul li::before,
  .hubs_list ul li::before,
  .companies_list ul li::after,
  .friends_list ul li::after,
  .hubs_list ul li::after {
    color: #de5e1e !important;
  }
  a.layout__elevator {
    background: transparent !important;
  }
  a.layout__elevator:hover,
  a.layout__elevator:focus {
    background: #1a1a1a !important;
  }
  a.layout__elevator svg {
    transform: translateY(42vh);
  }
  .tabs__menu,
  .tabs-menu,
  .menu_sub,
  .menu {
    border-top: solid #232323 !important;
    border-bottom: solid #232323 !important;
  }
  .tabs__menu,
  .tabs-menu,
  .menu_sub,
  .menu,
  .tabs__menu *,
  .tabs-menu *,
  .menu_sub *,
  .menu * {
    color: #de5e1e !important;
    background: #202020 !important;
    border-width: 1px !important;
  }
  .tabs__menu .tab__item,
  .tabs-menu .tab__item,
  .menu_sub .tab__item,
  .menu .tab__item,
  .tabs__menu * .tab__item,
  .tabs-menu * .tab__item,
  .menu_sub * .tab__item,
  .menu * .tab__item,
  .tabs__menu .tab-item_current,
  .tabs-menu .tab-item_current,
  .menu_sub .tab-item_current,
  .menu .tab-item_current,
  .tabs__menu * .tab-item_current,
  .tabs-menu * .tab-item_current,
  .menu_sub * .tab-item_current,
  .menu * .tab-item_current,
  .tabs__menu .menu__item,
  .tabs-menu .menu__item,
  .menu_sub .menu__item,
  .menu .menu__item,
  .tabs__menu * .menu__item,
  .tabs-menu * .menu__item,
  .menu_sub * .menu__item,
  .menu * .menu__item {
    border-right: 1px solid #232323 !important;
    padding-right: 20px !important;
  }
  .tabs__menu .tab__item.active,
  .tabs-menu .tab__item.active,
  .menu_sub .tab__item.active,
  .menu .tab__item.active,
  .tabs__menu * .tab__item.active,
  .tabs-menu * .tab__item.active,
  .menu_sub * .tab__item.active,
  .menu * .tab__item.active,
  .tabs__menu .tab-item_current.active,
  .tabs-menu .tab-item_current.active,
  .menu_sub .tab-item_current.active,
  .menu .tab-item_current.active,
  .tabs__menu * .tab-item_current.active,
  .tabs-menu * .tab-item_current.active,
  .menu_sub * .tab-item_current.active,
  .menu * .tab-item_current.active,
  .tabs__menu .menu__item.active,
  .tabs-menu .menu__item.active,
  .menu_sub .menu__item.active,
  .menu .menu__item.active,
  .tabs__menu * .menu__item.active,
  .tabs-menu * .menu__item.active,
  .menu_sub * .menu__item.active,
  .menu * .menu__item.active {
    border-bottom: solid #de5e1e !important;
  }
  .tabs__menu .tab-item_current,
  .tabs-menu .tab-item_current,
  .menu_sub .tab-item_current,
  .menu .tab-item_current,
  .tabs__menu * .tab-item_current,
  .tabs-menu * .tab-item_current,
  .menu_sub * .tab-item_current,
  .menu * .tab-item_current,
  .tabs__menu .tab-item_current *,
  .tabs-menu .tab-item_current *,
  .menu_sub .tab-item_current *,
  .menu .tab-item_current *,
  .tabs__menu * .tab-item_current *,
  .tabs-menu * .tab-item_current *,
  .menu_sub * .tab-item_current *,
  .menu * .tab-item_current * {
    background: #804020 !important;
    color: #171717 !important;
  }
  ul.topmenu {
    background: #202020 !important;
  }
  ul.topmenu li {
    position: relative;
    border-right: 1px solid #232323 !important;
  }
  ul.topmenu li a {
    display: block;
    position: absolute;
    height: 100%;
    width: 100%;
  }
  ul.topmenu li a.active {
    border-bottom: solid #de5e1e !important;
  }
  .similar-posts,
  .author-info,
  .layout__navbar,
  .comments_list,
  .live-broadcast,
  .postinfo-panel,
  .company_links,
  .corporate_blog,
  .post__footer,
  .post-type,
  .sidebar-block,
  .create_hub_promo_block,
  .block,
  .top_hub_invest,
  .similar-posts *,
  .author-info *,
  .layout__navbar *,
  .comments_list *,
  .live-broadcast *,
  .postinfo-panel *,
  .company_links *,
  .corporate_blog *,
  .post__footer *,
  .post-type *,
  .sidebar-block *,
  .create_hub_promo_block *,
  .block *,
  .top_hub_invest * {
    background: #202020 !important;
    color: #ccc;
  }
  .comments_list {
    padding-left: 0 !important;
    padding-top: 0 !important;
    margin: 15px 0 !important;
  }
  .comments_list > *,
  .comments_list .comments-list,
  .comments_list ul {
    margin: 20px !important;
  }
  .profile-header,
  .profile-header * {
    background: #232323 !important;
  }
}
