css:
@-moz-document domain("ultimate-guitar.com") {
a[href^="http://www.ringtonematcher.com/"],
script[src*="ad.yieldmanager"],
iframe[src*="ad"],
div[class^="ad"],
div[class$="banner"],
div[class^="comment"],
iframe,
div.p_h,
div.t_hs,
#advanced_search>table>tbody>tr:first-of-type,
#scSpeed_div,
.width-h>.header,
.footer_wrp,
section.b-tpw-tools,
section.b-suggest-correction,
img[src^="http://img.ultimate-guitar.com/_img/other/poll/"],
body>center>div>table>tbody>tr>td[valign="middle"] {
    display: none !important;
}
body {
    background-image: url(data:image/png;base64,) !important;
}

center>div, .page_border, .page_bcg, .t_b, .tb_ct, #cont>pre.js-tab-content {
    color: #eeeeee;
    background: none!important;
}

.t_dtde {
    color: #eeeeee;
    background: #0d0c18;
}

pre.js-tab-content > span {
    color: #fff158;
    font-weight: bold;
}
}
