css:
@-moz-document url-prefix("https://www.lewrockwell.com/") {
body{
    color: #ccc;
    background-color: #444;
}

a:link, .container a:link {
    color: #c8c8c8;
}
a:visited, .container a:visited {
    color: #999;
}
a:hover, .container a:hover {
    color: #ddd;
}

h1, h2, h3 {
    color: #d8d8d8;
}

.header.inner {
    border-bottom: 8px solid #999;
}

#menu-main-menu a, #menu-main-menu-1 a {
    color: #d8d8d8;
    background-color: #444 !important;
}
#menu-main-menu a:hover {
    background-color: #777 !important;
}

ul#menu-main-menu li a:hover {
    color: #d8d8d8;
}

.accordion a:hover, .accordion .active {
    background-color: #777 !important;
}
.sub-menu {
    background-color: #777 !important;
}
.sub-menu li a:link,
.sub-menu li a:visited {
    color: #ccc !important;
    background-color: #777;
}

.sub-menu li a:hover {
    color: #ddd !important;
 	background-color: #777 !important;
}

.header .sub-menu li a:hover {
    border-bottom:2px solid #282828;
}
    
.logo a, .logo a:hover, .logo a:visited {
    color: #d8d8d8;
}

.blog-header-title,
.single h2.blog-title a,
.single h2.blog-title a:visited {
    color: #d8d8d8;
}

.blog-content {
    color: #b0b0b0;
}

input[name="subscribe"], input[name="submit1"] {
    background-color: #4242CC;
}
input[name="submit1"]:hover {
    background-color: #1c1ca5;
}

/* Article */
h2.blog-title a:visited,
div.clearfix.post-info,
span.trc_rbox_header_span,
p.post-copyright {
    color: #ccc;
}

h3.blog-subtitle {
    color: #b5b5b5;
}

h5 {
    color: #bbb;
}
  
.social-sharing {
    background: #555;
    border: 1px solid #777;
    border-width: 1px 0;
}

.blog-content,
.blog-content p,
.blog-content p span {
    color: #ccc !important;
}

/* SideBar Specific */
.list-articles a:link,
.list-articles a:hover,
.list-articles a:visited {
    font-weight: normal;
}

ul.list-articles li a:focus,
ul.list-articles li a:link,
ul.list-articles li a:visited,
ul.list-articles li a:active {
    color: #ccc;
}

ul.list-articles li a:hover {
    color: #333;
    background: #f1f1f1;
}

.container h4 a:link,
.container h4 a:visited,
.container h4 a:hover {
    color: #ccc;
}

/* Authors */
.page-header h1 {
    color: #d8d8d8;
}
a.link-more.author-link {
    background-color: #393939;
    border: 1px solid #5a5a5a;
}

/* Contact */
.form .btn, .form .gform_button {
    background-color: #4242CC;
}

/* Archives */
.page-link:hover {
    color: #ddd;
}


}
