css:
@-moz-document url-prefix("http://daotao.dut.udn.vn/sv/") {
body {
    background: #fff;
    font-family: 'Calibri';
}
h1 {
    font-family: 'TUV Noh Carbone' !important;
    font-style: normal !important;
    font-size: 24px !important;
}
.page {
    margin: 0 0;
    width: 100%;
    height: 100%;
}
.header {
    background: #3a4f63;
    position: fixed;
    top: 0;
    left: 0;
    border-top: none;
    box-shadow: 0 0 5px #000;
}
.header h1 {
    color: #f93;
}
#TextBox1, #TextBox2 {
    width: 90px!important;
    height: 28px!important;
    color: #111;
    border: 1px solid #aaa;
    border-radius: 3px;
}
#TextBox2 {
    font-family: 'Arial Black'!important;
}
#TextBox1:focus,
#TextBox2:focus {
    background: #e9e9e9;
    color: #111;
    border: 1px solid #aaa;
}
ul .level2 .dynamic {
    box-shadow: 0 3px 3px rgba(0,0,0,0.4);
}
.page {
    border: 0px solid #000;
}
.main {
    margin: 110px 10px 70px 10px;
}
.footer {
    position: fixed;
    bottom: 0px;
    font-size: 36px;
    background: #3a4f63 url(http://i.imgur.com/KjAqPOn.png) center no-repeat;
    background-size: auto 46px;
    color: #fff;
    width: 100%;
    height: 48px;
    box-shadow: 0 0 5px #000;
    z-index: 99!important;
}
input[type=submit] {
    text-align: center!important;
    font-weight: bold!important;
    border-radius: 3px;
    height: 30px!important;
    min-width: 100px!important;
    transition: box-shadow 0.2s;
}
input[type=submit]:hover {
    box-shadow: 0 0 5px rgba(255,255,255,0.6);
}
select {
    height: 26px!important;
    width: 100%;
    border-radius: 3px;
    margin-top: 2px;
    margin-bottom: 2px;
}
.loginDisplay span {
    font-family: 'Calibri'!important;
    font-weight: bold;
    font-size: 16px!important;
    color: #f93;
    padding: 0 10px 0 0;
}
tr {
    height: 30px;
    line-height: 22px;
    border: 1px solid #032a46;
}
#MainContent_Panel11 {
    box-shadow: 0 5px 10px #000!important;
    padding: 10px;
    border: 1px solid #f50!important;
    border-radius: 5px;
}
.style47 {
    padding: 0 15px;
    font-size: 16px!important;
}
}
