css:
@-moz-document domain("dreamfilmhd.io") {
.bttgo, .bttgo .min {
visibility: hidden;
        display: none;
        position: absolute;
}
.container, body{
	background-color: #1e2124;
    border-radius: 0px !important;
}
.panel-default, .table-bordered, .list-group-item, .list-group-item.active, .panel-body, .panel-heading, .panel-footer, #search, div.dfoot.well, .table-bordered, .pull-right ul, #q, .table-bordered>thead>tr>th, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>tbody>tr>td, .table-bordered>tfoot>tr>td, hr {
	background-color: #282b30 !important;
    color: #b9babc !important;
    border-color: #36393e !important;
    border-radius: 0px !important;
    -webkit-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
	-moz-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
	box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
}
/*Browse*/
.row {
    margin-left: -15px;
    margin-right: -10px;
}
/*Search*/
.btn-primary {
    background-color: #3d7490;
    border-radius: 0px !important;
    border-color: #2d6987;
    color: #c4d6de;
}
.kapla {
    border-radius: 0px !important;
    background: rgba(10, 10, 10, 0.7);
}
/*Alert Boxes*/
.alert-warning {
    background-color: #4c3f2a;
    border-radius: 0px !important;
    border-color: #735721;
    color: #c09853;
}
.alert-info ,.alert-info .alert-link {
    background-color: #3d7490;
    border-radius: 0px !important;
    border-color: #2d6987;
    color: #c4d6de;
}
.input-group.input-group-lg {
    background-color: #3d7490;
    border-radius: 0px !important;
    border-color: #2d6987;
    color: #c4d6de;
    margin:0 0px 20px 0px !important;
}
/*Comment Section*/
#commentMainText, .dfoot, .input-group, .list-group, .pagination, .panel, nav {
    -webkit-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
	-moz-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
	box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
}
blockquote {
    border-left: 5px solid #30a4e7;
}
/*Pagination*/
.pagination>li>a, .pagination>li>span {
    color: #2fa4e7 !important;
    background-color: #282b30 !important;
    border: 1px solid #36393e !important;
    -webkit-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20);
	-moz-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20);
	box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20);
}
.pagination>.disabled>span, .pagination>.disabled>span:hover, .pagination>.disabled>span:focus, .pagination>.disabled>a, .pagination>.disabled>a:hover, .pagination>.disabled>a:focus {
    color: #b9babc !important;
    background-color: #282b30 !important;
    border-color: #36393e !important;
    -webkit-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20);
	-moz-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20);
	box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20);
}
/*Season Tabs*/
.sli>a:hover {
    background-color: #282b30 !important;
    color: #b9babc !important;
    -webkit-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
	-moz-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
	box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
}
.sli.active>a {
    background-color: #282b30 !important;
    color: #b9babc !important;
    -webkit-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
	-moz-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
	box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
}
/*Nav Bar*/
.navbar {
	background: #00659e; /* Old browsers */
	background: -moz-linear-gradient(top,  #00659e 0%, #017abf 60%, #00659e 100%); /* FF3.6-15 */
	background: -webkit-linear-gradient(top,  #00659e 0%,#017abf 60%,#00659e 100%); /* Chrome10-25,Safari5.1-6 */
	background: linear-gradient(to bottom,  #00659e 0%,#017abf 60%,#00659e 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
	/*filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#00659e', endColorstr='#00659e', GradientType=0); /* IE6-9 For some reason userstyles.org wont allow this */
    background-repeat: no-repeat;
    border-color: #012348;
    border-radius: 0px !important;
    -webkit-box-shadow: 0 1px 10px rgba(0,0,0,0.1);
    box-shadow: 0 1px 10px rgba(0,0,0,0.1);
}
.dropdown-menu {
    color: #b9babc !important;
    background-color: #178acc !important;
}
/*Suggestion Reel*/
.flickity-viewport {
    background-color: #282b30 !important;
    color: #b9babc !important;
    border-radius: 0px !important;
    border-color: #282b30 !important;
    -webkit-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
	-moz-box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
	box-shadow: inset 0px 0px 10px 1px rgba(0,0,0,0.20) !important;
}
}
