css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('.+mturk.+hit_scraper') {
html, body {
    margin:0;
}
.cpdeftault {
 padding:0;   
}
#ghost {
	width:100%;
    margin-top:10px;
	opacity:0.2;
    transition: all 0.5s;
}
#ghost .shine td {
 	border:none;   
}
#ghost:hover {
	opacity:1;   
}
#ghost button {
 display:none;   
}
#resultsTable > caption {
    display:none;
}
#resultsTable tr td:nth-child(3),
#ghost tr td:nth-child(3) {
	width:30%;
}
#resultsTable tr td:nth-child(3) a,
#ghost tr td:nth-child(3) a {
    display:block; height:28px;
    padding:3px;
    font-size:18px; font-weight:bold;
    color:#000;
    text-decoration:none;
}
#resultsTable tr td:nth-child(3) a:hover,
#ghost tr td:nth-child(3) a:hover {
	background-color: rgba(0,0,0,0.8);
    color:#EEE;
}
#resultsTable td,
#ghost td {
 position:relative !important;   
}
#resultsTable td:first-child div:first-child,
#resultsTable td:nth-child(2) div:first-child,
#ghost td:first-child div:first-child,
#ghost td:nth-child(2) div:first-child {
    position:absolute; 
    bottom:2px; right:2px;
}
#resultsTable td:first-child div:first-child button:hover,
#resultsTable td:nth-child(2) div:first-child button:hover,
#ghost td:first-child div:first-child button:hover,
#ghost td:nth-child(2) div:first-child button:hover {
    background-color:#000;
    color:#FFF;
    cursor:hand; 
}
#resultsTable td div:last-child,
#ghost td div:last-child {
	display:block;
	width:100%; 
}
#resultsTable td div a,
#ghost td div a {
 	display:block;
    height:28px; width: calc(100% - 6px);
    text-decoration:none;
    padding:3px;
}
#resultsTable td div a:hover,
#ghost td div a:hover {
	background-color: rgba(0,0,0,0.8);
    color:#EEE;
}
.tooltip {
    position:relative;
      display:block !important;
    box-shadow: none;
    background-color:transparent;
    width:80px !important;
    right:0px;
    padding:0px;
    transform: translateY(0%);
  }
  .tooltip p:first-child {
    display:none !important;
  }
  .tooltip meter {
    height:8px;
    width: 100%;
  }
  .tooltip .ffma,
  .tooltip .ffmb,
 meter:before,
meter:after {
    font-size:7px !important;
    text-shadow: 0px 0px 1px #ffffff;
  }
  .tooltip .ffma {
    padding-right:1px;
  }
  .tooltip .ffmb {
    padding-left:1px;
  }
  .static.toLink {
    font-size:0px;
  }
  .static.toLink:hover {
    cursor: arrow-right;
  }
  .static.toLink:before {
    display:none;
    
  }
  .static.toLink ul {
    padding-left:0;
  }
  .static.toLink ul li {
    display:none !important;
  }
  .static.toLink ul:before {
    display:block;
    content:"Not Available"; 
  }
}
