css:
@-moz-document domain("fitgirl-repacks.site") {
*::-moz-selection {
    background: #f887ff;
}
*::selection {
    background: #f887ff;
    background-color: #f887ff !important;
}
*::-moz-placeholder {
    color: #FFF;
}
.secondary-navigation li:hover > a,
.secondary-navigation li.focus > a {
    background-color: #5C5C5C;
    color: #fff;
}
.site-footer,
.site-content .entry-header,
.site::before,
.site-content .entry-content,
.site-content .entry-summary,
.page-content,
body.custom-background,
.site-header,
input,
textarea,
button,
.button,
input[type="button"],
input[type="reset"],
input[type="submit"] {
    background-color: #303030;
}
.site-navigation li,
.secondary-navigation li {
    border-top: 1px solid rgb(29, 29, 29);
}
.secondary-navigation {
    border-bottom: 1px solid rgb(29, 29, 29);
}

/*Replace a lot of green with pink*/
.search-toggle,
.search-toggle.active,
.primary-navigation li:hover > a,
.primary-navigation li.focus > a,
.entry-meta .tag-links a:hover,
.widget input[type="button"]:hover,
.widget input[type="button"]:focus,
.widget input[type="reset"]:hover,
.widget input[type="reset"]:focus,
.widget input[type="submit"]:hover,
.widget input[type="submit"]:focus,
button:hover,
button:focus,
.button:hover,
.button:focus,
input[type="button"]:hover,
input[type="button"]:focus,
input[type="reset"]:hover,
input[type="reset"]:focus,
input[type="submit"]:hover,
input[type="submit"]:focus,
.primary-navigation li:hover > a,
.primary-navigation li.focus > a,
.search-toggle,
.site-navigation .current_page_item > a,
.site-navigation .current_page_ancestor > a,
.site-navigation .current-menu-item > a,
.site-navigation .current-menu-ancestor > a,
.entry-meta .tag-links a:hover::before,
#lbjoin,
.primary-navigation ul ul,
.secondary-navigation ul ul,
.secondary-navigation ul ul a:hover,
.secondary-navigation ul ul li.focus > a,
.search-field,
.donatebutton,
#lbjoin2 {
    background-color: #f887ff !important;
}
.search-box {
    background-color: #1d1d1d;
}
#main,
.main-content,
.content-area,
.content-sidebar {
    background-color: #222222 !important;
}
.su-spoiler-style-fancy > .su-spoiler-title {
    border-bottom: 1px solid #303030;
    background: #222;
}
.su-spoiler-style-fancy {
    border: none;
    background: #3E3E3EB3;
    color: #8C8C8C;
}
table,
th,
td {
    border: 1px solid rgb(34, 34, 34);
}
.site-content .entry-meta {
    background-color: transparent;
}
.cat-links a:hover,
.entry-meta a:hover,
a:active,
a:hover,
.site-info a:hover,
.entry-title a:hover,
.widget a:hover,
.comment-author a:hover,
.comment-list .pingback a:hover,
.comment-list .trackback a:hover,
.comment-metadata a:hover,
.post-navigation a:hover,
.image-navigation a:hover {
    color: #f887ff;
}
body,
button,
input,
select,
.entry-title a,
textarea,
.cat-links a,
.entry-meta a,
a {
    color: #8C8C8C;
}
.widget button,
.widget .button,
.widget input[type="button"],
.widget input[type="reset"],
.widget input[type="submit"],
.widget button,
.widget .button,
.widget input[type="button"],
.widget input[type="reset"],
.widget input[type="submit"] {
    background-color: rgba(255, 255, 255, 0.1);
}
.search-toggle:hover,
.search-toggle.active {
    background-color: #faa4ff;
}
.site-navigation .current_page_item > a,
.site-navigation .current_page_ancestor > a,
.site-navigation .current-menu-item > a,
.site-navigation .current-menu-ancestor > a,
.primary-navigation li:hover > a,
.primary-navigation li.focus > a {
    color: #222;
}
.paging-navigation .page-numbers.current {
    border-top: 5px solid #f887ff;
}
.paging-navigation a:hover {
    border-top: 5px solid #faa4ff;
    color: #8C8C8C;
}
.list-view .site-content .hentry {
    padding-top: 6px;
}
.hentry {
    margin: 0 auto 6px;
}
.site-content .entry-header,
.site-content .entry-content,
.site-content .entry-summary,
.site-content .entry-meta,
.page-content {
    max-width: 950px;
}
.content-area,
.content-sidebar {
    padding-top: 1%;
}
#potatoid {
    background-color: #303030;
    border: 1px solid #f887ff;
}
}
