css:
@namespace url(http://www.w3.org/1999/xhtml);

/*通用*/
@-moz-document url-prefix("http://www.guokr.com"),url-prefix("https://www.guokr.com"){
  /*顶部导航栏*/  
  .gh-wrap{
    height: 44px !important;
  }
  .gheader-new{
    height: auto !important;
  }
  .gheader-new .gfl a{
    height: 24px !important;
    line-height: normal !important;
  }
  .gheader-wrap *{
    font-size: 17px!important;
  }
  .gh-notice.gfr a + div{
    width: auto !important;
  }
  .gh-notice.gfr ul a{
    font-size: 14px!important;
  }
  /*二级导航栏*/
  .sub-nav-wrap{
    width: 1200px !important;
  }
  .sub-nav-link a {
    font-size: 17px !important; 
    margin: auto !important;
    width: 70px !important;
  }
  .gh-search p{
    height: auto !important;
  }
  #searchGroup .gh-search-txt{
    font-size: 14px !important;
    height: 24px !important;
  }
  #searchGroup .gnicon-search{
    margin-top: 4px !important;
  }
}

/*首页*/
@-moz-document url("http://www.guokr.com/"),url("https://www.guokr.com/"){
  /*拉宽*/
  .grow{
    width: 1200px !important;
  }
    
  /*左侧*/  
  .main {
    width: 850px !important;
  } 
  .contents *:not(.stress) {
    font-size: 15px !important;
  }
    
  /*推荐*/  
  .focus{
    width: 410px!important;
    height: 250px!important;
  } 
  .focus-content a,.focus-content img {
    height: auto!important;
    width: 100%!important;
  } 
  .recos-article{
    width: 400px!important;
  }
  .stress{
    height: 32px !important;
    margin: 0 !important;
    width: 100% !important;
  }
  .stress a{
    font-size: 18px!important;
    float: left!important;
  }  
    
  /*问答热帖十五言*/  
  .contents.gclear>div{
    width: 50% !important;
  } 
  .contents.gclear>div ul{
    height: 320px !important;
  }  
  .contents.gclear>div li{
    height: 30px !important;
  } 
  .contents.gclear>div li[class]{
    height: 115px !important;
  }
  .contents.gclear>div .img{
    height: auto !important;
    width: auto!important;
  } 
  .contents.gclear>div img{
    height: 105px !important;
    width: auto!important;
  }  
  .contents.gclear>div .cont>*{
    height: 50% !important;
  }
    
  /*右侧*/ 
  .side {
    width: 330px !important;
  }
  .side p,.side a:not(.weixin):not(.weibo) {
    font-size: 15px;
  }
  .user-info a{
    max-width: 100px !important;
  }
  .user-asks .gellipsis {
    overflow: hidden;
    width: 300px;
  }
  .side li {
    height: 30px !important;
  }
  .side .pt-txt .pt-txt-d {
    height: auto !important;
    width: 252px !important;
  }
  .side #newGroups li {
    height: 70px !important;
    margin-bottom:0 !important;
  } 
  .side-zone-advert{
    display: none !important;
  }
}

/*果壳问答首页*/
@-moz-document url-prefix("http://www.guokr.com/ask"),url-prefix("https://www.guokr.com/ask"){
  .gwrap {
    width: 1200px !important;
  }
   .gmain {
     width: 850px!important;
  }
  .gmain :not(h3) {
    font-size: 16px !important;
  }
  .gside * {
    font-size: 16px !important;
  }
}

/*果壳小组主页*/
@-moz-document url-prefix("http://www.guokr.com/group/"),url-prefix("https://www.guokr.com/group/"){
  .gwrap{
    width: 1200px !important;
  }
  .group-info-detail :not(h3),.gbbcode-content p {
    font-size: 14px !important;
  }
  .gmain{
    width: 850px !important;
  }
  .gmain *{
    font-size: 16px !important;
  }
  ul.gclear li{
    width: 68px !important;
  }
  h2{
    font-size: 16px !important;
    font-weight: 400 !important;
  }
  .gside a {
    font-size: 14px !important;
  }
  .rank-group a{
    width: 45px !important;
  }
}

/*科学人主页*/
@-moz-document url-prefix("http://www.guokr.com/scientific/"),url-prefix("https://www.guokr.com/scientific/"){
 .container{
   width: 1200px !important;
  }
 .article-list .article {
   width: 359px !important;
  }
  .article:nth-of-type(3n+1){
    left: 800px !important;
  }
  .article:nth-of-type(3n+2){
    left: 400px !important;
  }
  .article:nth-of-type(3n+3),.article:first-child{
    left: 0px !important;
  }
  .article:nth-of-type(3){
    left: 820px !important;
  }
}

/*科学人内容页*/
@-moz-document url-prefix("http://www.guokr.com/article/"),url-prefix("https://www.guokr.com/article/"){
  .container{
    width: 1200px !important;
  }
  .main {
    width: 850px !important;
  }
  .main :not(h1):not(h2){
    font-size: 16px !important;
  }
  .main li{
    font-size: 14px !important;
  }
  #mceu_11{
    width: auto !important;
  }
  .side{
    margin: 50px 0 0 50px !important;
  }
}
/*果壳小组内容页，果壳问答内容页*/
@-moz-document url-prefix("http://www.guokr.com/post/"),url-prefix("http://www.guokr.com/question/"),url-prefix("https://www.guokr.com/post/"),url-prefix("https://www.guokr.com/question/"){
  .gwrap{
    width: 1200px !important;
  }
  .gmain{
    width: 850px !important;
  }
  .gmain *:not(h1){
    font-size: 16px !important;
  }
  .gside{
    margin-right: 20px !important;
  }
  .gside h2{
    font-size: 16px !important;
  }
  .gside li a{
    font-size: 14px !important;
  }
  .toolBar-wrap,.tool-service{
    margin-left: 600px !important;
  }
  #mceu_11{
    width: auto !important;
  }
}
/*果壳问答内容页*/
@-moz-document url-prefix("http://www.guokr.com/question/"),url-prefix("https://www.guokr.com/question/"){
  h1{
    font-size: 22px !important;
  }
  .answer-r{
    width: calc(100% - 40px) !important;
  }
  .gside li{
    font-size: 14px !important;
  }
}
