css:
@-moz-document domain("twitter.com") {
.logged-out, .logged-in{background:#ae0000 url("/*[[bkgimg]]*/") center center repeat-y;background-size:100% auto;}
.stream-item:not(.no-header-background-module){background: rgba(255, 255, 255, 0.8);border-left: 1px solid #FFC107;border-right: 1px solid #FFC107;}
.tweet:hover{background: rgba(255, 255, 255, 0.9)}
.home-tweet-box, .LiveVideo-tweetBox, .RetweetDialog-commentBox, .WebToast-box--altColor{background:#016e01}
.new-tweets-bar{background:#016e01;}
.new-tweets-bar:hover{background:#1b8d1b;}
.tweet{border-bottom: 1px solid #FFC107;}
.Trends {background-color: rgba(255, 255, 255, 0.8); border: 1px solid #FFC107;}
.module .flex-module {background-color:rgba(255, 255, 255, 0.8)}
.DashboardProfileCard{background-color:rgba(255, 255, 255, 0.8)}
.u-textUserColor, .u-textUserColorHover:active, .u-textUserColorHover:hover, .u-textUserColorHover:hover .ProfileTweet-actionCount, .u-textUserColorHover:focus {color: #be0101 !important;}
.Footer.module .flex-module {background-color:rgba(255, 255, 255, 0.8)}
.wtf-module h3{color:#be0101}
.username, .time, .time a, .metadata, .metadata button.btn-link, .metadata a{color:#000000}
.Footer-item, .Footer-link{color:#000000}
.TwitterCard .SummaryCard-destination{color:#000000} 
a, .btn-link, .btn-link:focus, .icon-btn, .pretty-link b, .pretty-link:hover s, .pretty-link:hover b, .pretty-link:focus s, .pretty-link:focus b, .metadata a:hover, .metadata a:focus, .account-group:hover .fullname, .account-group:focus .fullname, .account-summary:focus .fullname, .message .message-text a, .stats a strong, .plain-btn:hover, .plain-btn:focus, .dropdown.open .user-dropdown.plain-btn, .open > .plain-btn, #global-actions .new:before, .module .list-link:hover, .module .list-link:focus, .stats a:hover, .stats a:hover strong, .stats a:focus, .stats a:focus strong, .find-friends-sources li:hover .source, .stream-item a:hover .fullname, .stream-item a:focus .fullname, .stream-item .view-all-supplements:hover, .stream-item .view-all-supplements:focus, .tweet .time a:hover, .tweet .time a:focus, .tweet .details.with-icn b, .tweet .details.with-icn .Icon, .stream-item:hover .original-tweet .details b, .stream-item .original-tweet.focus .details b, .stream-item.open .original-tweet .details b, .client-and-actions a:hover, .client-and-actions a:focus, .dismiss-btn:hover b, .tweet .context .pretty-link:hover s, .tweet .context .pretty-link:hover b, .tweet .context .pretty-link:focus s, .tweet .context .pretty-link:focus b, .list .username a:hover, .list .username a:focus, .list-membership-container .create-a-list, .list-membership-container .create-a-list:hover, .card .list-details a:hover, .card .list-details a:focus, .card .card-body:hover .attribution, .card .card-body .attribution:focus, .new-tweets-bar, .remove-background-btn, .stream-item-activity-notification .latest-tweet .tweet-row a:hover, .stream-item-activity-notification .latest-tweet .tweet-row a:focus, .stream-item-activity-notification .latest-tweet .tweet-row a:hover b, .stream-item-activity-notification .latest-tweet .tweet-row a:focus b{color:#000000}
.username, .time, .time a, .metadata, .metadata button.btn-link, .metadata a{color:#be0101}
.username:hover, .time:hover, .time a:hover, .metadata:hover, .metadata button.btn-link:hover, .metadata a:hover{color:#be0101 !important;}
.ProfileTweet-actionButton, .ProfileTweet-actionButtonUndo{color:#be0101}
.ProfileTweet-actionCount {color: #be0101;}
.tweet .dismiss-btn {color: #be0101;}
.tweet .dismiss-btn .Icon--close {color: #be0101;}
.tweet .context a {color: #be0101;}
.Icon--promoted {color: #be0101;}
.top-timeline-tweetbox .timeline-tweet-box {border: 1px solid #be0101;}
.ProfileCardStats-statLabel{color:#217702;}
.Trends .context-trend-item .trend-item-stats {color: #217702;}
.DashboardProfileCard-screennameLink { color: #217702;}
.tweet-counter {color: #FFC107;}
.icon-btn {color: #FFC107;}
.username s, .account-group-inner s {color: #be0101;}
.u-bgUserColor, .u-bgUserColorHover:hover, .u-bgUserColorHover:focus{background-color: #be0101 !important;}
.account-summary .dismiss{color: #be0101;}
.twitter-hashtag.pretty-link.js-nav{color: #be0101;}
.Trends .trend-location {color: #be0101;}
.TweetTextSize .twitter-hashtag b, .TweetTextSize .twitter-atreply b{color: #be0101;}
.TweetTextSize .twitter-hashtag b:hover, .TweetTextSize .twitter-atreply b:hover{color: #be0101;}
}
