css:
@-moz-document regexp("http://www.neopets.com/(?!guilds/guild.phtml.*|userlookup.phtml.*|~.*).*") {
/* Change the following variables for custom colors! Values MUST be RGB!!! */

:root {
    --primaryColor: 91, 193, 190;
    --secondaryColor: 67, 173, 170;
    --highlightColor: 255, 255, 255;
    --shadowColor: 0, 0, 0;
}
/* Don't touch anything below here unless you know what you're doing! */
/* Don't freak out, this huge blob is just an imagehost-free way to bring you a background image.
   After Photobucket kicked the... bucket... I decided this was a better solution. */

body#neobdy {
    background: url(data:image/png;base64,) repeat !important;
}
.brand-mamabar,
#ad-slug-wrapper,
.ad_wrapper_fixed,
.ad_wrapper,
#pushdown_banner_btf,
.adBox,
#pw-oop-bottom_rail,
#ban,
#footer,
hr,
.eventIcon > b,
form[action="process_topic.phtml"] .contentModuleHeaderAlt,
form[action="process_topic.phtml"] td[width="150"],
div[style="position: relative; float: right; width: 160px; height: 630px; margin-left: 5px;"],
img[src$="signup-banner.png"],
div[style="float: right; width: 300px; margin-left: 4px;"],
div[style="float: right; margin-left: 7px; text-align: right;"] {
    display: none;
}
* {
    font-size: 12px;
    box-sizing: content-box !important;
    /* Can you even believe this is necessary? I was fighting for a while, trying to figure out why Firefox was using the old IE6 box model. */
}
a {
    color: rgba(var(--primaryColor), 1.0) !important;
}
:focus {
    outline: none;
}
/* ########## Header and Navigation ########## */

#header {
    width: 100% !important;
    height: 60px !important;
    position: fixed !important;
    top: 0 !important;
    left: 0 !important;
    background-image: none !important;
    background-color: rgba(var(--primaryColor), 1.0) !important;
    z-index: 99 !important;
    border-bottom: 5px solid rgba(var(--secondaryColor), 1.0);
}
#navigation img {
    visibility: hidden;
}
#template_nav img {
    display: block;
    padding: 0 !important;
}
#template_nav .nav_image {
    position: absolute;
}
#template_nav li:nth-of-type(1) a:before {
    content: "my account";
}
#template_nav li:nth-of-type(2) a:before {
    content: "customise"
}
#template_nav li:nth-of-type(3) a:before {
    content: "games"
}
#template_nav li:nth-of-type(4) a:before {
    content: "explore"
}
#template_nav li:nth-of-type(5) a:before {
    content: "news"
}
#template_nav li:nth-of-type(6) a:before {
    content: "boards"
}
#template_nav li:nth-of-type(7) a:before {
    content: "shops"
}
#template_nav li:nth-of-type(8) a:before {
    content: "nc mall"
}
#template_nav li:nth-of-type(9) a:before {
    content: "premium"
}
#template_nav li:nth-of-type(9) {
    right: 0 !important;
}
#template_nav li:nth-of-type(8) {
    right: 10% !important;
}
#template_nav li:nth-of-type(7) {
    right: 20% !important;
}
#template_nav li:nth-of-type(6) {
    right: 30% !important;
}
#template_nav li:nth-of-type(5) {
    right: 40% !important;
}
#template_nav li:nth-of-type(4) {
    right: 50% !important;
}
#template_nav li:nth-of-type(3) {
    right: 60% !important;
}
#template_nav li:nth-of-type(2) {
    right: 70% !important;
}
#template_nav li:nth-of-type(1) {
    right: 80% !important;
}
#header a[href="/index.phtml"] {
    z-index: 100 !important;
    position: absolute;
    top: 0;
    left: 0;
    height: 60px;
    width: 10%;
}
#header a[href="/index.phtml"]:before {
    content: "home"
}
#header a[href="/index.phtml"] img {
    display: none;
}
.nav_image a:first-of-type,
#header a[href="/index.phtml"] {
    color: rgba(var(--highlightColor), 1.0) !important;
    display: block;
    height: 10px;
    padding: 25px 0;
    text-align: center;
}
.nav_image {
    border-bottom: 5px solid rgba(var(--secondaryColor), 1.0);
}
.nav_image {
    width: 10% !important;
    background-color: rgba(var(--primaryColor), 1.0) !important;
    display: block;
    float: right;
}
#template_nav .dropdown a:before {
    content: "" !important;
}
ul.dropdown {
    border: 0px !important;
    background: none !important;
    padding: 10px !important;
    margin: 0 10px !important;
    position: relative;
    left: 0px;
    top: 0;
    text-align: left !important;
}
.dropdown li a {
    display: block !important;
    border: 0 !important;
    font-weight: normal;
    color: rgba(var(--highlightColor), 1.0) !important;
    padding: 4px 2px !important;
    text-align: left !important;
}
#templateLoginPopup {
    position: fixed !important;
    top: 120px !important;
    right: 10px !important;
    background: rgba(var(--highlightColor), 1.0);
    background: linear-gradient(to bottom, rgba(var(--primaryColor), 1.0) 0%, rgba(var(--primaryColor), 1.0) 36px, rgba(var(--highlightColor), 1) 36px, rgba(var(--highlightColor), 1) 100%);
    box-shadow: 0 0 10px rgba(var(--shadowColor), 0.6);
    height: 220px !important;
}
#templateLoginPopup td {
    vertical-align: middle !important;
}
#templateLoginPopup a[href="/account/passwordreset.phtml"] {
    display: none;
}
#navigation {
    width: 100% !important;
    position: absolute;
    right: 0;
    top: 0;
}
#navigation td {
    width: 100% !important;
    float: right;
}
#main {
    position: absolute;
    top: 150px;
    background: 0;
    border: 0;
    width: 1018px;
    left: 50%;
    margin: 0 0 0 -509px;
}
.topLink {
    margin: 0 20px 20px 0 !important;
}
.user {
    position: fixed;
    top: 80px;
    right: 5px;
    z-index: 96 !important;
    color: rgba(var(--highlightColor), 1.0) !important;
}
.dropdown li {
    margin-left: -19px;
}
.dropdown {
    overflow: hidden;
}
.eventIcon {
    position: fixed !important;
    top: 65px !important;
    left: 0px !important;
    width: 100% !important;
    padding: 10px !important;
    background: rgba(var(--shadowColor), 0.7) !important;
    color: rgba(var(--highlightColor), 1.0) !important;
    z-index: 1 !important;
    height: 24px !important;
}
.marqueeSlot {
    background-color: rgba(var(--primaryColor), 1.0) !important;
}
.marqueeSlotOn {
    background-color: rgba(var(--secondaryColor), 1.0) !important;
}
.marqueeSlot a,
.marqueeSlotOn a {
    color: rgba(var(--highlightColor), 1.0) !important;
    background: 0 !important;
}
#nst {
    position: fixed;
    top: 80px;
    left: 50%;
    margin-left: -60px !important;
    color: rgba(var(--highlightColor), 1.0) !important;
    z-index: 150 !important;
    max-width: 120px !important;
    font-size: 8pt !important;
    text-align: center;
}
/* ########## Sidebar ########## */

.sidebarTable {
    width: 202px !important;
    margin-right: 20px !important;
}
.sidebar,
.sidebar *:not(.dailies_bg_map) {
    background: 0 !important;
    border: 0 !important;
    font-size: 12px !important;
}
.sidebarHeader:first-of-type,
.activePet .pointer {
    display: none;
}
.sidebar .activePet img {
    border-radius: 75px;
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
    margin-bottom: 10px;
}
form br,
.neofriend br {
    display: none !important;
    height: 0 !important;
}
.sidebarModule:not(:last-of-type) .neofriend {
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
    padding: 0;
}
form[onsubmit="search_handle(this);"] {
    padding: 0 !important;
    border: 0 !important;
    width: 202px !important;
}
.sidebar input[name="q"] {
    background: rgba(var(--highlightColor), 1.0) !important;
    padding: 10px !important;
    float: left;
    width: 139px !important;
    border: 0 !important;
}
.sidebar input[value="Go!"] {
    background: rgba(var(--primaryColor), 1.0) !important;
    padding: 10px !important;
    color: rgba(var(--highlightColor), 1.0);
    float: right;
    width: 23px !important;
    border: 0 !important;
    margin: 0 !important;
}
.sidebar input[name="q"]:focus,
.sidebar input[value="Go!"]:focus {
    border: 0 !important;
}
.neofriend {
    padding: 10px 0 0;
}
.neofriend .button-link-online {
    background: rgba(var(--primaryColor), 1.0) !important;
    border: 1px solid rgba(var(--shadowColor), 0.2) !important;
}
.neofriend a {
    display: inline-block;
    margin: 3px 0;
}
.neofriend td {
    padding: 10px 0 0 !important;
}
/* ########## Styling for content boxes and their infinite children ########## */
.contentModule {
    background: rgba(var(--highlightColor), 1.0);
    border: 0;
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
    margin: 0 20px 20px 0 !important;
    display: block;
}
.contentModuleTable {
    background: 0;
    border: 0;
}
.contentModuleHeader,
.contentModuleHeaderAlt {
    background: rgba(var(--primaryColor), 1.0) !important;
    padding: 10px !important;
    font-size: 12px !important;
    color: rgba(var(--highlightColor), 1.0);
}
.moreLink,
.moreLinkAlt {
    border: 0;
    background: rgba(var(--highlightColor), 0.4) !important;
    font-size: 12px !important;
    padding: 3px;
}
.contentModuleContent {
    padding: 10px;
}
.moreLinkBottom {
    border: 0;
    background: rgba(var(--primaryColor), 0.4);
    padding: 10px !important;
    font-size: 12px !important;
    height: 12px;
    overflow: hidden;
}
td[width="7"],
td[height="7"] {
    display: none;
}

/* ########## Buttons, inputs, and dropdowns ########## */

input[type="text"],
input[type="password"],
textarea {
    background: rgba(var(--highlightColor), 1.0) !important;
    border: 1px solid rgba(var(--shadowColor), 0.2) !important;
    padding: 10px !important;
}
input[type="text"]:focus,
input[type="password"]:focus,
textarea:focus {
    border: 1px solid rgba(var(--primaryColor), 0.8) !important;
}
input[type="submit"],
input[type="button"],
input[type="reset"] {
    background: rgba(var(--primaryColor), 1.0);
    border: 1px solid rgba(var(--primaryColor), 1.0) !important;
    padding: 10px !important;
    color: rgba(var(--highlightColor), 1.0);
    margin-top: 10px;
}
select {
    background-color: rgba(var(--primaryColor), 0.4);
    background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAGCAYAAAD68A/GAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAHZJREFUeNpszssKg0AMheHRAS910ZUVEaF02/d/G0XfQKbqQoqXPxBhEAPfQMJhEmuM+SLCaO6rxNvyPPDCjukSKlDBSfCHWIcrZg3lqDGgtzp0SDW8IJF1Ou9kW+CtCfHBU3u5ucEmjfWCu/6Q4Y/2DEkdAgwAJsgWecqUcAwAAAAASUVORK5CYII=);
    background-repeat: no-repeat;
    background-position: calc(100% - 10px) center;
    padding: 10px 30px 10px 10px !important;
    border: 0 !important;
    -webkit-appearance: none;
    -moz-appearance: none;
    color: rgba(var(--secondaryColor), 1.0) !important;
}
}

@-moz-document url-prefix("http://www.neopets.com/index.phtml") {
div[style="margin-top:7px;"],
table[style="margin-top: 7px;"] {
    margin: 0 !important;
}
.content a[href*="/petlookup.phtml"] div {
    margin: 0 0 10px 0 !important;
    border: 0 !important;
    padding: 0 !important;
}
.content a[href*="/petlookup.phtml"] img {
    border-radius: 75px;
}
.content a[href*="/petlookup.phtml"] + br {
    display: none;
}
.newItemHeader {
    padding: 10px !important;
    text-align: center;
}
.newItemHeader a {
    color: rgba(var(--shadowColor), 1.0) !important;
    font-weight: normal;
}
.newItems {
    padding: 10px !important;
    width: 247px !important;
}
.neopointItem,
.otherItem {
    padding: 10px !important;
    margin: 0 10px 10px 0 !important;
    border: 1px solid rgba(var(--shadowColor), 0.2) !important;
    border-radius: 55px;
    float: left;
}
.latestGamesImg,
.spotlight img {
    border: 0 !important;
    border-radius: 30px;
    margin: 0 10px 0 0 !important;
}
.latestGamesBox,
.spotlight,
.spotlight + td {
    padding: 0 !important;
}
.latestGamesBox .medText {
    display: block;
    margin-top: 10px !important;
}
td[style*="background-color"] {
    padding: 0 0 10px 0 !important;
    background: 0 !important;
}
.medText[style*="background-color"] {
    text-align: right;
}
.sf[style="background-color: #EFEFEF; padding: 2px; text-align: center;"] {
    padding: 0 !important;
    background: 0 !important;
}
}

@-moz-document url-prefix("http://www.neopets.com/neoboards/") {
.content > div > table,
#boards_table {
    background: rgba(var(--highlightColor), 1.0);
}
form[action="process_topic.phtml"] table {
    border: 0 !important;
    width: 628px !important;
    background: none !important;
}
form[action="process_topic.phtml"] td {
    padding: 0 !important;
}
form[action="process_topic.phtml"] table table td {
    width: 62px !important;
    padding: 10px 0 !important;
}
#boards_table > tbody > tr:last-child {
    background: rgba(var(--highlightColor), 1.0) !important;
}
}

@-moz-document url("http://www.neopets.com/neoboards/"), url-prefix("http://www.neopets.com/neoboards/index.phtml") {
table[style="border: 1px solid #000000;"] {
    background: rgba(var(--highlightColor), 1.0);
    border: 0 !important;
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
}
table[style="border: 1px solid #000000;"] td {
    border: 0 !important;
    padding: 10px !important;
}
table[style="border: 1px solid #000000;"] tr:nth-child(odd) {
    background: rgba(var(--shadowColor), 0.08);
}
table[style="border: 1px solid #000000;"] tr>td:first-child {
    width: 50px !important;
}
table[style="border: 1px solid #000000;"] tr>td:first-child img {
    border-radius: 25px !important;
}
}

@-moz-document url-prefix("http://www.neopets.com/neoboards/boardlist.phtml") {
table[style="border-top: 1px solid #000000; border-left: 1px solid #000000; border-right: 1px solid #000000;"],
table[style="border-top: 1px solid #000000; border-left: 1px solid #000000; border-right: 1px solid #000000;"] td,
.contentModuleHeaderAlt {
    border: 0 !important;
}
table[style="border-top: 1px solid #000000; border-left: 1px solid #000000; border-right: 1px solid #000000;"] tr:not(:first-of-type) td,
table[style="border-top: 1px solid #000000; border-left: 1px solid #000000; border-right: 1px solid #000000;"] td div {
    background: 0 !important;
}
table[style="border-top: 1px solid #000000; border-left: 1px solid #000000; border-right: 1px solid #000000;"] tr:nth-of-type(odd) {
    background: rgba(var(--shadowColor), 0.08);
}
table[style="border-top: 1px solid #000000; border-left: 1px solid #000000; border-right: 1px solid #000000;"] {
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
}
table[style="border-top: 1px solid #000000; border-left: 1px solid #000000; border-right: 1px solid #000000;"] td {
    margin: 0 !important;
    padding: 10px !important;
    vertical-align: middle;
}
table[style="border-top: 1px solid #000000; border-left: 1px solid #000000; border-right: 1px solid #000000;"] tr:last-child {
    display: none !important;
}
table[style="white-space: nowrap;"] td {
    margin-top: 10px;
    visibility: hidden;
    display: block;
}
table[style="white-space: nowrap;"] * {
    font-size: 16px !important;
    visibility: visible;
}
}

@-moz-document url-prefix("http://www.neopets.com/neoboards/topic.phtml") {
#boards_table {
    border: 0 !important;
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
}
#boards_table * {
    border: 0 !important;
}
.topicSmall[width="40%"] {
    visibility: hidden;
}
.topicSmall[width="40%"] a {
    visibility: visible;
}
#boards_table img[src*="pets.neopets.com"] {
    border-radius: 25px !important;
}
.topicAuthor {
    padding: 10px !important;
}
#boards_table td[style="font-size: 10px; border-bottom: 1px solid rgb(0, 0, 0); background-color: rgb(183, 183, 183);"],
#boards_table td[style="font-size: 10px; border-bottom: 1px solid #000000; background-color: #B7B7B7;"] {
    height: 0px !important;
    overflow: hidden;
    border-top: 1px dotted rgba(var(--shadowColor), 0.1) !important;
    background: rgba(var(--shadowColor), 0.02) !important;
    padding: 0 !important;
}
#boards_table td[style="font-size: 10px; border-bottom: 1px solid rgb(0, 0, 0); background-color: rgb(183, 183, 183);"] img,
#boards_table td[style="font-size: 10px; border-bottom: 1px solid #000000; background-color: #B7B7B7;"] img {
    display: none;
}
#boards_table > tbody > tr:last-of-type,
#boards_table > tbody > tr:first-of-type {
    background: rgba(var(--primaryColor), 0.4) !important;
}
textarea[name="message"] {
    margin: 0 0 10px !important;
    border: 0 !important;
    padding: 10px !important;
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
    font-family: Tahoma, Helvetica, Arial, sans-serif;
    resize: vertical;
    width: 610px !important;
}
textarea[name="message"]:focus {
    box-shadow: 0 0 4px rgba(var(--primaryColor), 1.0);
}
}

@-moz-document url-prefix("http://www.neopets.com/neoboards/create_topic.phtml") {
form > table:first-of-type input {
    width: 628px !important;
    margin: 0 !important;
}
form > table:nth-of-type(2) {
    width: 628px !important;
}
form > table table td[valign="top"]:nth-of-type(2) {
    display: none;
}
select[name="board_id"] {
    border: 0 !important;
    width: 200px !important;
    z-index: 6;
}
input[name="boardType"]+table {
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
    background: rgba(var(--highlightColor), 1.0) !important;
    width: 644px !important;
}
input[name="boardType"]+table tr {
    display: inline-block;
    float: left;
}
input[name="boardType"]+table tr:first-of-type,
input[name="boardType"]+table tr:first-of-type * {
    width: 200px !important;
}
input[name="boardType"]+table tr:last-of-type,
input[name="boardType"]+table tr:last-of-type * {
    width: 422px !important;
}
input[name="topic_title"] {
    padding: 11px !important;
    border: 0 !important;
    width: 422px !important;
    z-index: 6;
}
input[name="topic_title"]:focus {
    border: 0 !important;
}
div[style="font-weight:bold;"] {
    display: none;
}
.content td[valign="top"] {
    padding: 0 !important;
}
textarea[name="message"] {
    margin: 0 0 10px !important;
    border: 0 !important;
    padding: 10px !important;
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
    resize: vertical;
    width: 624px;
    font-family: Tahoma, Helvetica, Arial, sans-serif;
}
textarea[name="message"]:focus {
    box-shadow: 0 0 4px rgba(var(--primaryColor), 1.0);
}
}

@-moz-document url-prefix("http://www.neopets.com/neoboards/preferences.phtml") {
table[style="border: 1px solid #000000;"] {
    border: 0 !important;
    background: rgba(var(--highlightColor), 1.0);
    margin-bottom: 20px;
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
}
table[style="border: 1px solid #000000;"] br {
    display: inline !important;
}
.contentModuleHeaderAlt {
    border: 0 !important;
}
}

@-moz-document url-prefix("http://www.neopets.com/safetydeposit.phtml") {
.content form > table:nth-of-type(2) td:nth-of-type(4) {
    display: none;
}

.content form > table:nth-of-type(2) {
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
    background: rgba(var(--highlightColor), 1.0);
    position: relative;
}
.content form > table:nth-of-type(1) {
    margin-bottom: 20px;
}
.content form > table:nth-of-type(1) > tbody > tr > td:nth-of-type(1) {
    box-shadow: 0 0 4px rgba(var(--shadowColor), 0.4);
    background: rgba(var(--highlightColor), 1.0);
}
.content form > table:nth-of-type(1) table td {
    height: 38px !important;
    overflow: hidden;
    vertical-align: top;
    line-height: 0px;
    font-size: 0px;
}
.content form > table:nth-of-type(1) > tbody > tr > td:nth-of-type(1) td[nowrap=""]:nth-of-type(1) {
    visibility: hidden;
}
.content form input[name="obj_name"] {
    border: 0 !important;
    width: 168px;
    float: right;
    visibility: visible;
}
.content form select[name="category"],
.content form select[name="offset"] {
    border: 0 !important;
}
.content form input[value="Find"],
.content form input[value="Go"],
.submit_data {
    margin: 0 !important;
}
.content form select[name="offset"] {
    position: absolute;
    top: 0;
    left: 0;
    visibility: visible;
    width: 28px !important;
}
.content form input[value="Go"] {
    position: absolute;
    top: 0;
    right: 0;
}
/* .content form .content form {
    visibility: hidden;
    width: 200px;
    overflow: hidden;
} */
.content form .content form select,
.content form .content form input {
    visibility: visible;
    position: absolute;
    top: 0;
}
.content form .content form input {
    right: 0;
}
.content form .content form select {
    left: 0;
}
.content form tr[bgcolor="#E4E4E4"] {
    background: 0 !important;
    width: 760px !important;
    height: 54px;
}
.content form tr[bgcolor="#E4E4E4"] td {
    padding: 0 !important;
}
.content form tr[bgcolor="#E4E4E4"] td b {
    display: none;
