css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("web.whatsapp.com") {
  *{
    border-radius: 0 !important;
  }
  body {
    background: #DFDFDF !important;
    background-image: none !important;
  }
  #wrapper::before {
    background-image: none !important;
    background: none !important;
  }
  #window {
    width: 450px !important; 
  }
  .entry-main {
    margin: 15px !important;
    display: block !important;
  }
  .qr-wrapper-container {
    margin: 0 !important;
  }
  #platforms {
    display: none !important;
  }
  .app-wrapper::after {
    background-color: transparent;
  }
  .app-wrapper-web.app {
    width: 100% !important;
    height: 100% !important;
  }
  .context.context-out{
    background: none !important;
  }
  .app {
    width: 100% !important;
    top: 0 !important;
    height: 100% !important;
    bottom: 0 !important;
    padding: 0 !important;
    box-shadow: none !important;
  }
  #side, .pane-one {
    width: 25% !important;
  }
  .pane-intro {
     display: none;
  }
  .pane-two{
    width: 75% !important;;
  }
  .tail-container {
    display: none;
    width: 0;
  }
  .msg{
    padding-left: 5px !important;
    padding-right: 5px !important;
  }
  .message-in, .message-out {
    box-shadow: none !important;
    border-radius: 2px !important;
  }
  .message-in {
    background: #EEE !important;
  }
  .context-in {
    background-image: none !important;
  }
  .pane-chat-tile {
    background-image: none !important;
  }
  .pane-chat {
    background-color: #FFF !important;
  }
  .btn-forward-chat {
    background-color: rgba(0,0,0, 0.3);
    border-radius: 50% !important;
  }
  .shade {
   display: none;
  }
  .btn-meta {
   border-radius: 24px !important;
  }
  .bubble-image .message-meta, .bubble-location .message-meta {
      color: rgba(0, 0, 0, 0.5);
  }
  .cont-input-search {
    border: 0;
    margin-left: 0;
    margin-top: 0;
    height: 100%;
    width: 100%;
  }
  .pane-header {
    background-color: #128c7e;
  }
  .pane-header .emojitext{
    color:#FFF;
  }
  .avatar, .avatar-image, .incoming-msgs,.btn-more,.unread-count,.menu-icons .menu-icons-item::after,.menu-icons-item {
    border-radius: 50% !important;
  }
  .message-system-body, .message-system-e2e {
    border-radius: 3px !important;
  }
  .message-system-body {
    background-color: rgba(0,0,0,0.5);
    color: #FFF;
    padding: 0 15px !important;
  }
  .unread-count {
    background-color: #CC0000 !important;
  }
  .incoming-msgs{
    right: 25px;
  }
  .active.chat{
    background: #34b7f1 !important;
  }
  .active.chat .emojitext, .active.chat .chat-status-name, .active.chat .chat-status-divider  {
    color: #FFF;
  }
  .chat .avatar, .chat .avatar-body, .chat .avatar-body img{
    height: 36px !important;
    width: 36px !important;
  }
  
  .message-in .large-emoji-text.message-text, .message-out .large-emoji-text.message-text {
    padding: 10px;
    text-align: left;
  }
  .msg-unread {
    background-color: rgba(0,0,0, 0.45);
    color: #FFF;
    border: none;
  }
  .msg-unread *{
    background: transparent;
  }
  .drawer-header {
    height: 59px !important;
  }
}
