css:
@-moz-document domain("habitica.com") {
/* colours: 
Grey: #e0e0e0 - #48494f - #333438 (Main background and text)
Red: var(--Highlight) (Highlights)
Black: #000000 (When you want to block all light)
Purple: #DDA0DD (To make paragraph links stand out)
White: #ffffff (Other Links)
*/
/* V1.7.7.1 */
/*
Corrections
- Corrected the new max party members warning
Bugs
- Guilds / Parties with long names mess with join button

                               ****NOTICE****
There's a huge overhaul of Habiticas default CSS underway, when the site
is transferred I will update this theme to keep things working.
I expect it may take some time to retheme (if it's needed!) the new site
design. Thank you.
                               ****NOTICE****
*/
:root {
	--Highlight: /*[[HIGHLIGHT]]*/;
}
body {
    font-size: 14px !important;
    background-color: #48494f !important;
    line-height: unset !important;
    margin: unset !important;
	color: #e0e0e0 !important;
}
#wrap {
    min-height: unset !important;
}
::selection {
	color: #e0e0e0 !important;
	background-color: #5cb85c !important;
}
#header .navbar .container-fluid {
	background-color: #333438 !important;
}
a, .btn-link, .btn-link:focus, .btn-link:hover {
	color: #fff !important;
}
h3 a, .chat-buttons input, .h1 .small, .h1 small, .h2 .small, .h2 small, .h3 .small, .h3 small, .h4 .small, .h4 small, .h5 .small, .h5 small, .h6 .small, .h6 small, h1 .small, h1 small, h2 .small, h2 small, h3 .small, h3 small, h4 .small, h4 small, h5 .small, h5 small, h6 .small, h6 small, .text-success, .glyphicon-plus:before, .glyphicon-search:before {
	color: #e0e0e0 !important;
}
p a:not(.label):not(.badge) {
	color: #dda0dd !important;
}
hr {
	background-color: #e0e0e0 !important ;
}
code{
	background-color: var(--Highlight) !important;
	color: #e0e0e0 !important;
}
blockquote {
	border-left-color: var(--Highlight) !important; 
}
button, .well, .panel-body, .btn-xs, textarea, .container-fluid input, .nav-tabs a:hover, .filters .hrpg-input-group input, .filters .hrpg-input-group textarea, .task-add input, .task-add textarea, .task-checklist-edit>.checklist-form li input, .task-checklist-edit>.checklist-form li textarea, .list-at-user li, .table-striped td {
	background-color: #48494f !important;
	color: #e0e0e0 !important;
}
.pet-button {
	background-color: #333438 !important;
}
button.active, .list-at-user li:hover, .progress-bar, .task-add>button {
	background-color: var(--Highlight) !important;
}
button.pet-button.active  {
	background-color: #5cb85c !important;
	border-radius: 5px !important;
}

.label-default:not(.label-contributor-1):not(.label-contributor-2):not(.label-contributor-3):not(.label-contributor-4):not(.label-contributor-5):not(.label-contributor-6):not(.label-contributor-7):not(.label-contributor-8):not(.label-contributor-9):not(.label-npc) {
	background-color: #48494f !important;
}

.task-add>button:hover, .list-at-user li {
	background-color: #333438 !important;
}

.task-filter li.active a, .task-filter li.active a:hover  {
	background-color: var(--Highlight) !important;
	color: #e0e0e0 !important;
	border: none !important;
}
.task-actions .glyphicon-plus:before  {
	color: #48494f !important;
}
.hero-stats .meter-label {
	margin-top: 2px !important;
	text-shadow: unset !important;
	background-color: #48494f !important;
}
.hero-stats .meter-label, .herobox .avatar-level, .herobox .avatar-name {
	background-color: #48494f !important;
	color: #e0e0e0 !important;
	border-color: #e0e0e0 !important;
	outline: none !important;
}
.glyphicon-heart  {
	color: #9d0000 !important;
}
.glyphicon-star {
	color: #5cb85c !important;
}
.glyphicon-fire {
	color: #4781E7 !important;
}
.modal-fixed-height .ng-scope .tab-content .tab-pane {
	background-color: #48494f !important;
}
#testimonial-carousel {
	background-color: #333438;
}
#myCarousel a {
	color: #000 !important;
}
.option-title {
	color: #e0e0e0;
}
.task-checker label::after {
	color: #000 !important;
}
.btn-default.active, .btn-default:active, .btn-default:focus, .btn-default:hover, .open .dropdown-toggle.btn-default {
	background-color: var(--Highlight) !important;
	color: #e0e0e0 !important;
}
.toolbar-mobile textarea, .toolbar-mobile>a, .toolbar-mobile>button, .toolbar-mobile>input {
	background-color: unset !important;
	color: #e0e0e0 !important;
	border: none !important;
}
.toolbar-options .toolbar-controls, .toolbar-mobile-nav .toolbar-controls {
	background-color: var(--Highlight) !important;
}
.toolbar-mobile h4 {
	background-color: var(--Highlight) !important;
	color: #e0e0e0 !important;
}
.toolbar-mobile>div a, .toolbar-mobile>div a:link, .toolbar-mobile>div a:visited {
	color: #e0e0e0 !important;
}
.customize-option {
	background-color: #48494f !important;
}
.selectableInventory, button.pet-button.active {
	Background-color: var(--Highlight) !important;
}
#group-leader-selection {
	background-color: var(--Highlight) !important;
    color: #e0e0e0 !important;
}
.dropdown-menu>li>a, .dropdown-menu {
	background-color: #333438 !important;
	color: #e0e0e0 !important;
}
.dropdown-menu>li>a:hover {
	background-color: #48494f !important;
}
.dropdown-menu .divider {
	background-color: #e0e0e0 !important;
}
ul.toolbar-wallet, ul.toolbar-options {
	padding: 6px 5px 0 0 !important;
}
button.background-unlocked {
	border-color: #48494f !important;
}
button.background-unlocked:hover {
	border-color: var(--Highlight) !important;
}
.input-group-addon {
	background-color: #333438 !important;
}
.options-menu li.highlight:hover {
	background-color: unset !important;
}
.options-menu .badge { /* Prevent menu breaking with inbox message */
	padding: 1px 4px !important;
}
.group-leave-join .btn  {
	padding: 7px 12px !important;
	border: 0 !important;
}
.panel-heading .btn-default, .panel-heading .btn.btn-sm.btn-primary.pull-right {
	padding: 7px 12px !important;
}
.panel-heading {
	padding-right: 0 !important;
	padding-top: 8px !important;
	padding-bottom: 7px !important;
}
.panel-heading .btn:not(.btn-success):not(.btn-danger), .group-leave-join .btn {
	margin-right: 0 !important;
	margin-top: -8px !important;
	border: 0 !important;
	border-radius: 0 !important;
	border-left: 1px #e0e0e0 solid !important;
	border-bottom: 1px #e0e0e0 solid !important;
}
.group-leave-join .btn-success {
	padding: 7px 12px 5px 12px !important;
}
.panel-title small {
	padding-right: 15px !important;
}
.toolbar-bailey {
	margin-top: -2px !important;
	padding: 0 !important;
}
.party-chat li, .tavern-chat li {
	border-bottom: 1px #48494f solid !important;
}
button.task-action-btn.tile.spacious.bright {
	background-color: var(--Highlight) !important;
	outline: none !important;
}
button.task-action-btn.tile.spacious.bright:hover {
	background-color: #333438 !important;
}
.col-md-4 .panel.panel-default .panel-body {
	max-height: 800px !important;
	overflow-y: auto !important;
}
.bg-info {
	background-color: #333438 !important;
}
.filters .filters-tags.challenge>a {
	background-color: #333438 !important;
	color: #e0e0e0 !important;
	border: 1px #e0e0e0 solid !important;
}
.filters .filters-tags.challenge>a:hover, .filters .filters-tags.challenge.active.filters-tags a {
	background-color: var(--Highlight) !important;
}
.cast-target:hover {
	border-color: var(--Highlight) !important; /* Themes hovering when casting skill */
}
.label-primary[href]:focus, .label-primary[href]:hover {
	background-color: var(--Highlight) !important;
}
.label-primary {
	background-color: var(--Highlight) !important;
}
/* Battle Monsters With Friends */
pre {
	background-color: #48494f !important;
	color: #e0e0e0 !important;
}
.alert-info {
	border-color: #e0e0e0 !important;
}
/* Challenges */
.challenge-accordion-header-specs li {
	color: #e0e0e0 !important;
	background-color: unset !important;
}
.create-challenge-from select {
	background-color: #48494f !important;
	color: #e0e0e0 !important;
}
.create-challenge-from .main-list {
	margin-top: 90px !important;
}
.challenge-accordion-header-specs .btn {
	border: none !important;
	border-left: 1px #e0e0e0 solid !important;
	border-bottom: 1px #e0e0e0 solid !important;
	margin: -10px -13px 0 0 !important;
	padding: 7px 10px !important;
}
.challenge-accordion-header-specs .btn.btn-sm.btn-success:not(.ng-scope),.panel-default .bg-transparent .btn.btn-sm.btn-danger { /* Fix challenges, but not guilds */
	margin-right: -5px !important;
}
.panel-heading {
	border-bottom: 1px #e0e0e0 solid !important;
}
/* Task Popup */
.task-options, .task-options input,
.task-modal.color-worst:not(.completed) .task-checklist-edit li>input,
.task-modal.color-worse:not(.completed) .task-checklist-edit li>input,
.task-modal.color-bad:not(.completed) .task-checklist-edit li>input,
.task-modal.color-neutral:not(.completed) .task-checklist-edit li>input,
.task-modal.color-good:not(.completed) .task-checklist-edit li>input,
.task-modal.color-better:not(.completed) .task-checklist-edit li>input,
.task-modal.color-best:not(.completed) .task-checklist-edit li>input
{
	background-color: #333438 !important;
	color: #e0e0e0 !important;
}
.task-options .glyphicon-trash:before {
	color: #000 !important;
}
/* Fix task tool tips dissapearing before able to click links */
.popover.top {
	transition-delay: 0.4s !important;
}
/* NAVBAR */
/* hide close toolbar button */
.toolbar-toggle {
    display: none !important;
}
nav.toolbar {
    background-color: #333438 !important;
	border-bottom: 1px #e0e0e0 solid !important;
}
.toolbar-nav {
    padding: 6px 5px 5px 5px !important;
}
.toolbar-nav .toolbar-button-dropdown > a,
.toolbar-nav .toolbar-button-dropdown button {
    color: #e0e0e0 !important;
    border: none !important;
}
.toolbar-nav .toolbar-button-dropdown > a:nth-of-type(2) {
    border: none !important;
}
/* Loading Bar */
#loading-bar .peg {
    box-shadow: none !important;
}
#loading-bar .bar {
	background: var(--Highlight) !important;
}
#loading-bar-spinner .spinner-icon {
	border-top-color: var(--Highlight) !important;
	border-left-color: var(--Highlight) !important;
}
/* Menu's */
.toolbar-submenu, .toolbar-submenu a, .toolbar-submenu::before, .modal-body, .modal-content, .task-filter li>a {
	color: #e0e0e0 !important; 
	background-color: #333438 !important;
}
.toolbar-audio>div h4, .toolbar-settings>div h4, .toolbar-sync>div h4, .panel-heading, .alert, .modal-header, .nav-tabs .active a  {
	color: #e0e0e0 !important; 
	background-color: var(--Highlight) !important;
}
h4::before , .toolbar-notifs>div h4{
	color: #e0e0e0 !important; 
	background-color: var(--Highlight) !important;
}
.toolbar-nav .toolbar-button-dropdown>div a, .toolbar-nav .toolbar-button-dropdown>div a:link, .toolbar-nav .toolbar-button-dropdown>div a:visited, .toolbar-nav .toolbar-button-dropdown.highlight>div a, .toolbar-nav .toolbar-button-dropdown.highlight>div a:link, .toolbar-nav .toolbar-button-dropdown.highlight>div a:visited {
	color: #e0e0e0 !important; 
}
.toolbar-audio>div, .toolbar-mobile>div, .toolbar-nav .toolbar-button-dropdown>div, .toolbar-notifs>div, .toolbar-settings>div {
	color: #e0e0e0 !important; 
	background-color: #333438 !important;
}
.toolbar-nav .toolbar-button-dropdown.highlight, .toolbar-nav .toolbar-button-dropdown.highlight a, .toolbar-nav .toolbar-button-dropdown.highlight>a:nth-of-type(2), .toolbar-nav .toolbar-button-dropdown.highlight:hover, .toolbar-nav .toolbar-button-dropdown.highlight>a:hover, .toolbar-nav .toolbar-button-dropdown.highlight>button:hover {
	border: unset !important;
	color: #e0e0e0 !important;
	background-color: unset !important;
}
.options-menu li>a:nth-of-type(2) {
	border-left: 0 !important
}
.popover {
	color: #e0e0e0 !important; 
	background-color: #333438 !important;
}
.popover-title, .popover-title::before {
	color: #e0e0e0 !important; 
	background-color: var(--Highlight) !important;
}
.arrow {
	display: none !important;
}
.options-menu {
	background-color: var(--Highlight) !important;
}
.task-filter li>a:hover {
	background-color: #48494f !important;
}
.options-submenu {
	background-color: var(--Highlight) !important;
}
.options-submenu li>a, .options-menu li>a {
	border: unset !important;
	border-right: 1px solid #e0e0e0 !important;
	border-radius: 2px !important;
}
.options-menu li>a, .options-menu li>button, .options-submenu li>a, .options-submenu li>button {
	background-color: #333438 !important;
	color: #e0e0e0 !important;
	padding: 5px 7px !important;
}
.options-menu li>a:hover, .options-menu li>button:hover, .options-submenu li>a:hover, .options-submenu li>button:hover {
	background-color: #48494f !important;
	color: #e0e0e0 !important;
}
.options-submenu, .options-menu {
	padding: 0 !important;
}
.options-menu li.highlight>a {
	border: unset !important;
	border-left: 1px solid #e0e0e0 !important;
}
.options-menu li.active a, .options-menu li.active button, .options-submenu li.active a, .options-submenu li.active button, .options-submenu li.active>a:hover, .options-submenu li.active>button:hover  {
	background-color: var(--Highlight) !important;
}
.options-menu li, .options-submenu li {
	margin: 0 !important;
}
.options-submenu li.active a, .options-submenu li.active button, .options-menu li.active a, .options-menu li.active button {
	border-color: #e0e0e0 !important;
}
.tab-content {
	background-color: #333438 !important;
}
.chat-message pre, .chat-message pre code {
	background-color: #48494f !important;
}
.options-menu li.highlight {
	background-color: unset !important;
	border: 0 !important;
}
.options-menu li.highlight>a, .options-menu li.highlight>button {
	background-color: #333438 !important;
	color: #e0e0e0 !important;
}
.options-menu li.highlight>a:hover, .options-menu li.highlight>button:hover {
	background-color: #48494f !important;
}
/* Fix border only showing on left side of popups */
.modal-content {
	border: none !important;
}
/* Fix checklists on task page */
.glyphicon-resize-vertical,
.task-column:not(.rewards) .color-worst:not(.completed) .task-checklist-edit li>input,
.task-column:not(.rewards) .color-worse:not(.completed) .task-checklist-edit li>input,
.task-column:not(.rewards) .color-bad:not(.completed) .task-checklist-edit li>input,
.task-column:not(.rewards) .color-neutral:not(.completed) .task-checklist-edit li>input,
.task-column:not(.rewards) .color-good:not(.completed) .task-checklist-edit li>input,
.task-column:not(.rewards) .color-better:not(.completed) .task-checklist-edit li>input,
.task-column:not(.rewards) .color-best:not(.completed) .task-checklist-edit li>input {
	color: #e0e0e0 !important;
}
.task-column:not(.rewards) .color-worst:not(.completed) .task-checklist-edit li>a,
.task-column:not(.rewards) .color-worse:not(.completed) .task-checklist-edit li>a,
.task-column:not(.rewards) .color-bad:not(.completed) .task-checklist-edit li>a,
.task-column:not(.rewards) .color-neutral:not(.completed) .task-checklist-edit li>a,
.task-column:not(.rewards) .color-good:not(.completed) .task-checklist-edit li>a,
.task-column:not(.rewards) .color-better:not(.completed) .task-checklist-edit li>a,
.task-column:not(.rewards) .color-best:not(.completed) .task-checklist-edit li>a,
.task-column:not(.rewards) .completed .task-checklist-edit li>a { /*corrects completed tasks */
	background-color: #333438 !important;
}
/* buttons */
nav.toolbar li.toolbar-button a,
nav.toolbar li.toolbar-button-dropdown,
nav.toolbar li.toolbar-button-dropdown a,
nav.toolbar ul.toolbar-options a {
    background: none !important;
    border: none !important;
	color: #e0e0e0 !important;
}
.toolbar-nav .toolbar-button-dropdown > a:first-of-type,
.toolbar-nav .toolbar-button-dropdown > a:nth-of-type(2) {
    border-radius: 2px !important;
}
.toolbar-nav .toolbar-button > a:hover,
.toolbar-nav .toolbar-button button:hover,
.toolbar-nav .toolbar-button-dropdown > a:hover,
.toolbar-nav .toolbar-button-dropdown button:hover {
    background-color: rgba(0, 0, 0, 0.1) !important;
    color: #e0e0e0 !important;
}
/* upper-right settings */
.toolbar .glyphicon {
    color: #e0e0e0 !important;
}
nav.toolbar .toolbar-subscribe {
    display: none !important;
}
.toolbar-currency.gold, .toolbar-currency.silver {
	color: #e0e0e0 !important;
}
/* fix gems */
.toolbar-wallet .gem-wallet span {
    padding-top: .218em !important;
}
.gem-wallet .tile {
	background-color: #48494f !important;
	color: #e0e0e0 !important;
}
/* HEADER */
.sticky-wrapper {
    height: 183px !important;
}
.header-wrap {
	background-color: #333438;
	border-bottom: 1px solid #e0e0e0 !important;
	padding: 38px 0 13px 0 !important;
	/*[[HEADERSCROLL]]*/
}
.site-header {
    height: auto !important;
    width: 100% !important;
    margin-bottom: -12px !important;
	margin-top: 0 !important;
}
.herobox {
    outline: none !important;
}
.herobox.noBackgroundImage {
	background: none !important;
}
.herobox .avatar-level.label {
    background: none !important;
    color: rgba(0, 0, 0, 0.5) !important;
    font-weight: 400 !important;
}
.label .glyphicon {
    color: rgba(0, 0, 0, 0.25) !important;
}
.addthis_counter.addthis_pill_style.addthis_nonzero a.addthis_button_expanded, .addthis_counter.addthis_pill_style a.atc_s {
	display: none !important; 
}
/* bars */
.hero-stats {
    border: none !important;
    background: none !important;
    min-width: 300px !important;
    padding: 0 15px !important;
}
.meter {
    font-size: 12.5px !important;
    font-weight: 600 !important;
    color: rgba(0, 0, 0, 0.5) !important;
    background: #e0e0e0 !important;
    border: none !important;
    outline: none !important;
    box-shadow: inset 0 0 0px 1px rgba(0, 0, 0, 0.25) !important;
    margin: 0 0 4px !important;
    height: 2.25em !important;
}
/* TASKS */
/* task columns */
.tasks-lists .row .col-md-3 {
    padding: 5px 8px 0 5px !important;
    margin: 0 !important;
}
.task-column {
    padding: 0 !important;
    border: none !important;
    background: none !important;
}
/* add new task */
.addtask-form {
    outline: none !important;
    background: none !important;
    margin: 0 !important;
}
.addtask-field input {
    height: auto !important;
    padding: .5em .5em !important;
    background: none !important;
}
.addtask-field input:focus {
    background: none !important;
    box-shadow: none !important;
}
.addtask-btn {
    display: none !important;
}
/* reduce tab sizes */
.task-filter {
	margin: 10px 0 !important;
}
.glyphicon-question-sign {
	display: none !important;
}
.panel-body p {
	margin: 5px !important;
}
h2.task-column_title {
    font-weight: 400 !important;
    font-size: 1.8em !important;
    margin: 0 !important;
    text-align: center !important;
    line-height: 1 !important;
    font-family: Open Sans, Lato, sans-serif !important;
    color: #e0e0e0 !important;
}
h2.task-column_title { /* Should only apply to the challenges list */
	margin-bottom: 10px !important;
}
/* make Emoji's fit in challenges */
.panel-heading {
	height: auto !important;
}
/* messes up to-dos progress */
.glyphicon-signal {
    display: none !important;
}
.task-column hr {
    display: none !important;
}
/* Task body */
.task {
    border: none !important;
    border-radius: 1.5px !important;
    box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.1) !important;
    min-height: unset !important;
    margin: 0 0 3px 0 !important;
    clear: unset !important;
    /* fixes spells */
}
.task-meta-controls {
    height: unset !important;
    float: right !important;
	margin: 2px 2px 0 0 !important;
}
.hero-stats .health .bar {
	background-color: #9d0000 !important;
}
.hero-stats .experience .bar {
	background-color: #5cb85c !important;
}
.task-meta-controls span {
	color: #e0e0e0 !important;
	background-color: #48494f !important;
	border-radius: 2px !important;
	padding: 2px 2px !important;
}
.task-meta-controls .badge, .badge {
	background-color: var(--Highlight);
}
.task-meta-controls .badge {
	margin-left: 5px !important; 
}
/* Habit +/- */
.habits .task-action-btn {
    width: 24px !important;
    height: 100% !important;
    line-height: unset !important;
    font-size: 15px !important;
    color: rgba(0, 0, 0, 0.33) !important;
    font-weight: 700 !important;
    outline: none !important;
    border: none !important;
    box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.1) !important;
    padding: 3px 0 0 0 !important;
}
/* - */
.habits .task-action-btn:nth-child(2) {
    box-shadow: inset 0 -1px 1px -1px rgba(0, 0, 0, 0.3), inset -1px 0 1px -1px rgba(0, 0, 0, 0.3), inset 0 1px 1px -1px rgba(0, 0, 0, 0.3) !important;
}
/* match .task border-radius */
.task-action-btn:first-child,
.task-checker label {
    border-top-left-radius: 1.5px !important;
    border-bottom-left-radius: 1.5px !important;
}
/* fix task settings */
.task-options .task-action-btn {
    width: unset !important;
    padding: 4px 8px !important;
    height: auto !important;
