/* minimum setting testing on jp site only */

body{padding:0px !important;}

/* date */
table#results tr th:first-child{
   min-width:2.3em;
}
table#results tr td:first-child{
    /* delete margin */
    padding-left:0px;
    padding-right:0px;
}
/* monster name */
table#results tr th:nth-child(3){
    min-width: 7em;
}
table#results tr td:nth-child(3){
    text-overflow: ellipsis;
    min-width: 7em;
    max-width: 7em;
    white-space: nowrap;
    overflow: hidden;
    /* delete margin */
    padding-left:0px;
    padding-right:0px;
}

/* ID */
table#results tr th:nth-child(4){
  min-width:5.2em
}
table#results tr td:nth-child(4){
    /* delete margin */
    padding-left:0px;
    padding-right:0px;
}
table#results tr td:nth-child(4) input{
  width:5.2em
}

/* copy */
table#results tr th:nth-child(5){
  min-width:4.9em
}

/* delete Margin */
.col-sm-12{
    position: relative;
    min-height: 1px;
    padding-right: 0px;
    padding-left:0px;
}
.container-fluid{
    padding-left:0px;
    padding-right: 0px;
    margin-right: auto;
    margin-left: auto;
}

table#results tr th:nth-child(1){
   display:table-cell !important;
}
table#results tr td:nth-child(1){
   display:table-cell !important;
}

table#results tr td:nth-child(5) button{
/*
