css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tieba.baidu.com") {

    /* ------------------------ =贴吧内 ------------------------------*/
    /* 在每个贴后显示该贴子的发帖时间,避免挖坟 */
    .is_show_create_time {
        display: block !important;
        font-size: 110%;
        color: orange;
    }

    /*扩宽 主题作者的名字容器 的宽度，贴吧默认6个字就显示省略号了*/
    .frs-author-name {
        max-width: 100px !important;
    }

    /* 主题作者的名字后的'印记'
       右上角 '百度首页'
       右上角 '会员'
       右侧 皇冠身份 兑换会员
       顶部搜索框弹出的'大伙正在聊'、'吧友热议', '搜索风云' */
    .frs_bright_icons,
    .u_bdhome,
    .u_member,
    .celebrity,
    .operation_item, .break_title:nth-child(1), .bdfengyun {
        display: none !important;
    }

    /* 今日话题*/
    .interview {
        display: /*[[interviewVisable]]*/;
    }
  
    /* ----------------------- =个人主页 ---------------------------*/
    /* 会员一键签到按钮
    个人主页右方 t豆 蓝钻等无用信息
    个人主页右方 访客、关注列表
    贴吧个人主页的'热门动态',所属的贴子从不更新,整整几年都是那几个
    顶部搜索框弹出的'更多热议话题' */
    .onekey_btn,
    .userinfo_scores,
    .right_aside,
    .ihome_hot_feed,
    .tbtopic {
        display: none !important;
    }

    /* 调整头像及下方内容与搜索框之间过大的距离*/
    .headinfo_wrap {
        height: 90px !important;
    }

    /*  加宽'关注的吧'列表宽度*/
    .content_wrap {
        width: 960px;
    }

    /* 修改加宽后的列表背景*/
    .left_aside, .ihome_left_aside {
        background: #f5f7fa;
    }
  
    .search_back_box {
        height: 0;
        display: none;
    }
}
