css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.reactor.cc") {
  #post_list{text-align: center !important;}
  .m_wrapper{
    width: 900px !important;
    display:block !important;
    margin:0px auto 10px auto !important;
    padding: 0px;
    border: none !important;
    border-radius: 0px !important;
    background: #464646;
  }
  .m_top{
    display: flex;
    width: 900px;
    margin: auto;
    justify-content: space-between;
      }
  .m_pagination{
    display: flex;
    justify-content: space-between;
  }
  .m_pagination a{
    text-shadow: none !important;
    color: #ffa500 !important;
    padding: 0 15px;
    line-height: 40px;
    font-weight: bold;
    margin: auto;
  }
  .comments{
    font-size: 12px !important;
    margin: 0px !important;
    background-image: none !important;
    border: none !important;
    background: none !important;
  }
  .comments a, .submenuitem.active a, .submenuitem a{
    text-shadow: none !important;
    padding: 0px !important;
    color: #ffa500 !important;
    line-height: 20px;
  }
  .ufoot{text-align: right !important;}
  .post_rating{display: none !important;}
  body{
    font: normal 13px Verdana;
    background: black;
  }
  .article{
    background-color: #303030;
    border: none;
    margin: 0 0 2px;
    color: #ffa500;
    line-height: 20px !important;
  }
  .mainheader{display: none !important;}
  a{
    color: #ffa500;
    text-decoration: none;
  }
  .taglist, .uhead_nick{
    text-align: left;
    font-size: 10px;
  }
  .taglist{margin: 0 0 10px;}
  div.post_content>span{
    margin: 0 0 5px !important;
    display: block !important;
    color: #d0d0c0 !important;
    text-align: left;
  }

  .image{text-align: center;}
   
  .full_link, .counters{display: none !important;}
  .post_poll_holder{background: none !important;}
  .post_content_expand{
    height: 20px !important;
    background-color: #7a7a7a !important;
  }
  .submenuitem, .submenuitem.active, .m_pagination a{
    font-size: 13px;
    border: none;
    background: none;
    background-image: none;
    padding-left: 10px;
  }
  
  .image{margin-bottom: 5px;}
  .m_menu{margin: 10px 5px 0;}
  .m_menu>p{
    color: red;
    padding-left: 10px;
    display: flex;
    justify-content: space-between;
    font-size: 13px !important;
  }
  .m_menu>p>a{
    color: #d0d0c0 !important;
  }
  .submenuitem.active a{text-decoration: underline;}
  a.m_logo>img{display: none !important;}
  a.m_logo{
    height: 53px;
    width: 200px;
    border: none;
    padding: 0px;
    margin-left: 0px;
    background: url('data:image/png;base64,') 0 0 no-repeat !important;
  }

.more_blogs {border-top: 1px solid #7a7a7a;}
.comment{
  background: none !important;
  border-radius: 0px !important;
  border: 1px solid #272727;
  color: #d0d0c0;
}
textarea{background-color: #d0d0c0;}
input[type="submit"]{background-image:none !important;}
#post_navigation_wrapper{display: none !important;}
}
