css:
@-moz-document domain("poczta.wp.pl") {
#mainColumnInner {
    min-width: 760px;
    max-width: 775px;
}

.button {
    padding: 4px 0px 0 0px;
    margin: 1px;
}

.button.arrow, .button.gear, a.splitArrow.button.secondary.disabled {
    width: 17px;
    margin-right: 0px;
}

#bxPanelTopBody .search {
    margin-right: 0px;
}

#main .middleSection, #middle {
    min-width: 985px;
}

.tabs .tab {
    padding: 10px 15px 9px 40px;
}

.search {
    display: table-cell;
    min-width: 170px;
}
}
