css:
@-moz-document domain("docs.oracle.com") {
  header, footer,
  #CONTENT,
  .row, .titleDiv {
    background-color: aliceblue;
    filter: invert(100%);
  }
  div[style='padding-top: 1.5em; padding-bottom: 0.5em; padding-left: 15px;'] {
    background-color: aliceblue;
    filter: invert(100%);
  }

  pre, .IND div > pre, .IND dd > pre, .IND li > pre, .IND pre {
    font-family: "Ubuntu Mono"; 
  }
}
