css:
/* i really want this to be global */
html,body {
	background: #222c32;
	margin: 0;
	padding: 0;
	color: #fff;
}

#fp {
	height: 100%;
	margin-bottom: -180px;
}

#fp h1 a {
	background: url('https://upload.wikimedia.org/wikipedia/commons/thumb/5/53/Skull_and_crossbones.svg/799px-Skull_and_crossbones.svg.png') no-repeat;
	background-size: 336px 323px;
	width: 336px;
	height: 323px;
	
}

h1 {
	margin-top: 40px;
}

#navlinks a {
	color: white;
	text-decoration: none;
}

#navlinks a:hover, a:active, a:visited {
	color: white;
	font-weight: bold;
	text-decoration: none;
}

#inp span a {
	color: white;
	text-decoration: none;
}

#inp span a:hover, a:active, a:visited {
	color: white;
	font-weight: bold;
	text-decoration: none;
}
	
#howdl {
	display: none;
}

footer {
	display: none;
}





#header {
	margin-left: 40px;
	margin-top: 20px;
}

img#TPBlogo {
	display: none;
}

#header a {
	color: white;
	text-decoration: none;
}

#header a:hover, a:active, a:visited {
	color: white;
	font-weight: bold;
	text-decoration: none;
}

h2 {
	display: none;
}

h2 span, a {
	color: #222c32;
	text-decoration: none;
}

table {
	color: #222c32;
}

table#searchResult {
	border: 4px solid black;
}

table#searchResult th, table#searchResult td {
	border: 2px solid black;
}

thead#tableHead th {
	background: #acaeae;
}

table#searchResult tr {
	background: #dfe1e1;
}

table#searchResult tr:hover {
	background: #fff;
}

table#searchResult tr.alt {
	background: #eff1f1;
}

table#searchResult td.vertTh a {
	color: #222c32;
	text-decoration: none;
}

table#searchResult td.vertTh a:hover, a:active, a:visited {
	color: #222c32;
	text-decoration: none;
}

thead#tableHead, table#searchResult a:hover {
	text-decoration: none;
	font-weight: bolder;
}

a[href*="/search"] {
    color: white;
	font-weight: bold;
}

div#SearchResults {
	padding-bottom: 40px;
}

div#foot {
	display: none;
}





#detailsframe {
	border: 3px solid black;
	color: #222c32;
}

#detailsframe #title {
	background: #acaeae;
	color: #222c32;
}

#details {
	background: #dfe1e1;
	color: #222c32;
}

#details dt {
	color: #222c32;
}

#details dd a{
	border-bottom: none;
}

#details h4 {
	color: #222c32;
}

#comments p.byline a {
	color: #222c32;
	border-bottom: none;
}

#comments p.byline a:hover {
	color: #222c32;
	border-bottom: none;
	font-weight: bold;
}

div.comment a {
	color: #222c32;
	border-bottom: none;
}

div.comment a:hover {
	color: #222c32;
	border-bottom: none;
	font-weight: bold;
}

a[target="_blank"] {
    color: #222c32;
	font-weight: bold;
	border-bottom: none;
}

#browseContainer {
	margin-top: 160px;
}

#browseContainer a {
	color: white;
	text-decoration: none;
}

#browseContainer a:hover {
	color: white;
	text-decoration: none;
	font-weight: bold;
}
