table[id^="post"] tr+tr+tr td.alt2 a {text-decoration:none}
table[id^="post"] tr+tr+tr td.alt2 a:after {content:"Report";font-size:9px}
table[id^="post"] td[id^="td_post_"],pre.alt2,
table[id^="post"] tr+tr+tr td,
table[id^="post"] td[id^="td_post_"] div[id*="post_message"]
{
background-color:transparent!important;
background-image:url('data:image/jpg;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABZ0RVh0Q3JlYXRpb24gVGltZQAwMS8wNi8wOeQdC6wAAAAYdEVYdFNvZnR3YXJlAEFkb2JlIEZpcmV3b3Jrc0+zH04AAAANSURBVAiZY7h270oDAAgfAwmCDOwgAAAAAElFTkSuQmCC')!important;
}

