background-repeat:no-repeat!important;
background-image:url('http://img158.imageshack.us/img158/807/deviantmas3vo9.gif')!important;}

table[id^="post"] td[id^="td_post_"] div[id*="post_message"] {padding:5px!important;}

.thead {
	background-color:#D6DED4!important;
	border-top:0!important;
	border-color:#556C5F!important;
