	-moz-border-radius-topright:5px!important;
	-moz-border-radius-topleft:5px!important;
	clear:both!important;
	width:40px!important;
	margin:0 auto 0 4px!important;
	border:0!important;
	background:#fff;
	text-align:left!important;
	padding:2px 4px!important
}

div.fieldset,table#vB_Editor_QE_1_editor {border:0!important;width:90%!important;}
.fieldset > div {color:#000!important;padding:3px!important;}
img[src*="images/statusicon/user_"] {float:left!important;margin:3px 3px 0 0!important}
a[onclick*="return toggle_collapse"] {float:left!important;padding:4px 5px 0 0!important;}

/* Stuff hidden, banners, unwanted linebreaks etc... */
td#header_right_cell, div > div.page > div > br,
img[src*="images/editor/separator.gif"],
table[id^="post"] td.alt2 > div[id^="postmenu_"]+.smallfont+.smallfont br,
table[id^="post"] tr+tr+tr td hr,
img[src$="collapse_tcat_collapsed.gif"],
img[src$="collapse_thead_collapsed.gif"],
img[src$="collapse_tcat.gif"],
img[src$="collapse_thead.gif"],
